@-moz-document domain("tieba.baidu.com") {*/
html body, button, input, select, textare, td, th,.region_2 .region_cnt {
    font: 12px/18px Arial;
    font-family: Arial,Tohoma;
}

html a {
	color: #1D53BF !important;
	text-decoration: none !important;
}
 
html a:hover {
	color: #FF3366 !important;
	-moz-transition: .2s ease-in;-moz-transition: .2s ease-out;
	-webkit-transition: all .2s ease-in;-webkit-transition: all .2s ease-out;
}

a:visited {
    color: #68526B !important;
}
.ui_btn em{
    color: #FFF !important;
}

#com_userbar a:link, #com_userbar a:active, #com_userbar a:visited, #com_userbar a:hover {
    color: #1D53BF !important;
}

#frs_nav .nav_left .focus a:visited {
    color: #333 !important;
}
#frs_nav .nav_left .focus a {
    color: #333 !important;
}
#frs_nav .nav_center .first a:visited {
    color: #555 !important;
}
#frs_nav .nav_center .first a {
    color: #555 !important;
}
#frs_nav a {
    color: #FFF !important;
}
#frs_nav a:visited {
    color: #FFF !important;
}
#jq_top_bar{
    display:none !important;
}
#nav_sub a{
    color:#FFF !important;
    font-weight: normal;
}
#nav_sub .focus a{
    color:#333 !important;
    font-weight: normal;
}
#nav a{
    font-weight: normal;
}
#content_inner dl dt h2{
    background:#F1F1F1 !important;
}
#content_inner dl dt h2{
    font-weight: normal;
}
.l_header table{
    display:none !important;
}
.bawu_log_header{
    margin-bottom:10px !important;
}
#contents .index_nav a,.nav_bar a,#feat .feat_tieba a{
    color: #fff !important;
}
.item_op .btn_buy,.item_op .btn_recharge{
    color: #FFF !important;
}
.item_op .btn_preview{
    color: #000 !important;
}
.item_op .btn_disable{
    color: #777 !important;
}
.nav_bar a:hover{
    color: #f1f1f1 !important;
}
#j_frs_gamezone,.l_banner,#cproshow,#h123_mod,#rp_picture,#rp_jff_old,.recommend_ad{
    display:none !important;
}
