css:
/* AGENT_SHEET */
html scrollbarbutton[sbattr="scrollbar-up-top"],
html scrollbarbutton[sbattr="scrollbar-down-top"],
html scrollbarbutton[sbattr="scrollbar-up-bottom"],
html scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: none !important;
    display: none !important;
}
html > scrollbar thumb {
    position: relative !important;
    -moz-appearance: none !important;
    background: rgba(255,255,255,0) !important;
    border: 0 !important;
}
html > scrollbar[orient="vertical"] thumb {
    background-image: -moz-linear-gradient(left , rgba(255,255,255,0) 85%, rgb(221,74,20) 57%) !important;
    max-height: 800px !important;
    padding: 0 0 !important;
    min-height: 77px !important;
    width: 21px !important;
}
/*html > scrollbar[orient="vertical"] thumb:-moz-window-inactive {
    background-image: -moz-linear-gradient(left , rgba(255,255,255,0) 85%, rgb(150, 150, 150) 57%) !important;
}*/
html > scrollbar[orient="horizontal"] thumb {
    background-image: -moz-linear-gradient(top , rgba(255,255,255,0) 85%, rgb(221,74,20) 57%) !important;
    max-width: 800px !important;
    padding: 0 0 !important;
    min-width: 77px !important;
    height: 21px !important;
}
/*html > scrollbar[orient="horizontal"] thumb:-moz-window-inactive {
    background-image: -moz-linear-gradient(top , rgba(255,255,255,0) 85%, rgb(150, 150, 150) 57%) !important;
}*/
html > scrollbar thumb:hover, html > scrollbar thumb:active {
    background-position:center !important; 
}
html > scrollbar[orient="vertical"] thumb:hover, html > scrollbar[orient="vertical"] thumb:active {
    background-image: url(data:image/png;base64,) !important;
}
html > scrollbar[orient="horizontal"] thumb:hover, html > scrollbar[orient="horizontal"] thumb:active {
    background-image: url(data:image/png;base64,) !important;
}
html > scrollbar[orient="vertical"],
html > scrollbar[orient="horizontal"] {
    padding: 0px !important;
}
html > scrollbar {
    -moz-appearance: none !important;
    background: none !important;
    position: relative !important;
    z-index: 9999 !important;
}
html > scrollbar[orient="vertical"] {
    margin-left: -21px !important;
}
html > scrollbar[orient="horizontal"] {
    margin-top: -21px !important;
}
