css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("google.com"), domain("msn.com"), domain("yahoo.com"), domain("hotbot.com"), domain("pastebin.com")
{
	INPUT *, TEXTAREA * 
	{
		color: black !important;
	}
}

@-moz-document domain("google.com")
{
	input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"]
	{
		color: black !important;
	}
	.jfk-textinput
	{
		background-color:white !important;
	}
	.docos-input-typing > .docos-input-textarea, .aoT
	{
		color: buttontext !important;
	}
	input.aoT
	{
		color: #222 !important;
	}
}

@-moz-document url("https://docs.google.com/viewer"), domain("play.google.com")
{
	input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"], input[type="url"], .text-input
	{
		color: ButtonText !important;
	}
	
	#gbqfq {
		color: black !important;
	}
}

@-moz-document domain("proboards.com"), domain("banweb.plu.edu"), domain("search.yahoo.com"), domain("startpage.com")
{
	INPUT *, TEXTAREA *, select
	{
		color: ButtonText !important;
	}

	HTML:root INPUT[type="button"],
	HTML:root INPUT[type="submit"],
	HTML:root INPUT[type="reset"],
	HTML:root input[type="text"],
	HTML:root input[type="password"],
	HTML:root textarea,
	HTML:root BUTTON
	{
		color: ButtonText !important;
	}
	
	HTML:root INPUT[type="text"]
	{
		background: InfoBackground !important;
	}
}

@-moz-document domain("search.plu.edu")
{
	INPUT *, TEXTAREA * 
	{
		color: black !important;
	}
	
	#search_box input:focus {
    color: black !important;
	}
}


@-moz-document domain("sourceforge.net")
{
	HTML:root textarea 
	{
		color: ButtonText !important;
	}
	
	html.js body#c2.short-head div.send_a_message div#page-body div#bd.inner-bd div#doc4.yui-t6 form p input[type="text"] {
		color: ButtonText !important;
	}
}

@-moz-document domain("deviantart.com"), domain("sta.sh")
{
	.cctextarea textarea, .previewbox
	{
		color: black !important;
	}

	#overhead label .itext
	{
		color: black !important;
	}

	#modal-form-category .pb input
	{
		color: black !important;
	}
}

@-moz-document domain("github.com")
{
	.login-form form input.text, .login-form form textarea, #top_search_form input[type="text"]
	{
		color: black !important;
	}
}

@-moz-document domain("youtube.com")
{
	.yt-uix-form-input-text, .yt-uix-form-textarea 
	{
		color: ButtonText !important;
	}
	#masthead-search-term
	{
		color: black !important;	
	}
}

@-moz-document domain("thesaurus.com"),  domain("dictionary.com"), domain("reference.com")
{
	#hdrT input {
		background: white !important;
	}
	
	.sign-form .area-holder input, input#q {
		color: black !important;
	}
}

@-moz-document domain("kongregate.com")
{
	.text_field, #search dt input 
	{
		color:#707070;
	}
}

@-moz-document domain("facebook.com")
{
	#reg_box.registration_redesign .text_field, .textInput
	{
		color: black !important;
	}

	.DOMControl_placeholder
	{
		color: graytext !important;
	}
}

@-moz-document domain("thinkgeek.com")
{
	#leftnav ul.signup li.signupbox input
	{
		color: buttontext !important;
	}
}

@-moz-document domain("existor.com")
{
	#stimulus, #sayit
	{
		color:buttontext !important;
	}
}

@-moz-document domain("grooveshark.com")
{
	input, textarea, select, .uneditable-input 
	{
		background-color: #F1F1F1;
	}
	#header-search input
	{
		background-color:white;
	}
}

@-moz-document domain("charahub.com")
{
	ul.charactergrid li select {
		color:buttontext;
	}
}

@-moz-document domain("mybb.com") {
	input#search_box {
		color: black !important;
	}
}

@-moz-document domain("barnesandnoble.com") {
	.quick-search .search-input, #quick-search-1-category {
		color:black !important;
	}
}

/* restrict to bigfuture.collegeboard.org? */
@-moz-document domain("collegeboard.org") {
	input {
		background: none repeat scroll 0 0 #EEEEEE;
	}
	select {
		color: buttontext !important;
	}
}

@-moz-document domain("random.org") {
	#cse-search-box input[type="text"]
	{
		color: black;
	}
}

@-moz-document domain("flightrising.com")
{
	.input_reg {
		color: black;
	}
	select[disabled] {
		color: infotext !important;
	}
}

@-moz-document domain("biblegateway.com")
{
	body, td, th, input, select, option {
		color:  buttontext !important;
	}
}

@-moz-document url-prefix("https://www.tumblr.com/ask_form")
{
	#question
	{
		background:white !important;
	}
}

@-moz-document url-prefix("https://www.blogger.com/comment-iframe.g?blogID=")
{
	#identityMenu
	{
		color:black;
	}
}

@-moz-document domain("aftercollege.com")
{
	.edit_label_and_field input {
		background:white;
	}
}

@-moz-document domain("globalresearch.ca")
{
	#NLform input {
		color:infotext;
	}
}

@-moz-document domain("livescience.com")
{
	.fixed-bar-search-box {
		color: #666;
	}
	
	#searchbox, .newsletter-input {
		background:white;
	}
}

@-moz-document domain("f-list.net")
{
	input#LoginBoxUsername,
	input#LoginBoxPassword
	{
		color: black;
	}
}

@-moz-document domain("weatherbug.com")
{
	#wXzip-box .wXtxt-search-box-entry
	{
		color: ButtonText !important;
	}
}

@-moz-document domain("checkshorturl.com"), domain("prevurl.com"), domain("urlsniffer.info")
{
	#shorturlform {
		background:white;
	}
	#shorturlbutton {
		color:black;
	}
}

@-moz-document domain("duckduckgo.com")
{
	.frm__select select {
		color:black;
	}
}

@-moz-document domain("mail.com")
{
	#login, #password, input[name="q"] {
		background: white;
	}
}

@-moz-document domain("emotiv.co")
{
	div.forum-filter-field span
	{
		color: InfoText;
	}
}


@-moz-document domain("bitbucket.org") {
	input {
		background:white;
	}
}


@-moz-document domain("reddit.com") {
	input.c-form-control {
		color: black;
	}
}

@-moz-document domain("weblogin.plu.edu") {
	input#username, input#password {
		color: black;
	}
}
