css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("itusozluk.com") {

/* buttons */
.entrymenu input, #entryform input, #msgfrm input, .msjmenu input{
	color:#ff0000 !important;
	background: #111111 !important;
}

/* msg */

.yanik {
	background-image: none !important;
	background-color:#c81616 !important;
}

::-moz-selection{background: #770000 !important; color: #ffffff !important;}
a::-moz-selection{background: #770000 !important; color: #ffffff !important;}
h2{color: #ffaa00 !important; font-family: Candara, "Trebuchet MS", sans-serif !important;}
.no728{background-color: #222222 !important;}
footer{background-color: #222222 !important; color: #888888 !important; padding-top: 12px !important; margin-top: 0 !important;}
footer a{color: #cccccc !important;}
footer a:hover{color: #eeeeee !important;}
div.posts{background-color: #ffffff !important;}
div.hd{color: #999 !important;}
#solframe li {line-height: 15px !important; border-bottom: 1px dotted #343434 !important;}
#solframe li:hover {background-color: #271919 !important;}
#solframe{color: #777777 !important;}
#solframe a{color: #ff7700 !important; padding-top: 6px !important; padding-bottom: 5px !important; width: 156px !important;}
#solframe a:hover{color: #ff3300 !important;}
span.nmbr{color: #777 !important; position: absolute !important; left: 178px;}

.curtit{font-weight: normal !important; background: #121212 !important; border-right: 30px solid #121212 !important;}
div.pg a{color: #ff3300 !important;}
div.pg a:hover{color: #ff7700 !important;}
div.middle{color: #aaaaaa !important;}
.title a{color: #ffffff !important;}
.title a:hover{color: #ffaa00 !important;}
ol.ceviri{position: absolute !important; right: 332px !important; top: 86px !important;}
div.main ol.ceviri a{color: #444444 !important;}
div.main ol.ceviri li a:hover{color: #cccccc !important;}
p.topgos a{color: #ffffff !important;}
p.topgos a:hover{color: #ff7700 !important;}
ol#entries li{color: #cccccc !important;}
ol#entries a{color: #ffffff !important;}
ol#entries p.dateauthor a{color: #ff2200 !important;}
ol#entries p.entrymenu a{color: #ff0000 !important;}

/* online */

div#kenks{border: 2px ridge #000000 !important; border-radius: 0 12px; padding: 0 6px 8px 6px !important; margin-bottom: 10px !important;}

/* duyurular */
div.duyurular{background-color: #882222 !important; border: 2px ridge #000000 !important; border-radius: 0 12px;}
div.duyurular h2{background-color: #202020 !important; color: #ffaa00 !important; padding-left: 10px !important;}
#dypgr{margin-top: 8px !important;}
#dypgr a{color: #ffaa00 !important;}
ul#dyrlar{margin-bottom: 8px !important;}
ul#dyrlar li{height: 18px !important; overflow: auto !important; visibility: hidden !important;}
ul#dyrlar a{border-left: 2px ridge #777777 !important; color: #c2c2c2 !important; font-weight: bold !important; padding-left: 5px !important; visibility: visible !important;}
ul#dyrlar a:hover{color: #e2e2e2 !important; border-left: 2px ridge #ffffff !important;}
ul#dyrlar li.duycontent{height: 100% !important; color: #aaaaaa !important; margin: 2px 10px 0 26px !important; visibility: visible !important;}
ul#dyrlar li.duycontent a{font-weight: normal !important; color: #ffffff !important; border: none !important; padding-left: 3px !important;}

/* favoriler */
div#faves{border: 2px ridge #000000 !important; border-radius: 0 12px; padding: 0 6px 8px 6px !important;}
div#faves a{font-size: 11px !important; color: #ff7700 !important;}
div#faves p{font-size: 9px !important; font-style: italic;}

/* belki ilginizi çeker */
div.ilgili{background-color: #882222!important; padding: 0 4px 4px 0px !important; border: 2px ridge #000000 !important; border-radius: 0 12px;}
div.ilgili h2{background-color: #202020 !important; color: #ffaa00 !important; padding-left: 10px !important; width: 98% !important;}
div.ilgili ul{margin-left: 6px !important;}
div.ilgili ul li a{color: #c2c2c2 !important; font-weight: bold !important;}
div.ilgili ul li a:hover{color: #e2e2e2 !important;}

/* radyo */
div.radyo{border: 2px ridge #000000 !important; border-radius: 0 12px; padding: 0 6px 8px 6px !important;}
div.radyo a{color: #ffaa00 !important;}
div.radyo div {color: #ffffff !important;}

/* atlas */
div#atlasselector{margin-left: 9px !important; overflow: hidden;}

div.entrykutu{margin-top: 50px !important;}

ol.paging li a.selected{color: #ff7700 !important;}

/* panel */
div.panel li.highlight{background-color: #333333 !important;}
div.panel a{color: #ffaa00 !important;}
div.panel a:hover{color: #ff7700 !important;}
div.panel fieldset div{color: #aaaaaa !important;}
tr.first{background-color: #000000 !important;}
div.middle ol a, div.general a, div.secs a{color: #ff2222 !important;}
div.middle ol a:hover, div.general a:hover, div.secs a:hover{color: #ffaa00 !important;}

/* userdisclaimer */

p.userdisclaimer{color: #999999 !important;}
p.userdisclaimer a{color: #cccccc !important;}
p.userdisclaimer a:hover{color: #eeeeee !important;}

div#main-menu{border-top: 1px dashed #c00 !important;}

/* duyurular */
.newcommentarea textarea {width:378px !important;}

}
