css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document  url-prefix(chrome://stylish/content/stylish.xul),
		url-prefix(chrome://stylish/content/manage.xul?sidebar=1) {

dialog {
padding: 5px 2px 5px 0px !important;
}

#toolbar {
margin: 0px 5px !important;
}

toolbarbutton {
padding: 4px 0px 4px 4px !important;
}

toolbarbutton > label {
display: none !important;
}

#add {
	list-style-image: url(data:image/png;base64,) !important;
}

#edit {
	list-style-image: url(data:image/png;base64,) !important;
}

#delete {
	list-style-image: url(data:image/png;base64,) !important;
}

#update {
	list-style-image: url(data:image/png;base64,) !important;
}

#help {
	list-style-image: url(data:image/png;base64,) !important;
}

toolbarbutton {
-moz-image-region: rect(0, 16px, 16px, 0) !important;
}

toolbarbutton[disabled="true"] {
-moz-image-region: rect(48px, 16px, 64px, 0) !important;
}

toolbarbutton:hover:not([disabled="true"]) {
-moz-image-region: rect(16px, 16px, 32px, 0) !important;
}

toolbarbutton:hover:active:not([disabled="true"]) {
-moz-image-region: rect(32px, 16px, 48px, 0) !important;
}

#clearFilter {
display: none !important;
}

}
