css:
#header {
       background: #97c1f4 !important;}    

table#topbar {
	margin-bottom: 35px !important;}
	
tr.oddRow, tr.evenRow {
	height: 20px !important;}
	
p.menu {
	line-height: 30px !important; 
	color: #000 !important; 
	padding-bottom: 10px !important;}
	
a:link.title {
	font-family: Arial, sans-serif !important; 
	color: #1462C1 !important; 
	font-size: 16px !important;}
	
a:visited.title {
	font-family: Arial, sans-serif !important; 
	color: #252525 !important; 
	font-weight: normal !important; 
	font-size: 16px !important;}
	
a:hover.title {
	font-family: Arial, sans-serif !important; 
	color: #003f90 !important; 
	font-size: 16px !important;}
	
a:active.title {
	font-family: Arial, sans-serif !important; 
	color: #000 !important; 
	font-size: 16px !important;}
	
.link {
	font-family: Arial, sans-serif !important; 
	margin: 0pt !important;
	border-bottom: 1px solid #c4c4c4 !important;
	padding: 4px 0px !important;}
	
.link:hover {
	font-family: Arial, sans-serif !important; 
	background: #e1e1e1 !important;}
	
.link .rank {
	font-family: Arial, sans-serif !important; 
	font-size: 16px !important;}
	
.midcol {
	background: transparent !important;}

.login-form-side {
	border:0px dotted gray !important;
	margin-top:10px !important;}
	
# {
	display: none !important;}
	
.tagline {
	color: #666 !important;}
	
.comment .child {
	margin-top: 10px !important;}
	
.comment .author {
	color: #1462C1 !important;}
	
.comment .entry {
	padding: 3px 5px 3px !important; 
	border-bottom: 0px solid #ddd !important;
	border-left: 2px solid #ddd !important;}
	
.comment .entry:hover {
	background: #fff !important;}
	
.even, .odd {
	border-left: 0px dotted #ddd !important;}
	
#sr-header-area {
	background: #020810 !important;
	border: 10px !important;
        border-bottom: 10px solid #020810 !important;
        border-top: 10px solid #020810 !important;          
	color :#fff !important;}
	
#sr-header-area a {
	color: #fff !important;
        font-size: 13px !important;}
	
#sr-more-link {
	background: #222 !important;
	color: #777 !important;}
	
.dropdown.srdrop .selected {
	color: #777 !important;}
	
.last-clicked {
	border: 0px !important; 
	border-left: 1px solid grey !important; 
	border-bottom: 1px solid grey !important;}
	
.organic-listing {
	border: 0px !important;}
	
.arrow.upmod { 
	background-image: url(http://www.reddit.com/static/aupmod.gif) !important;}
	
.arrow.downmod{ 
	background-image: url(http://www.reddit.com/static/adownmod.gif) !important;}
	
.md {-moz-border-radius: 5px !important;}

.formtab{ color: red !important; }

.link-desc {display: none !important;}

.roundfield {
  -moz-border-radius: 0px !important; 
  border: 1px dotted black !important;
  background-color: #CEE3F8 !important;
  font-size: small !important;

}
