css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* AGENT_SHEET */

#main-window[tabsontop="true"][sizemode="normal"] #toolbar-menubar[inactive] ~ #TabsToolbar {
  -moz-padding-start: 2px !important; 
}

#appmenu-button .box-inherit.button-box {
  display: inherit !important;
}

#appmenu-button .box-inherit.button-box .button-menu-dropmarker {
  display: none !important;
}

#appmenu-button .button-icon {
  display: inherit !important;
  list-style-image: url("chrome://mozapps/skin/extensions/utilities2.png") !important;
  -moz-image-region: rect(0px, 18px, 18px, 0px) !important;
  margin: 0 !important;
  padding: 0 !important;
}

#appmenu-button {
  padding: 0px !important;
  background: transparent !important;
  background-clip: padding-box !important;
  border: 1px solid !important;
  border-color: transparent !important;
  border-width: 5px !important;
  min-height: 18px !important;
  min-width: 18px !important;
  -moz-padding-start: 1px !important;
}

#nav-bar #appmenu-button {
  margin-top: 3px !important;
  margin-bottom: 3px !important;
}

#appmenu-button:hover {
  -moz-border-image: url("chrome://global/skin/icons/browser_action_hot.png") 5 fill repeat stretch !important;
}

#appmenu-button:hover:active,
#appmenu-button[checked="true"],
#appmenu-button[open="true"] {
  -moz-border-image: url("chrome://global/skin/icons/browser_action_pressed.png") 5 fill repeat stretch !important;
}
