css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("akatsuki-novels.com") {
  * {
    font-size: 100% !important;
    line-height: 1.5em !important;
    max-width: 100% !important;
  }
  input {
    line-height: 1.6em !important;
    width: auto !important;
  }
  body {
    font-size: 100% !important;
    -webkit-text-size-adjust: none !important;
    -moz-text-size-adjust: none !important;
    -ms-text-size-adjust: none !important;
    padding-top: 2.30em !important;
  }
  table {
    table-layout: auto !important;
  }
  #wrapper,
  #header,
  .story{
    width: auto !important;
    height: auto !important;
  }
  #header{
    font-size: 100% !important;
    padding-left: 1% !important;
    padding-right: 1% !important;
    position: fixed;
    top: 0 !important;
    left: 0 !important;
    width: 100% !important;
    z-index: 100 !important;
  }
  #global-menu {
    padding: 0.4em 0 0.4em 0 !important;
    float: left;
  }
  #global-menu a{
    color: #ffffff !important;
    margin-left: 0.5em !important;
    background: none !important;
    text-indent: 0px !important;
    width: auto !important;
    height: auto !important;
  }
  #global-menu li:nth-of-type(1) a{
    margin-left: 0 !important;
  }
  #g-m-novels a:after {
    content: "小説検索";
  }
  .header-r {
    padding: 0 !important;
    width: auto !important;
    margin-right: 2% !important;
  }
  .header-r li{
    float: right !important;
    padding: 0.4em 0 0.4em 0 !important;
  }
  .header-r li a{
    text-indent: 0px !important;
    background: none !important;
    margin-right: 0.5em !important;
    height: auto !important;
    color: #ffffff !important;
    width: auto !important;
    height: auto !important;
  }
  .header-r li:nth-of-type(1) a{
    margin-right: 0 !important;
  }
  .header-r li#m-login a:after{
    content: "ログイン";
  }
  .header-r li#m-register ,
  .header-r li#m-logout{
    display: none !important;
  }
  #header .fl-l,
  .features,
  .features + div,
  #member-counter,
  #header-login-status,
  #g-m-about,
  #g-m-users,
  #g-m-faq,
  #g-m-manual,
  #sub-header,
  #trace,
  #footer,
  .sp,
  .body-novel ~ a[href*="/bookmark/"],
  script[src*="show_ads.js"]{
    display: none !important;
  }
  .txt-c {
    text-align: left !important;
  }
  .body-novel{
    line-height: 1.5 !important;
  }
  #contents-inner2 #contents {
    margin-right: 0px !important;
  }
  #contents div {
    width: auto !important;
  }
  #contents div:nth-of-type(5),
  .topicsBox table{
    width: 100% !important;
  }
  .story .body-normal {
    padding: 10px 0 0 0 !important;
    width: 92% !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  .story .body-normal .body-normal {
    width: auto !important;
  }
  .topicsBox strong{
    width: auto !important;
  }
  .novelTitle{
    width: auto !important;
  }
  #contents-inner2 > p:nth-of-type(1){
    width: 92% !important;
    margin-left: auto !important;
    margin-right: auto !important;
    text-align: right !important;
    color: #ffffff !important;
  }
  #contents-inner2 > p:nth-of-type(1) a,
  #contents-inner2 > p:nth-of-type(1) s{
    display: none !important;
  }
  #contents-inner2 > p:nth-of-type(1) a[href*="favorite_novel"] {
    color: #0000FF !important;
    display: inline !important;
  }
  .list-box-inner hr{
    clear: left !important;
  }
  .body-normal .paging_for_view,
  .body-normal a[href*="/bookmark/"],
  .body-normal a[href*="#bottom"],
  .body-normal a[href*="#top"]{
    font-size: 100% !important;
  }
  .body-normal a[href*="#top"]{
    margin-top: 1em !important;
  }
  .body-normal .paging_for_view span{
    padding-left: 0.3em !important;
    padding-right: 0.3em !important;
  }
  #novel #sidebar {
    display: none !important;
  }
  #ranking #sidebar {
    float: none !important;
    width: auto !important;
    padding-left: 0.3em !important;
  }
  #ranking #sidebar h3{
    margin-bottom: 0 !important;
  }
  #ranking #sidebar ul{
    float: left !important;
    font-size: 100% !important;
    margin: 0 !important;
  }
  #ranking #sidebar ul li{
    float: left !important;
    list-style-type: none !important;
    padding: 0 !important;
  }
  #ranking #sidebar ul li a{
    text-indent: 0px !important;
    background: none !important;
    height: auto !important;
    width: auto !important;
  }
  #novel #contents,
  #ranking #contents {
    margin: 0 !important;
    clear: both !important;
  }
  #novel #contents div[style*="float"],
  #ranking #contents div[style*="float"]{
    float: none !important;
  }
  #novel #contents .topicsBox,
  #ranking #contents .topicsBox{
    border: none !important;
  }
  body#mypage #container {
    margin-right: 0 !important;
  }
  body#mypage #contents {
    float: left !important;
    margin-left: 0 !important;
  }
  body#mypage #contents-inner {
    margin: 0 0 0 0 !important;
    padding: 0 !important;
  }
  body#mypage #contents-inner .list tr,
  body#mypage #contents-inner .list th{
    width: auto !important;
  }
  body#mypage #contents-inner > div:nth-of-type(1),
  body#mypage #contents-inner .box p,
  body#mypage #contents-inner .box h3,
  body#mypage #contents-inner > div:nth-of-type(1n+3),
  body#mypage #left-sidebar,
  body#mypage #right-sidebar .list-box > div:nth-of-type(1n+2) {
    display: none !important;
  }
  body#mypage #right-sidebar {
    float: none !important;
    width: 100% !important;
  }
  body#mypage #right-sidebar .list-box,
  body#mypage #right-sidebar .list-box-inner,
  body#mypage #right-sidebar .list-box-inner h3{
    width: auto !important;
    height: auto !important;
  }
  body#mypage #right-sidebar .list-box-inner h3{
    background: none !important;
    color: #000000 !important;
  }
  body#mypage #right-sidebar .list-box-inner li{
    list-style-image: none !important;
    margin-left: 0 !important;
  }
  body#mypage #side-menu ul li a {
    width: auto !important;
    background-color: #2E3342 !important;
  }
  body#novel #contents-inner2 > .story > .body-normal{
  }
  body#user #contents-inner2 > .box > div:nth-of-type(1){
    width: 92% !important;
  }
  .font-bb {
    clear: both !important;
  }
  div.box {
    padding: 0 !important;
  }
  div.list table {
    border: none !important;
  }
  div.box,
  div.list,
  table tbody,
  table thead,
  div.list table th,
  div.list table td{
    border-top: none !important;
    border-left: none !important;
    border-right: none !important;
  }
  div.list table tr td:nth-of-type(1) {
    word-break:keep-all !important;
  }
  #novel table.list tr th {
    width: auto !important;
  }
  #novel table.list tr td:nth-of-type(2) {
    width: 9em !important;
    word-break:normal !important;
  }
  #novel iframe,
  #novel ins {
    width: 100% !important;
  }
  div.list table tr td:nth-of-type(1) br{
    display: none !important;
  }
  #star {
    width: 92% !important;
  }
  textarea {
    width: 90% !important;
  }
}
