/* Кинопоиск - Упрощенный стиль v1.0 */
/* Автор - Dimaster, 03.01.2013 */
/* За основу взят стиль пользователя PoZitron */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kinopoisk.ru")
{

html {background: none !important}
body {background: none !important}
body {background-color: #E0E0E2 !important}
.menu p{border-color: #ff6600 !important}
.menu .act p {color: #ff6600 !important}
.menu .act .ar {background: #ffc29a !important}
.search_line_flash {visibility: hidden !important}
a.brand.snap_noshots {visibility: hidden !important}
div#brandingFlash {visibility: hidden !important}

/* убираю пустоту вверху (поднимаем "шапку") */
.menu {top:40px !important}
#top_form {top:40px !important}
.png_block {top:40px !important}

/* подгоняю под "шапку" */
#top {height: 120px !important}

/* убираю баннер загораживающие ссылки */
#branding_flash {display: none !important}
.search_line_image {display: none !important}

/* блок сверху */
.png_block {background-color: #151515 !important}

}
