css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("realgm.com") {

blockquote {
  background-color:#FFFFFF!important;
  border:thin solid #336598!important;
  margin-left:10px !important;
  margin-right:10px !important
}

cite{ 
  padding:0 !important;
}

.adheader, .adsmall, #header, #submenu, #topmenu, #footer, #google_ads_div_Basketball_Tier3_160x600{
  display: none !important;
}

#forumextras{
  background: #F4F3EE !important;
  height: 5px !important;
  margin-left: 0px !important;
  margin-bottom: -20px !important;
  margin-top: 0px !important;
  vertical-align: top !important;
  padding-top: 10px !important;
  padding-left: 0px !important;
  width: 100% !important;
}

#forumextras #newswrap{
  visibility: hidden !important;
}

#forumextras:hover #newswrap{
  visibility: visible !important;
  height: auto !important;
  margin: 0px 0px 10px 10px !important;
}

#forumextras:hover{
  height:auto !important;
}

#pagewrap{
  width:70% !important;
  padding: 0pt !important;
  background: #ffffff !important;
}

#wrap, #page-body{
  width:98% !important;
  background: #FEFDFA !important;
  margin: 0px !important;
  padding: 5pt !important;
}

body{background: none !important;}

input.tiny{
  width:300px !important;
}

.postprofile{
  width:90px !important;
}

.postbody{
  width:83%!important;

}

.forumbg {
  background-image:none !important;
  background-color:#336598 !important;
}


.views{
width: 60px !important;
}

.posts{
min-width: 50px !important;
}

.row{
  background: #e1ded2 !important;
overflow: hidden !important;
}

.row .views{
  background: #f0eee5 !important;
  margin-top: -2px !important;
  margin-bottom: -2px !important;
  min-height: 35px !important;
}

.row .posts{
  background: #e1ded2 !important;
  border:0px !important;
  margin-top: -2px !important;
  margin-bottom: -2px !important;
  min-height: 35px !important;
}

.row .lastpost{
 
  color: black !important;
  margin-top: -2px !important;
  margin-bottom: -2px !important;
  min-height: 35px !important;
  width: auto !important;
}

li.row, dl.row  {
  border-color: white !important;
  padding: 0px !important;
}

.row dt{
  margin-top: -2px !important;
  margin-bottom: -2px !important;
  min-height: 43px !important;
}

dt{
  width:50%!important;
}

.lastpost{
  width:auto !important;
}

*[class*="post bg1"]{ background-color: #F4F3EE ! important}
*[class*="post bg2"]{ background-color: #EDEBE4 ! important}
*[class*="row bg1"]{ background-color: #F4F3EE ! important}
*[class*="row bg2"] { background-color: #EDEBE4 ! important}

*[class*="content"] { font-family:Verdana ! important } 
*[class*="content"] { font-size: 12px ! important } 
*[ID=page-body] { font-family:Verdana ! important } 
*[ID=page-body] { font-size: 90% ! important } 

}
