css:
@-moz-document domain(tumblr.com) {
#dashboard_index, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members{
background-color:#3c3c3c !important;
background-image:url('http://i.imgur.com/eOldP.png') !important;
background-attachment:fixed !important;
}

#dashboard_edit_post{
background-color:#3c3c3c !important;
background-image:url('http://i.imgur.com/eOldP.png') !important;
background-attachment:fixed !important;
}

.dashboard_edit_post without_auto_paginate logged_in layout_standard{
background-color:#3c3c3c
}
body {
background-color:#3c3c3c;
background-image:none;
background-attachment:fixed;
}

#right_column::after {
display: none;
}

#posts .post .permalink {
background: none;
}

#pagination a {
background-color: #5b5b5b !important;
}

#logo a {
height: 0 !important;
width: 0 !important;
padding-left: 230px !important;
padding-top: 85px !important;
margin-top: -5px !important;
background: url(http://i54.tinypic.com/2dgqlgz.png) no-repeat !important;}

div.l-header .selection_nipple {
border-bottom: 12px solid #5B5B5B;
}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(100,100,100,0.8) !important;
}

#dashboard_edit_post {
background-color: #ffffff !important
}

#right_column .dashboard_nav_item {
border-bottom:0px solid #eee !important;
background-image:none  !important
}

#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a {
color:#fff !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
color:#888 !important ;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background: none !important;
}

#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:#fbfbfb !important;
opacity:0.5 !important;
}

#left_column #posts .notification .avatar:hover{
opacity:1 !important;
}

.dashboard_switch_blog_menu_header{
background-color:#fbfbfb !important;
color:#666 !important;
}

.dashboard_switch_blog_menu_item{
background-color:#fbfbfb !important;
color:#666 !important;
border:0px !important
}

.dashboard_switch_blog_menu_footer{
background-color:#fbfbfb !important;
color:#666 !important;
}

.dashboard_switch_blog_menu_wrapper{
background-color:#fbfbfb !important;
}

#new_post_notice_container{
color:#fff !important
}

#new_post_notice_container a{
color:#fff !important
}

#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current {
background-image:none !important
}



#left_column #posts .notification {
background-color:#fbfbfb !important;
color:#666 !important;
border-bottom-color: #eee !important
}

#left_column #posts .notification .block {
color:#333 !important;
}

#pagination a {
background-color:#5b5b5b;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:#5b5b5b;
}

#left_column #posts .notification.alt {
background-color: #f7f7f7 !important;
}

#left_column #posts .notification.last_notification, #left_column #posts .notification.single_notification {
border-bottom-color:#ececec !important;
}

#left_column #posts .notification a {
color:#565656 !important;
border-bottom:0px !important;
}

#left_column #posts .notification blockquote {
border-left-color:#eee !important;
}

#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:#fbfbfb !important;
opacity:0.5 !important;
}

#left_column #posts .notification .avatar:hover{
opacity:1 !important;
}

.notification_type_icon{display:none !important}

::selection {
        background: #fff; 
color:#000
        }
::-moz-selection {
        background: #fff; 
color:#000}


#left_column #posts .post .post_avatar{
-moz-border-radius: 38px !important; 
-webkit-border-radius: 60px !important; 
width: 64px !important; 
height: 64px !important; 
margin: 0px !important;
}

#right_column {
background-color: none
}

.photoset{
background-color:white
}
}
