css:
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document domain(strims.pl) {

/*
Podziękowania dla C2ShadowMan i JesterRaiin
Autor: binprogrammer
*/


#top_bar {
background: #820020 !important;
color: white !important;
height: 20px !important;
position: relative !important;
z-index: 1000 !important;
overflow: hidden !important;
font-size: 0 !important;
white-space: nowrap !important;
}


#top_bar_left {
background: #820020 !important;
color: white !important;
display: inline-block !important;
line-height: 20px !important;
padding: 0 12px !important;
font-size: 12px !important;
}


#top_bar_more {
background: #820020 !important;
color: white !important;
display: block !important;
height: 20px !important;
line-height: 20px !important;
padding: 0 12px !important;
position: absolute !important;
right: 0 !important;
top: 0 !important;
z-index: 50 !important;
font-weight: bold !important;
font-size: 12px !important;
}

body {
background: #820020 !important;
color: black !important;
font-family: arial,sans-serif !important;
font-size: 13px !important;
font-weight: normal !important;
font-style: normal !important;
font-variant: normal !important;
word-wrap: break-word !important;
}

.border_gray {
border-color: #820020 !important;
}

div.template_wrapper {
padding: 0 20px !important;
max-width: 1200px !important;
margin: 0 auto !important;
background: #FFFFF0 !important;
}

ul.menu_bookmarks li a {
border: none !important;
}

#top_bookmarks li.selected a {
background: #820020 !important;
color: white !important;
}

#top_user_menu li a.notifications span.haze {
background: #820020 !important;
}
.background_gray {
background: #FFFFF0 !important;
}

span.icon_light.on {
background: none !important;
}

}
