css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.wykop.pl/') {

div.rel section.fleft div.scale
{
    margin-right: 310px!important; /*zmniejsza margines między treścią a boczną belką*/
}

#scrolling
{
    background: transparent!important;  /*wyłącza biały gradient pod belką wykopu*/
}

.wrapper 
{
padding: 0 5px!important; /*marginesy boczne*/
}

section.margintop30
{
margin-top: 7px!important; /*margines pod belka*/
}
aside.margintop30
{
margin-top: 7px!important; /*margines pod belka*/
}

.recentPlaceHolder
{/*marginesy i wysokość komunikatu o nowych wpisach...*/
height: 22px!important;
margin: 5px!important;
padding: 0px!important;
}
.alert
{
    padding:1px!important; /*... i jego marginesy z tłem*/
}

.activitiesStream .marginbott10
{
margin: 1px!important; /*odstepy pomiedzy komentarzami/wpisami*/
}

.activitiesStream .pding5
{/*odstepy pomiedzy wpisami i pierwszymi komentarzami*/
padding: 1px!important; /* razem z tlem */
padding-bottom: 0px!important;/*dolne*/
margin-bottom: 3px!important;
}

.activitiesStream .dropshadow 
{/*cienie pod wpisami*/
-webkit-box-shadow: none!important;
box-shadow: none!important;
}

.activitiesStream .brededed {
border: 0px!important;/*ramki*/
background-color: #101010; /*tlo wpisow (komentarzy)*/
}

.activitiesStream .subcomments blockquote
{
border-top: 1px #101010 solid!important; /*linie pomiędzy komentarzami */
}

.activitiesStream .bgfa
{
     background:transparent!important; 
} /*tlo komentarzy*/

.activitiesStream .brbottf1 {border-bottom: 0px solid #f1f1f1!important;} /*kreska pod nickami we wpisach */
/*.activitiesStream .bgf3f3f3 {background: #f6f6f6!important;} kolor "pokaz wszystkie komentarze*/
.activitiesStream li.aC {margin-bottom: 6px!important} /*odstepy pomiedzy wpisami (nie komentarzami) */

.newtag { /*tagi*/
    background:transparent!important;/*tlo*/
    border:0!important;/*ramki*/
    box-shadow:none!important;/*cienie*/
    text-shadow:none!important;
    margin: 1px!important;/*odstepy*/
    padding: 0px!important;
}
.newtag .number {/*cyferki przy tagach*/
    border:0!important; /*ramka*/
    opacity:0.5!important; /*przezroczystosc*/
    /*color: gray!important;* /*kolor*/
    text-shadow:none!important;/*cien*/
    margin: 0px!important;/*odstepy*/
    margin-right: 3px!important;
    padding: 0px!important;
}
.newtag:hover .number {/*numerki po najechaniu mysza*/
    opacity:1!important;
    transition:0.2s;
    margin: 0px!important;/*odstepy*/
    margin-right: 3px!important;
    padding: 0px!important;
}

.minheight42 {padding-bottom: 3px!important;} /* "bo pasek płci wystawał pod wpis" */


}
