css:
@-moz-document url-prefix("http://narenji.ir"), url-prefix("http://www.narenji.ir") {
body,
.bodyclass {
  font: 18px "B Roya", sans-serif !important;
  background: #fff !important;
}
h1, h2, h3, h4, h5, h6 { font-fmaily: "B Roya" !important; }

#col-top { display: none !important; }
#left-sidbar { display: none !important; }
#right-sidbar { float: none !important; }
#col3 { display: none !important; }
#col2 { margin: 0 auto !important; float: none !important; }
#tabliq-bottom-content { display: none; }

.node,
.view-content .views-row {
  box-shadow: none !important;
  border-bottom: 1px solid #ebebeb;
  margin-bottom: 40px;
  padding: 0 !important;
}

.node h1,
.inner_content .views-field-title,
.inner_content .views-field-title h2 {
  font: normal 28px "B Roya", sans-serif !important;
}
.inner_content .views-field-title a,
.node h1.title a { color: #000; }
.inner_content .views-field-title a:hover,
.node h1.title a:hover { color: red; }
#col2 .views-field-created,
.node .submitted {
  color: #cecece;
  text-align: left;
  border-bottom: 1px solid #EBEBEB !important;
  font-size: 14px;
}
.views-field-name { color: #cecece; }
.views-field-teaser,
.field-content blockquote,
.node .content blockquote,
.field-content p,
.node .content p {
  font: 18px/34px "B Roya", sans-serif !important;
  color: #4D4D4D;
  text-align: justify;
}
.views-field-teaser { margin-top: 15px; }
.views-field-teaser a,
.node .content a {
  color: black;
  border-bottom: 1px solid #ebebeb;
}
.views-field-teaser a:hover,
.node .content a:hover {
  color: black;
  border-bottom: 1px solid red;
}
#col2 .views-field-field-content-more-value span { background: none; }
.views-field-view-node a,
.views-field-field-content-more-value a,
.views-field-comment-count a {
  font: 18px/34px "B Roya", sans-serif !important;
  color: black;
}
.views-field-view-node a:hover,
.views-field-field-content-more-value a:hover,
.views-field-comment-count a:hover {
  font: 18px/34px "B Roya", sans-serif !important;
  color: red;
}
.views-field-nothing-1 { padding-bottom: 10px; }
.narenjisection a:hover { color: black !important; }
.bodyclass .narenjipic img,
.bodyclass #col2 .views-field-field-story-main-image-fid img {
  margin: 0 !important;
}
}
