css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url("http://vampirefreaks.com/profiles/home.php")
{

/* STYLES SPECIFIC TO THIS PAGE */
.loggedin { /* the main outer table */
margin-left: auto; margin-right: auto; 
table-layout: fixed;
text-align: left;
}
.loggedin td { vertical-align: top; }

.onlinefriendsbox { /* lower right dark box with online friends in */
padding-top: 10px;
}
.friendicon { /* small online friends pictures */
border: 1px solid #444444;
width: 50px;
height: 50px;
margin: 4px;
float: left;
}

.latestupdates { /* latest cult updates table */
border: 1px solid #343434;
}
.latestupdates td { 
vertical-align: middle; 
}
.titlecell { /* titles on latest cult updates table */
font-size: 8pt;
line-height: 15pt;
}


.tdnoborders td { border: none; }

table.main .googleads{display:block!important;}
    .googleads{display:block!important;}
    div .googleads{display:block!important;}


.menu, .menu2, .menu2 a:link, .menu2 a:visited, .menu a:link, .menu a:visited  {  font: bold 8pt arial!important; color:#ffffff!important; font-variant: small-caps;   }  


.menu, .menu a:link, .menu a:visited { background: #2e1d3f!important;  }  

#menu0 {  width: 750px!important; margin:0px auto; margin-bottom:8px; ;}  

.menu2 table { height: 20px!important;}  

.menu2, #menu0 table { border: none!important; }  .
menu2 table, #menu0{ border: 1px solid #5d4270!important; }   

.maintop, .profileurl, .titletext, .icon, .newstuff, h3, .earnpoints {display:none;}

a:link, a:visited{  
font: bold 10pt "trebuchet ms", times new roman!important;  
text-decoration: none;  
background: none;
text-align: left;}    

a:hover{    
color: white!important;  
text-decoration: none;  
background: none!important; 
border: none; 
text-align: left;
font-variant: small-caps!important;}  

}

td {border: none!important;}

.leftnav a {text-align: center!important;}
