css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("law.e-gov.go.jp") {
body
{
  line-height: 130% !important;
  margin: 1% 5%;
}
a:link, a:visited
{
  color: inherit !important;
  text-decoration: none !important;
  border-bottom: dotted 1px #0000ff;
}
p
{
  text-indent: 0 !important;
}
/* 法令名 */
body > b
{
  display: block;
  text-align: center;
  color: #009999;
  background-color: #eeffee;
  border-top:    dashed 1px #bbffbb;
  border-bottom: dashed 1px #bbffbb;
  border-radius: 10px;
  box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  line-height: 150%;
}
div[align="right"], div[align="right"] td
{
  font-size: x-small;
}
div[align="right"]
{
  float: right;
}

/* 目次 */
a[target="data"]
{
  font-size: small;
}

body > a[target="data"]
{
  margin-left: 15%;
}

td > a[target="data"]
{
  line-height: 120%;
}

/* 款/章/附則 */
p > b, a > b
{
  display: block;
  color: #cc0066;
  background-color: #fff0f0;
  border-bottom: dashed 1px #ffcccc;
  border-radius: 10px;
  box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  line-height: 150%;
  text-indent: 1em;
}
form + p > b
{
  margin: -10px;
}

/* 条 */
div.arttitle
{
  background-color: #fafaff;
  text-indent: 0 !important;
  color: #777777;
}
div.item
{
  background-color: #fafaff;
  border: solid 1px #ccccff;
  border-radius: 10px;
  text-indent: 0 !important;
  margin: 0 auto 3px auto;
}
div.item > b
{
  display: block;
  color: #009999;
  background-color: #f0fff0;
  border-bottom: dashed 1px #ccffcc;
  border-radius: 10px;
  margin: 3px;
}
/* 箇条書き */
.para1, .para2, .para3, .para4, .para5,
.para6, .para7, .para8, .para9, .para10, .number
{
  margin: auto 5px;
  text-indent: 0 !important;
}
.para1 > b, .para2 > b, .para3 > b, .para4 > b, .para5 > b,
.para6 > b, .para7 > b, .para8 > b, .para9 > b, .para10 > b,
.number > b
{
  font-size: small;
}
.number > b
{
  color: #6666cc;
}
.para1 > b
{
  color: #66cc66;
}
.para2 > b
{
  color: #cc6666;
}
.para3 > b
{
  color: #cccc66;
}
.para4 > b
{
  color: #66cccc;
}
.para5 > b
{
  color: #cc66cc;
}
/* 表 */
table
{
  border: 1px #e3e3e3 solid;
  border-collapse: collapse;
  border-spacing: 0;
  background-color: white;
  font-size: small;
  margin: 5px;
}
tr:first-child
{
  background-color: #eeeeff;
  color: #777777;
}
tr:hover
{
  background-color: #ddddff;
}
td
{
  padding: 5px;
  border: 1px #e3e3e3 solid;
  border-width: 0 0 1px 1px;
  text-align: center;
}
body > table td
{
  text-align: left;
  white-space: normal !important;
}
}
