css:
@-moz-document domain("boards.4chan.org") {
form {
background: rgb(247, 247, 247)!important;
border-left: 1px solid rgba(214, 38, 38, 0.47);
border-right: 1px solid rgba(214, 38, 38, 0.47);
border-top: 1px solid rgba(214, 38, 38, 0.47);
border-bottom: 1px solid rgba(214, 38, 38, 0.47);
margin: 0 auto !important;
max-width: 900px !important;
padding: 25px 5px 5px 5px;
width: 85% !important;
}

div.deleteform {
margin-top: 10px!important;
}

div.navLinks {
margin-bottom: 15px!important;
}

#quickrep {
position: absolute;
border-radius: 0px;
-webkit-box-shadow: #888 4px 4px 6px;
top: 200px;
left: 300px;
z-index: 3;
border: 1px solid #AAA;
border-image: initial;
}

.recaptchatable a img {
background-image: url(http://i.imgur.com/yCO2k.png) !important;
border: 0;
}

span.pln {
color: #2D8B4C;
}

div.navLinks {
margin-bottom: 10px;
height: 0px;
}

a, a:visited {
color: red!important;
}

.center, .middlead, .globalMessage {
display: none;
}

#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, .reply, #backwash_tooltip {
color: #575757 !important;
}

.recaptchatable .recaptcha_image_cell center img {
height: 57px;
width: 240px!important;
}

@media screen and (-webkit-min-device-pixel-ratio: 0)
tbody textarea {
width: 232px!important;
}

@media screen and (-webkit-min-device-pixel-ratio: 0)
tbody textarea {
margin-bottom: -3px!important;
width: 232px!important;
}

.recaptchatable .recaptcha_image_cell center {
height: 57px;
width: 242px!important;
}

div.reply {
background-color: rgb(248, 248, 248)!important;
border:  1px solid rgba(214, 38, 38, 0.47)!important;
display: table;
padding: 2px;
}

body {
		background: rgb(236, 236, 236)!important;
		color: #000000!important;
	}
	
	.globalMessage {
		padding-top: 20px;	
	}
	


	.postingMode, .bf, blockquote embed, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], #recaptcha_logo, #recaptcha_tagline, .pagelist > div.prev, .pagelist > div.next {
		display: none !important;
	}

    
	div.hide_reply_button {
		display: block !important;
	}

	.postarea form div {
		border: none !important;
	}

	div.postarea, div.pagelist div.pages, div.pagelist {
		padding: 0 !important;
	}


	a {
		color: #FF6B6B!important;
	}

	hr {
		display: none !important;
	}

	#recent-images li {
		background-color: transparent !important;
	}
	
	#boardNavDesktop, #boardNavDesktopFoot, #boardNavMobile {
		color: #000 !important;
		width: 100%;
		text-align: center;
	}
	
	#boardNavMobile {
		color: #fff !important;
		border-bottom: none !important;
	}

	#boardNavDesktop *, #boardNavDesktopFoot * {
		display: inline;
	}

	#boardNavDesktop *, #boardNavDesktopFoot *,	#boardNavMobile * {
		color: #eee;
		font-size: 7pt;
	}

	#boardNavDesktop a:hover, #boardNavDesktopfoot a:hover {
		background: #555;
	}

	form {
		margin: 1em;
		max-width: 1000px !important;
		width: 85% !important;
	}

	.inputtext, textarea {
		width: 295px;
	}

	.rules {
		display: none;
	}

	table.postForm > tbody > tr > td:first-child, .qrHeader {
		background: #444 !important;
		border: 1px solid #666 !important;
		color: #aaa !important;
		font-size: 6pt !important;
	}

	h1, h2, h3, h4 {
		color: gray !important;
	}
	
	dt, .boxcontent, #updater {
		color: #fff !important;
	}

	.menubutton {
		background-image: none !important;
	}

.reply:target, .reply.highlight {
background: #FAFAFA!important;
border: 1px solid #111!important;
padding: 2px;
}

.nameBlock .capcode, .postertrip, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, span.subject, .nameblock .name, .omittedposts, .abbr, a.useremail .name, .name {
color: #5C5C5C !important;
font-size: 8pt;
}
	
	.capcodeAdmin .name {
		color: #a55 !important;
	}
	
	span.subject {
		color: #e15014 !important;
	}

	#hd, small, font[size="1"], font[size="2"], #ft, #option-button, #navtopr, .boardBanner, .sideArrows, img.rotating, .stylechanger {
		display: none !important;
	}


	.pagelist > div {
		font-size: 8pt;
	}

	.pagelist > div a, .pagelist > div strong {
		color: #eee !important;
	}

	.pagelist > div strong {
		background-color: #883535;
	}

	.pagelist > div input {
		display: none !important;
	}

	.pagelist > div:first-child {
		display: none;
	}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt ! important;
		color: #333!important;
		background: #fff !important;
		border-color: gray !important;
		border: 1px solid;
	}

	#filter-container .yuimenuitem-selected {
		border-color: gray !important;
	}

	.boxbar, #preview-tooltip-nws h2, #preview-tooltip-ws h2, #disclaimer-dialog h2, .hd {
		background-color: transparent !important;
	}

	dd {
		color: #fff !important;
		border-bottom: 1px gray solid !important;
	}

	input[type="submit"], input[type="button"], select {
		background: #444 !important;
		color: #aaa !important;
		border: 1px solid gray;
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt !important;
	}

	input[type="checkbox"] {
		-moz-appearance: none !important;
		-webkit-appearance: none !important;
		background: rgba(0,0,0,.4) !important;
		border: 1px solid rgba(255,255,255,.3) !important;
		height: 10px !important;
		width: 10px !important;
		border-radius: 2px !important;
	}

	.reply input[type="checkbox"] {
		border: 1px solid rgba(255,255,255,.3) !important;
	}

	input[type="checkbox"]:checked {
		background: rgba(255,255,255,.4) !important;
	}
	
	div[style="width: 100%; font-size: 4px; background-color: rgba(224,64,0,1);"] {
		display: none !important;
	}

	th {
		background: #222 !important;
	}

	th font {
		font-size: 7pt;
	}

	td.deletebuttons {
		font-size: 8pt !important;
		color:#eee !important;
	}
	
	img#recaptcha_reload,
	img.refreshBtn {
		height: 0px !important;
		padding-top: 18px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}

	img#recaptcha_switch_audio {
		height: 0px !important;
		padding-top: 15px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}
	
	img#recaptcha_whatsthis {
		height: 0px !important;
		padding-top: 16px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}

	.pagelist, .pagelist div {
width: 100% !important;
background-color: #983b3b!important;
	}

	.pagelist .pages {
		padding: 1px 5px;
		text-align: center;
	}	
	
	.new {
		background: rgba(255,255,255,.4) !important;
		border-radius: 5px;
	}

	.postarea  td.recaptcha_image_cell, #quickrep td.recaptcha_image_cell,  td[style="padding-left: 7px;"] {
		padding: 0 !important;
	}

	#recaptcha_image img {
		opacity: 0.8;
	}

	.recaptchatable #recaptcha_response_field, #quick_recaptcha_response_field {
		margin-top: 3px !important;
	}

	.recaptcha_input_area {
		padding: 3px 0 0 0 !important;
	}

	tr[height="73"], tr[height="73"] td {
		height: 59px !important;
	}
}

@-moz-document url("https://www.4chan.org/") {
#announce .boxbar {
background: #474747;
color: white;
}

.boxbar {
background: #CF5B5B;
color: white;
}

.left-box .boxbar {
background: #4D4D4D;
color: white;
}

.right-box .boxbar {
background: #4D4D4D;
color: white;
}

.right-box {
border-color: black!important;
color: #992E2E;
background: white;
}

.left-box {
color: black;
background:white;
}

#recent-images li {
background: rgba(250, 250, 250, 0);
}

#logo {
background: url('http://i.imgur.com/8FSp6.png') top left no-repeat!important;
width: 400px;
}

body {
background: white;
color: #800;
}

#preview-tooltip-ws {
background: white;
border-color: black;
padding: 0;
}

#preview-tooltip-nws {
background: white;
border-color: black;
padding: 0;
}

.top-box .menubutton {
background-image: url('http://i.imgur.com/SSK26.png');
}

.right-box .menubutton {
background-image: url('http://i.imgur.com/xM4nF.png');
}

#ft li a:hover {
color: #C33;
}

#ft li.current, #ft li.fill {
background: white;
color: #800;
}

#ft li {
background: #F1F1F1;
}

html {
background: white;
}
}
