css:
/* column mode */
body {
	width: 1200px !important;
	margin: 0 auto !important;
}
/* base text color */
html, body, div#boardNavDesktop, div#boardNavDesktopFoot, div.pagelist, .boxcontent, .content {
	color: #434343 !important;
}
/* floating bars */
div#boardNavDesktop, div.pagelist {
	position: fixed !important;
	left:0 !important;
	top:0 !important;
	width: 100% !important;
	text-align: center !important;
	background: #080808 !important;
	padding-top: 5px;
	padding-bottom: 5px;
	z-index: 99999 !important;
}
div.pagelist {
	top: auto !important;
	bottom:0 !important;
	border: 0 !important;
}
div.pagelist > div, div.pagelist div.pages  {
	float: none !important;
	display: inline-block !important;
}
div.boardBanner { 
	left: 20px !important;
	position: fixed !important;
	top: 30px !important;
}
img {opacity: 0.4 !important;}
img:hover { opacity: 1 !important;}
div.pagelist div.cataloglink, #ft li { border: 0 !important; }
a, .useremail:not(:hover) .name, .useremail:not(:hover) .postertrip, .quoteLink, .quotelink, .deadlink, div.post div.postInfo span.nameBlock span.name {
	color: #4b5f72 !important;
	text-decoration: none !important;
}
a:hover, .useremail:hover *, div.post div.postInfo span.subject, div.pagelist strong, #ft li.current a, #ft li.fill a, dt, .boxbar {
	color: #0083fe !important;
}
.warning { color: #8F0000 !important; }
/* base background color */
html, body {
	background: #121212 url(data:image/gif;base64,%3D) !important;
}
.left-box hr {
	background-color: #434343 !important;
	color: #434343 !important;
	border-color: #434343 !important;
}
hr, .yotsuba_new .panelHeader, .yui-skin-sam .yuimenu ul, .boxcontent dd, .boxcontent, .boxcontent hr {
	border-color: #434343 !important;
}
#recent-images li, #ft li, .boxbar, .left-box .boxbar, .right-box .boxbar, #filter-container .yuimenuitem-selected, #optionsmenu .yuimenuitem-selected, #preview-tooltip-ws h2 { background: transparent !important; }
div.op {
	display: block !important;
}
div.reply, div.op, #quote-preview, #globalMessage, .top-box, #announce, .left-box, .right-box, .yui-skin-sam .yuimenu .bd, #preview-tooltip-ws  {
	background: #080808 !important;
	padding: 15px !important;
	border: 0 !important;
	width: 100% !important;
	color: #505050 !important;
	border-bottom: 1px solid #232323 !important;
	border-radius: 4px !important;
}
#preview-tooltip-ws {
	width: auto !important;
}
/* triangles in menu */
.yui-skin-sam .yuimenuitem-checked, .top-box .menubutton, .right-box .menubutton {
	background-image: none !important;
}
#filter-container a, #options-container a {
	display: inline-block !important;
}
.right-box .menubutton:after,
.top-box .menubutton:after {
	position: relative;
	left: -15px;
	content: "▼";
	color: #505050;
}
.yui-skin-sam .yuimenuitem-checked:before {
	content: "✔";
	color: #505050;
	margin-left: 6px !important;
	margin-right: -6px !important;
}
a.yuimenuitemlabel-checked {
	padding: 0 10px !important;
}
.yui-g .yui-u, .yui-g .yui-g, .yui-g .yui-gb, .yui-g .yui-gc, .yui-g .yui-gd, .yui-g .yui-ge, .yui-g .yui-gf {
	width: 47.5% !important;
}
#globalMessage { margin-bottom: 20px !important; }
.reply:target blockquote.postMessage, .reply.highlight blockquote.postMessage {
	color: #0083fe !important;
}
.reply:target,.reply.highlight {
	background: #050505 !important;
}
#quote-preview {
	box-shadow: 0 0 15px 10px rgba(0,0,0,0.7) !important;
}
.UIPanel > div {
	width: 400px !important;
}
div.sideArrows, table#postForm .rules, div[class="center middlead"], body > hr, div.postingMode, body > div.navLinks, div#hd {
	display: none !important;
}
body > form[name="post"]:before {
	content: "Move cursor over here for a cake";
}
div#doc > div#bd > div.yui-g:before {
	display: block !important;
	text-align: center !important;
	content: "Move cursor over here for a cake";
	width: 100% !important;
	border: 2px dashed #292929 !important;
	display: block !important;
	text-align: center !important;
	margin: 15px auto 15px auto !important;
	padding: 13px !important;
	background: #080808 !important;
}
div#doc > div#bd > div.yui-g > div {
	display: none !important;
}
div#doc > div#bd > div.yui-g:hover > div {
	display: block !important;
}
body > form[name="post"] {
	width: 100% !important;
	border: 2px dashed #292929 !important;
	display: block !important;
	text-align: center !important;
	margin: 40px auto 15px auto !important;
	padding: 13px !important;
	background: #080808 !important;
}
form table#postForm {
	display: none !important;
	background: #080808 !important;
	padding: 12px !important;
	border-radius: 4px !important;
}
body > form[name="post"]:hover table#postForm {
	display: table !important;
	text-align: left !important;
}
div.board > hr {
	border: 0 !important;
	height: 30px !important;
}
input[type="submit"], input[type="button"], button {
	background: #080808 !important;
	color: #0083fe !important;
	border: 1px solid #232323 !important;
}
input[type="text"], input[type="password"], select, input[type="checkbox"], input[type="file"], .content h3 {
	background: #080808 !important;
	color: #4b5f72 !important;
	border: 1px solid #232323 !important;
}
html > body.yotsuba_new.ws > form > table#postForm.postForm.hideMobile > tbody > tr:nth-child(4) > td:nth-child(2) > * {
	background: #080808 !important;
	color: #4b5f72 !important;
	border: 1px solid #232323 !important;
}

h3 { margin: 0 !important; }
#doc {
	margin-top: 20px !important;
}
