css:
@-moz-document url("http://www.jeuxvideo.com/cgi-bin/jvforums/forums.cgi"), url-prefix("http://www.jeuxvideo.com/forums/"), url-prefix("http://www.jeuxvideo.com/messages-prives") {
body { background: white url(http://image.noelshack.com/fichiers/2013/01/1357340006-wall.png) fixed no-repeat !important; }
::selection { background-color: rgba(255,255,255,0.35) !important; }
#global h1 { visibility: hidden !important; }
#col1 h1 { visibility: visible !important; }
html, #new_header, #header, #page { background: none !important; }
#header #logo a, #menu_mc, #table_menu_mc { display: none !important; }
#header { z-index: 1 !important; }
#connexion { position: absolute !important; z-index: 2 !important; margin-top: 10px !important; margin-right: 46px !important; }
#connexion td { background: none !important; }
#connexion a { color: rgb(128,50,128) !important; }
#connexion a strong { color: rgb(128,50,128) !important; }
#recherche form { background: none !important; }
#select_skin { display: none !important; }
#connexion #compte a, #connexion #connect a {  background: none !important; }
#connexion { text-shadow: rgb(128,50,128) 0px 0 0px !important; }

.bloc_forum { padding-top: 10px !important; }
.bloc_forum { background-image: none !important; }
.msg img, .ignorer { opacity: 0.1 !important; }
.msg img:hover, .post img, .avatar tr img, .msg_body img, .ignorer:hover, .ancre a:hover { opacity: 1 !important; }
.bloc_forum h1, .titre_forum span, .bloc_forum h1.titre_forum { background: none !important; color: rgb(128,50,128) !important; }
.ancre a { opacity : 0.1 !important; }

td { color: rgb(128,50,128) !important; }
.topic_mod { font-weight: bold !important; color: rgb(128,50,128) !important; }
.tr1, .tr2 { opacity: 0.9 !important; }
.tr1, .tr2 { background: none !important;  }
.tr1 a, .tr2 a { color: rgb(128,50,128) !important; }
.tr1:hover, .tr2:hover { opacity: 1 !important; }

#recherche_forum #textfield_forum, .bloc_inner select { opacity: 0.2 !important; }
.bloc_inner #submit { opacity: 0 !important; }

#liste_topics th { display: none !important; }
#recherche_forum #textfield_forum:hover, .bloc_inner select:hover { opacity: 0.8 !important; }
#liste_topics th { background: none !important; color: rgb(128,50,128) !important; }
#liste_topics a.ltopic { background: none !important;  }
#liste_topics a, #liste_topics a:visited, #liste_topics a.ltopic strong { color: rgb(128,50,128) !important; }
#liste_topics { border: none !important;  }
#liste_topics td, #liste_topics element.style { text-align: center !important; }
#liste_topics td+td+td+td+td a { text-align: center !important; }

#menu2 { position: relative !important; margin-left: -30px !important; }
#menu2 { opacity: 0.02 !important; }
#menu2:hover { opacity: 0.8 !important; }
#menu2 a, #menu2 a:visited { color: rgb(128,50,128) !important; font-weight: normal !important; }
#menu2 a:hover { color: rgb(128,50,128) !important; font-weight: bold !important; }
#menu2 ul { background-image: none !important; background-color: transparent !important; }
#menu2 li, #menu2 li a, #menu2 div { background: none !important; }
#menu2 h3, #menu_boutique { display: none !important; }

.hp_forums dt, .hp_forums li { background: none !important; }
.new_bg #banner_jv { display: none !important; }

.bloc3:first-child, #fiche_infos2 { display: none !important; }
#col2 { margin-top: 0px !important; position: relative !important; margin-right: -30px !important; }
#col2 .bloc3 { opacity: 0.02 !important; }
#compte, #mp, #connect { opacity: 0.3 !important; }
#col2 .bloc3:hover, #compte:hover, #mp:hover, #connect:hover { opacity: 0.8 !important; }
#col2 a:visited { color: rgb(128,50,128) !important; }
#col2 a:hover { color: rgb(128,50,128) !important; font-weight: bold  !important; }
#col2 .titre_bloc {  background: none !important; }
#col2 span { color: rgb(128,50,128) !important; background: none !important; }
h3.titre_bloc { line-height: 25px !important;}
h3.titre_bloc span {  margin-left: 55px !important;  }
#col2 .bloc_inner {  background: none !important; }
.sujet span, .lien_base a { color: rgb(128,50,128) !important; }
.bloc3, .bloc_inner h1, .bloc_inner .sujet { background: none !important; }
#col2 li { background: none !important; margin-left: -6px !important; }
#recherche { opacity: 0.01 !important; }
#recherche:hover { opacity: 0.5 !important;  }
#rech_txt:hover, #rech_ou:hover, #rech_valid:hover { opacity: 0.5 !important; }

#newmessage, p.nouveau_sujet input, .code_confirm input { color: rgb(128,50,128) !important; border: 1px solid white !important; background: rgba(255,255,255,0.2) !important; }

.bloc_forum h3, h3.titre_bloc { background: none !important; color: rgb(128,50,128) !important; position: relative !important; }
.bloc_forum h3 span { display: none !important; }
.bloc_inner { border: none !important; color: rgb(128,50,128) !important; }

.login_memo li, .login_memo ul { background: none !important; }
.login_memo label, #post label, .message label { color: rgb(128,50,128) !important; }
.login_memo li a { color: rgb(128,50,128) !important; }

*[src="http://image.jeuxvideo.com/pics/forums/topic_dossier1.gif"] { 
width: 0 !important; height: 0 !important; padding-left:16px !important; padding-top: 13px !important;
background: url(http://image.noelshack.com/fichiers/2013/01/1357343125-dossier-jaune.png) no-repeat !important; } 

*[src="http://image.jeuxvideo.com/pics/forums/topic_dossier2.gif"] { 
width: 0 !important; height: 0 !important; padding-left:16px !important; padding-top: 13px !important;
background: url(http://image.noelshack.com/fichiers/2013/01/1357343128-dossier-rouge1.png) no-repeat !important; } 

*[src="http://image.jeuxvideo.com/pics/forums/topic_marque_on.gif"] { 
width: 0 !important; height: 0 !important; padding-left:16px !important; padding-top: 16px !important;
background: url(http://image.noelshack.com/fichiers/2013/01/1357340606-eping-verte.png) no-repeat !important; } 

*[src="http://image.jeuxvideo.com/pics/forums/topic_marque_off.gif"] { 
width: 0 !important; height: 0 !important; padding-left:16px !important; padding-top: 16px !important;
background: url(http://image.noelshack.com/fichiers/2013/01/1357340990-eping-rouge.png) no-repeat !important; }

*[src="http://image.jeuxvideo.com/pics/forums/topic_cadenas.gif"] { 
width: 0 !important; height: 0 !important; padding-left:16px !important; padding-top: 13px !important;
background: url(http://image.noelshack.com/fichiers/2013/01/1357343362-cadenas.png) no-repeat !important; } 


.navig_pages { background: none !important; }
.pagination a  { color: rgb(128,50,128) !important; }
.p_debut, .p_prec, .p_suiv, .p_fin { color: rgb(128,50,128) !important; }
.p_debut:hover, .p_prec:hover, .p_suiv:hover, .p_fin:hover { color: rgb(128,50,128) !important; }
.pagination strong { background-color: rgba(128,50,128,.5) !important; }
#dix_msg, .suite_sujet strong, #resultat_topics { color: rgb(128,50,128) !important; }
.suite_sujet strong a { color: rgb(128,50,128) !important; }
.msg .suite_sujet { background: none !important; border: none !important; }
.error, .form_error { display: none !important; }

#col1 { background: none !important; }
.msg { text-align: justify !important; background: rgba(255,255,255,0.1) !important; border: none !important; color: rgb(128,50,128) !important; }
.msg a { color: rgb(180,80,180) !important; }
.msg a:hover { color: rgb(100,40,100) !important; }
.post { background: none !important; }
.msg .pseudo, .date { color: rgb(128,50,128) !important; }
.date span { color: rgb(128,50,128) !important; }
.ancre a { color: rgba(128,50,128,0.1) !important; }
.ancre a:hover { color: rgb(128,50,128) !important; }
.btn { opacity: 0.2 !important; }
.btn:hover { opacity: 0.8 !important; }


#mp_onglets { background: none !important; }
.td2, .td2 a { color: rgb(128,50,128) !important; }
.td2 a:hover { color: rgb(128,50,128) !important; }
.td2 strong { color:  rgb(128,50,128) !important; background: none !important; }
.actif, #reception { color: pink !important; }
img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_supprimer.png"], img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_actualiser.png"] { opacity: 0.2 !important; }
img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_supprimer.png"]:hover, img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_actualiser.png"]:hover { opacity: 0.8 !important; }

#mp_onglets li a, #mp_onglets li.actif a {background: none !important; color: rgb(128,50,128) !important; }
#mp_onglets li.actif a:hover, #mp_onglets li a:hover { color: rgb(128,50,128) !important; }
#reception, #envoyes, #nouveau, #indesirables { background-image: none !important; background-color: rgba(128,50,128,0.1) !important; }

#pseudo, #sujet, #date, #supp { background-color: rgba(128,50,128,0.1) !important; color: rgb(128,50,128) !important; }
.exp, .sujet, .date, .c_sup, .dest { background: rgba(200,200,200,0.1) !important; color: rgb(128,50,128) !important; }
#liste_messages .tri_down span, #liste_messages .tri_up span { background-image: none !important; }

#rediger form { background: none !important; }
.ui-resizable-se { opacity: 0.2 !important; }

img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_retourliste.png"], img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_fermer.png"] { opacity: 0.2 !important; }
img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_retourliste.png"]:hover, img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_fermer.png"]:hover { opacity: 0.8 !important; }
#ajout_destinataire { opacity: 0.2 !important; }
#ajout_destinataire:hover { opacity: 0.8 !important; }
#titre_sujet { background: rgba(128,50,128,0.05) !important; }
#titre_sujet h3 { color: rgb(128,50,128) !important; }
#entre_pseudos a, #voir_debut a { color: rgb(128,50,128) !important; }
#entre_pseudos a:hover, #voir_debut a:hover, #voir_debut a span:hover { color: rgb(128,50,128) !important; }

#voir_debut a { background: rgba(255,255,255,0.05) !important; border: none !important; -webkit-border-radius: 2px !important; -webkit-box-shadow: 0px -10px 10px 5px rgba(235,235,235,0.6) inset !important; }


.msg_body { background: none !important; }
.msg_infos { color: rgb(128,50,128) !important; }
#rediger h3 { display: none !important; }
#rediger form { border: none !important; }
.lien_base a { color: rgb(128,50,128) !important; } 
.lien_base a:hover { color: rgb(128,50,128) !important; } 
img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_quitter.png"], img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_apercu.png"], input[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_repondre.png"],  input[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_envoyer.png"] { opacity: 0.2 !important; }
img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_apercu.png"]:hover, img[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_quitter.png"]:hover, input[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_repondre.png"]:hover,  input[src*="http://image.jeuxvideo.com/pics/messages_prives/bt_envoyer.png"]:hover { opacity: 0.8 !important; }
#nouveau.actif span { visibility: hidden !important; }
#new_msg h4 { color: rgb(128,50,128) !important; }
#new_msg h4 span { color: rgb(128,50,128) !important; }
#new_msg input { background: white !important; }
.fermer .generic { color: rgb(128,50,128) !important; }


.nouveau a img { opacity:0 !important; }
.nouveau { background: url(http://image.noelshack.com/fichiers/2012/17/1335535584-nouveausujet.png) no-repeat left !important; opacity: 0.4 !important; }
.revenir a img { opacity:0 !important; }
.revenir { background:url(http://image.noelshack.com/fichiers/2012/17/1335535625-revenirauxmessages.png) center no-repeat !important; opacity: 0.4 !important;  }
.liste a img { opacity:0 !important; }
.liste { background: url(http://image.noelshack.com/fichiers/2012/17/1335535649-listedesujets.png) no-repeat right !important; opacity: 0.4 !important;  }
.alertemail, .bt_repondre, .bt_rafraichir { opacity: 0 !important; }
.discu_boutons { background: url(http://image.noelshack.com/fichiers/2012/17/1335535684-discubouton.png) no-repeat !important; height : 20px !important; opacity: 0.4 !important;  }
#rafraichir a img { opacity: 0 !important; }
#rafraichir { background: url(http://image.noelshack.com/fichiers/2012/17/1335535707-refresh.png) no-repeat right !important; opacity: 0.3 !important;  }
#bouton_apercu, #bouton_post { opacity:0 !important; }
#boutons_repondre { background: url(http://image.noelshack.com/fichiers/2012/17/1335535725-aperuposter.png) no-repeat !important; height : 20px !important; opacity: 0.4 !important; }



#pub_carre1, #google_ads_frame1, #banner_data, #col2 ins, #sponsor_google, #banner, #eyeDiv, iframe[name*="google_ads"], #pub_carre1, #pub_carre1 iframe, #google_ads_frame1, #banner_data, .titre_page, #sponsor_google, #banner, #eyeDiv , #aus, #aubg, #ads, #load, #ads0, #taw0, .ad, .adc, .adt, .adb, .adu, .adus, #footer, #taw1, #taw2, #uaw2, #uaw0, #uaw1, #ainw, #smac, #smap, #sman, #abgi, #ft1, #ft2, .rss, .google468x60, #aswift_0_anchor, #banner_jv { display: none !important; }
}

@-moz-document url-prefix("http://www.jeuxvideo.com/messages-prives") {
#bouton_apercu, #bouton_post { opacity: 0.2 !important; }
#bouton_apercu:hover, #bouton_post:hover { opacity:0.8 !important; }
}
