css:
@namespace url(http://www.w3.org/1999/xhtml);

/*@-moz-document domain("news.bbc.co.uk")*/
@-moz-document url(http://news.bbc.co.uk/),
               url-prefix("http://news.bbc.co.uk/1"),
               url-prefix("http://news.bbc.co.uk/2")
{

/* Tweaks to Phil_X original
   as of update: 2-Jun-08 */

/* This Ollie tweak: 2009-Jul-23*/



/* Reduce font size globally */

body{ font-size: 65% !important; min-width:0px !important }


/*
 * BANNERS
 */

/* Accessibility banner off (and move explore button) */

#blq-mast{ display:block !important; height: 0px !important }
#blq-accesslinks{ display:none !important; left: 121px !important }
#blq-mast img   { display:block !important;
                    left: -10px !important;
                    top:  17px !important }

/* Explore button */
#blq-explore-link{ display:block !important; position: absolute !important; 
                   left: -10px !important; top: 70px !important }
#blq-explore-link a:hover{ opacity:.85 !important; }
#blq-explore-link a:active{ opacity:.65 !important; }
.blq-nav-orange,.blq-nav-sky{ width:120px !important;
                 background: none !important; opacity:.65 !important;
                 font-size: 11px !important; line-height: 1.1em !important; }
.blq-nav-orange{ background: #B7591C !important; }
#blq-nav-container{ opacity:1 !important; font-size: 11px !important; }


/* Reduce News banner */

.newsbanner{ background-position: 450px 0px !important; 
             width:100% !important;
             height: 60px !important; 
             margin-bottom:5px !important; }
.newsbanner div.o, div.banSpecialv4{ width:700px !important }
.newsbanner H1,.newsbanner .logo{ background-position: 102px 0px !important;
                                  width:250px !important;
                                  height: 60px !important} 
.newsbanner .o .miitb A{ line-height: 1.1em !important; 
                         font-size: 12px !important }


/* Reduce Services and Products banner */

.servicev4{ display:block !important;  /* 'none' to disable */
            padding-left: 0px !important; 
            padding-right: 0px !important;
            text-align: right !important;
            width:879px !important }
.servicev4 h4{ display:none !important }


/* Footer off */

#blq-footer{ display:none !important }




/*
 * TABLES
 */

/* Reduce main tables */

table[width="974"]{ width: 100% !important}
img[width="974"]  { width: 100% !important}
table[width="800"]{ width: 744px !important}
td[width="800"]   { width: 744px !important}
img[width="800"]  { width: 744px !important}
table.storycontent{ width: 732px !important}
img[width="786"]  { width: 716px !important}
table[width="786"]{ width: 716px !important}
td[width="786"]   { width: 716px !important}
.datetools{ width: 744px !important }
.ds{ width: 100% !important }
.mediaAssetWrapper{ width: 665px !important }


/* Lefthand menu (121px used to match legacy format) */

table[width="174"]{ width: 121px !important}
td[width="174"]   { width: 121px !important}
.lhs,.lhsl        { width: 121px !important}
.lhsl,.lhsdl{ border-bottom-width:0px !important }
.lhs a{ font-size:10px !important; 
        text-align: right !important; 
        padding-right:7px !important }


/* Righthand column */

table[width="320"]{ width:250px !important} /* sport */
td[width="306"]   { width:250px !important}
img[width="306"]  { width:250px !important}
td[width="334"]   { width:250px !important}
td.storyextra     { width:250px !important}
.dd250{ width: 220px !important }
.arr{ padding-bottom: 5px !important } /* Compress links */
.picheadline{ font-size: 1.2em !important }
/*.picheadline4{ font-size: 8.5px !important }*/
.nlp{ font-size: 9.5px !important; /*font-weight: bold !important*/ }



/*
 * MISC
 */

#toolbar-inner{ font-size: 9px !important}
.blq-rst{ border: 0px !important; width: 100% !important }


/* Reduce misc white space */

img[height="10"][width="1"][src*="o.gif"]{ height: 6px !important }
img[height="10"][src*="ffffff.gif"]{ height: 6px !important }


/* Ticker */

.ticko iframe{ width:99% !important }
.ticko{ width:100% !important }
a.tickl{ font-size:11px !important }

}
