css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.metafilter.com") {
    #posts .copy {
        padding-left: 30px;
    }
    #posts .copy blockquote {
        padding-left: 0 !important;
        margin-left: 0 !important;
        margin-top: 0;
        margin-bottom: 0;
    }
    #posts .copy br {
        display: none;
    }
    #posts .copy .smallcopy:last-of-type {
        padding-top: 4px;
        display: block;
    }
    #posts .posttitle.front {
        padding-bottom: 5px;
    }
    #posts .posttitle.front a {
        color: #8bd1f4 !important;
        font-size: 15px;
    }
}
