@-moz-document domain("boojour.eu") {
	body {
		background-color: black !important;
		background-image: none !important;
	}
	#container > div {
		width: 100% !important;

	}
	a#player{
		margin: auto !important;
		width:  1154px !important;
		height:  620px !important;

		/*
		default values
		aspect ratio (may vary ?): 1.86
		width: 860px !important;
		height: 462px !important;
		*/
			/*
			width: 1024px !important;;
			height: 550px !important;
			*/
			/*
			width: 1280px !important;
			height: 687px !important;
			*/
	}
	object#player_api{
		box-shadow: 0 0 3px 0 firebrick !important;
	}
	#container + div {
		color: hsla(240, 6%, 6%, 1.0) !important;
		text-shadow: 2px 2px 0px hsla(360, 3%, 3%, 1.0) !important;
	}
}
