css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("b.hatena.ne.jp") {
 #container,.box2, .box4, .box3,#branding,#navi-category {
  width:98% !important; 
  text-align:center;
 }
 #navi-page{
  padding:0 !important;
  width:98% !important;
  border-radius:6px;
 }
 #navi-page li,#navi-page li a{
  border-radius:6px;
 }
 #navi-category{
  padding:0 !important;
  border-radius:6px;
 } 
 #navi-category li, #navi-category li a{
  border-radius:6px;
 }
 .category{
  border-bottom-left-radius:6px;
  border-bottom-right-radius:6px;
 }
 .ad-head-text,.ad-unit, .recommended, #main > DIV:first-child > DIV:last-child > H2:nth-child(14){
  display:none !important;
 }
 body:not(.global-index) .box3 .entry-contents, .box2 .entry-contents, .box4 .entry-contents{
  width:70% !important; 
  float:left !important;
  border:none !important;
 }
 .box3_1 .entry-contents{
  margin:0 !important;
 }
 .box3_2 .entry-contents{
  width:80% !important;
  padding:4px !important;
  float:left;
 }
 body:not(.global-index) .box3 .entry-list, .box2 .entry-list, .box4 .entry-list{
  border-top:4px solid #ddd;
  min-height:0 !important;
  height:5em !important;
 }
 body:not(.global-index) .box3 .entry-unit, .box2 .entry-unit, .box4 .entry-unit{
  text-align:left;
  width:98% !important; 
  border-top:4px solid #ddd !important;
  min-height:80px;
 }
  body.video .entry-unit{
  min-height:110px;
 }
 body.video .hb-entry-link-container{
  float:left;
  padding:0 !important;
  margin:-5em 0 0 40px !important;
  width:80% !important;
 }
 ul,div{
  background-image:none !important;
 }
 body:not(.global-index) .box3 .users span, .box2 .users span, .box4 .users span {
  font-size:100% !important;
 }
 body:not(.global-index) .box3 .users, .box2 .users, .box4 .users{
  float:right !important;
  margin-top:-4px !important;
 }
 body:not(.global-index) .box3 .entry-data, .box4 .entry-data{
  display:block;
  position:absolute;
  left:74%;
  margin-top:-4.9em !important;
 }
 .box3_1 .entry-data{
  clear:none;
  position:absolute;
  right:4em;
  margin-top:2.8em !important;
 }
 body:not(.global-index) .box3 .thumbnail, .box4 .thumbnail{
  position:absolute !important;
  float:left !important;
  margin-right:12px !important;
 }
 body:not(.global-index) .box3 .thumbnail img, body:not(.global-index) .box3 .video, .box2 .thumbnail img, .box2 .video, .box4 .thumbnail img, .box4 .video{
  height:60px !important;
  max-width:90px !important;
  border-radius:6px;
  margin-top:-8em;
 }
 body.video .thumbnail img{
  height:90px !important;
  max-width:120px !important;
  border-radius:6px;
 }
  body:not(.global-index) .box3 .hb-entry-link-container, .box4 .hb-entry-link-container{
  display:inline;
  position:relative;
  left:100px;
 }
 .box3_1 .hb-entry-link-container{
  margin:0 !important;
  text-align:left;
 }
 .box3_1 .hb-entry-unit-with-favorites h3 a {
  font-size:80% !important;
 }
 .box3_2 .hb-entry-unit-with-favorites h3 a {
  font-size:80% !important;
 }
 body:not(.global-index) .box3 .hb-entry-link-container a,.box4 .hb-entry-link-container a{
  display:block;
  width:80% !important;
  height:1.2em !important;
  overflow:hidden !important;
 }
 .box3_1 .entry-meta{
  margin:.5em 0 0 0 !important;
 }
 body:not(.global-index) .box3 .entry-meta,.box4 .entry-meta{
  display:block;
  float:right;
  margin-top:-2em !important;
  width:25%;
 }
 body:not(.global-index) .box3 BLOCKQUOTE, .box4 BLOCKQUOTE{
  padding-left:100px !important;
  min-height:3em;
 }
 li.tag > a.tag{
  font-size:70% !important;
 }
 span.play{
  width:28px !important; 
  height:28px !important;
  margin:0 0 0 -14px !important;
  border-radius:14px;
 }
 .box3_1 {
  width:32% !important;
 }
 h2{
  border-radius:1em; 
 }
 .box3_2 {
  width:65% !important;
 }
 .entry-list-xl{
  text-align:left;
 }
 .entry-list-xl .domain{
  display:block;
  clear:both;
 }
 .entry-comment {
  padding:.5em !important;
  border-radius:1em !important;
 }
 .more-bookmark {
 }
 .entry-comment li {
  margin:2px !important;
 }
 .comment {
  padding:0 4px !important;
  font-size:80% !important;
  max-width:70% !important;
 }
 BLOCKQUOTE{
  clear:none;
 }
}
