css:
@-moz-document domain("todoist.com") {
html { min-height:100%; }
a.action.go_premium {display:none}
.note_icon img {visibility:hidden}
.note_icon:hover img {visibility:visible}

#logo a img { display:none; }
#logo {margin:0;}
#logo a {
    background:url('http://d3ptyyxy2at9ui.cloudfront.net/7e840d2e71e45472542a1588471ef45b.png') no-repeat 0 0;
    width:97px;
    height:33px;
    text-indent:-9999px;
    display:block;
    outline:0;
    margin: 28px 0 0 0px;
}
body {
background:url('https://wunderlist2.s3.amazonaws.com/f79974d07ebc76689f47697b2a7063fc5c3b48c7/images/backgrounds/01-2048.jpg') no-repeat center 0;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center top;
    padding:0 10px;
}
#content {
    min-width:880px;
}
.task_item .content, .task_item .menu, .task_item .project, .task_item .time {
    border-color: rgba(255,255,255,0.2) !important;
}
.task_item .content {
    padding-left:8px;
}
#editor .history_desc, #bottom {
    border-color: rgba(255,255,255,0.1) !important;
}
.items .text, .items .name { color:#fff; }
.task_item:hover .content, .task_item:hover .menu, .task_item:hover .project /*,
#project_list li:hover, .items .current*/ {
    background-color:rgba(255,255,255,0.2) !important;
    background-color:transparent !important;
}
#top_right, .top_right_on {
    background:none;
    border:none !important;
}





/* Items list backgrounds */
.checked, .history_item {
    opacity:0.5;
}
.task_item td.checker, .agenda_checker {
    /*min-width: 16px;*/
    padding:0;
}
.amicheckbox {
    padding:8px 9px 10px 10px;
    border-right: 1px dotted #c0c0c0;
    margin-top:0;
}
.amicheckbox img {
    border-radius: 3px;
}.task_item.history_item td {
   padding: 0;
}
.task_item.history_item td.content {
   padding: 9px 9px 7px 8px;
}
.items .text, .items .name { color:#202020; }
li.manager {
    margin-top:0;
}
#editor .items .manager {
    padding: 5px!important;
}
#editor .items li {
    margin-bottom:3px;
    border-radius:4px;
    box-shadow:0 2px 5px rgba(0,0,0,0.6);
    background: #f4f4f4;
    background: -moz-linear-gradient(top,  #f4f4f4 1%, #e8e8e8 66%, #d6d6d6 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#f4f4f4), color-stop(66%,#e8e8e8), color-stop(100%,#d6d6d6));
    background: -webkit-linear-gradient(top,  #f4f4f4 1%,#e8e8e8 66%,#d6d6d6 100%);
    background: -o-linear-gradient(top,  #f4f4f4 1%,#e8e8e8 66%,#d6d6d6 100%);
    background: -ms-linear-gradient(top,  #f4f4f4 1%,#e8e8e8 66%,#d6d6d6 100%);
    background: linear-gradient(to bottom,  #f4f4f4 1%,#e8e8e8 66%,#d6d6d6 100%);
}
#editor .items li.empty { 
    background:none !important;
    border:none !important;
    box-shadow:none;
    color:#ccc;
}
.task_item .content, .task_item .menu, .task_item .project, .task_item .time {
    border: none !important;
}
#pe_items_controller.actions a.action {
    text-shadow:0 1px 0 rgba(0,0,0,0.3);
    color:#90BEDB !important;
    margin-left:12px;
}
#pe_items_controller.actions a.action:before {
    content:'+';
    padding-right:7px;
}
#bottom {
    border:none;
    text-align:left;
}
#bottom a {
    opacity:0.3;
}






/* Project's list */
#list {
    background: #EBEBEB;
    border-radius: 4px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.6);
    margin-top: 38px;
}
#agenda {
    margin-bottom:0;
}
#agenda li {
    padding: 5px 5px 9px 7px;
    border-bottom: 1px dotted #c0c0c0;
}
#agenda_box .input_q {
    width: 189px;
    border-color: #CACACA;
}
#project_list li {
    padding: 7px 1px 7px 8px!important;
    border-bottom:1px dotted #c0c0c0;
}
#project_list .arrow {
    margin-left:-23px;
    margin-left:8px;
    margin-top:1px;
}
.cmp_arrow_closed:hover, .cmp_arrow_open:hover {
    background-color: transparent;
}
#project_list .drag_and_drop_handler {
    margin-left:-12px !important;
}
#project_list.items li.current {
    padding: 8px 1px 8px 8px!important;
    margin:-1px 0 0;
    border:none;
    color:#fff;
    background: #2a6aa3 !important;
background: -moz-linear-gradient(top,  #2a6aa3 0%, #639fd3 10%, #90bedb 94%, #6999bf 100%) !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#2a6aa3), color-stop(10%,#639fd3), color-stop(94%,#90bedb), color-stop(100%,#6999bf)) !important;
background: -webkit-linear-gradient(top,  #2a6aa3 0%,#639fd3 10%,#90bedb 94%,#6999bf 100%) !important;
background: -o-linear-gradient(top,  #2a6aa3 0%,#639fd3 10%,#90bedb 94%,#6999bf 100%) !important;
background: -ms-linear-gradient(top,  #2a6aa3 0%,#639fd3 10%,#90bedb 94%,#6999bf 100%) !important;
background: linear-gradient(to bottom,  #2a6aa3 0%,#639fd3 10%,#90bedb 94%,#6999bf 100%) !important;
}
.items .current .name {
    color:#fff;
}
.items .current .name .counter_count {
    color:#A2CAE0;
}
#project_man {
    padding: 8px 0 12px 6px;
}
#project_man a.action {
    color:#999!important;
    text-shadow:0 1px 0px rgba(255,255,255,0.7);
}
#project_man a.action:before {
    content:'+';
    padding-right:11px;
}







/* Left attached sidebar */
#logo a {
    margin:13px 0 0 14px;
}
#list {
    border-radius: 0px;
    box-shadow: 2px 2px 6px rgba(0, 0, 0, 0.6);
    margin-right:0;
}
body {
    padding:0;
    width:auto;
    margin:0;
}
#top {
    margin:0 0 1px;
    box-shadow:0 2px 6px rgba(0,0,0,0.6);
background: #eeeeee;
background: -moz-linear-gradient(top,  #eeeeee 0%, #bcbcbc 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#eeeeee), color-stop(100%,#bcbcbc));
background: -webkit-linear-gradient(top,  #eeeeee 0%,#bcbcbc 100%);
background: -o-linear-gradient(top,  #eeeeee 0%,#bcbcbc 100%);
background: -ms-linear-gradient(top,  #eeeeee 0%,#bcbcbc 100%);
background: linear-gradient(to bottom,  #eeeeee 0%,#bcbcbc 100%);
}

#content { margin:0; }
#list { margin-top:0; }
#editor { padding-top:16px; }
#bottom { /*width:880px;*/ padding-right:12px; }
}
