@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
	url-prefix("http://www.teamsoc.net/forums/")
{

/* Reset backgrounds */
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span, .forabg, .forumbg, .bg1, .bg2, ul.forums,
li.row:hover, #tabs a {
	background: none !important;
}

/* Background colors */
.forabg, .forumbg, div.headerbar, #search fieldset {
	background-color: #034 !important;
}
.headerbar {
	background: #222 !important;
}
#navigation a {
	background: #9aa !important;
}
#navigation a:hover, #navigation #active-subsection a {
	background: #333 !important;
}
html, body, select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited,
dl.codebox {
	background-color: #111 !important;
}
.navbar, .bg1, .bg2, ul.forums, .panel, .postbody, #cp-main .pm, div.cp-mini, #tabs a, div #content, fieldset {
	background-color: #222 !important;
}
tr {
	background-color: #222 !important;
}
li.row:hover {
	background-color: #111 !important;
}
blockquote {
	background-color: #333 !important;
}
.pagination span a:hover {
	background-color: #888 !important;
}
.pagination span strong {
	background-color: #0af !important;
}
.postlink:hover {
	background-color: #ddd !important;
}
#tabs .activetab a span, #tabs .activetab a, #tabs a:hover span, #tabs a:hover {
	background: #056 !important;
}
#tabs a span {
    background: #222 !important;
}


div.rules {
	background-color: #411 !important;
}

/*Special Admin Stuff*/
TR[height="20"][bgcolor="#EEB4B4"] {
    background-color:#311 !important;
}
TR[height="20"][bgcolor="#B4EDB4"] {
    background-color:#131 !important;
}
TR[height="20"][bgcolor="#FFFFFF"] {
    background-color: #111 !important;
}

/* Text colors */
#navigation #active-subsection a, html, body, .headerbar, h2, h3, .postbody, .postbody .content, .content p, #tabs .activetab a span, ul.topiclist li,
.panel li.header dd, .panel li.header dt, option {
	color: #fff !important;
}
.inputbox {
	color: #bbb !important;
}
a:hover {
	color: #ee1 !important;
}
a.forumtitle {
	color: #8ff !important;
}
.postprofile, dl.details dd {
	color: #aaa !important;
}
li.row strong, .postprofile strong, label, .panel, #tabs a span, dl.details dt {
	color: #999 !important;
}
select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited, th.name {
	color: #888 !important;
}
.postlink:hover, .pagination span a:hover, .pagination span strong {
	color: #333 !important;
}
dl.codebox code {
	color: #6a6 !important;
}
div.rules {
	color: #999 !important;
}
#navigation #active-subsection a:hover, #tabs a span:hover, a.forumtitle:hover {
    color:#ee1 !important;
}
.quotecontent, .row3 {
    color: #000 !important;
}
.uncited {
    color: #fff !important;
}

/* Border colors */
hr {
	border-color: #888 !important;
}
blockquote {
	border-color: #333 !important;
}
dl.codebox {
	border-color: #363 !important;
}
select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:hover, .pagination span strong {
    border-color: #444 !important;
}
h2, h3 {
	border-bottom-color: #222 !important;
}
ul.navlinks, #cp-main h3 {
	border-bottom-color: #333 !important;
}
dl.codebox dt {
	border-bottom-color: #363 !important;
}
#tabs .activetab a {
    border-bottom-color: #444 !important;
}
.postlink {
	border-bottom-color: #a96 !important;
}
li.row {
	border-top-color: #333 !important;
	border-bottom-color: #222 !important;
}
.signature, ul.cplist {
	border-top-color: #333 !important;
}
ul.topiclist dd, .postprofile {
	border-left-color: #333 !important;
}

/* Other */
.forabg, .forumbg {
	border-radius: 4px;
}
blockquote, dl.codebox {
	box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1) !important;
	margin-right: 4px !important;  /* So the shadow fits */
