css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://tieba.baidu.com/f?kw="),
url-prefix("http://tieba.baidu.com/f?ie="),
url-prefix("http://tieba.baidu.com/f/vote?kw="),
url-prefix("http://tieba.baidu.com/f/good?kw="),
url-prefix("http://tieba.baidu.com/f?tp=0&kw=") {

  #frs_list_pager {
    background: #CCC;
    display: inline-block !important;margin:0;padding:10px;border-radius: 15px 15px 0px 0px;;
    position: fixed;right:10%;bottom:-38px;z-index: 999;
    transition: ease 0.2s }
  
  #frs_list_pager:hover {bottom:0px;}

}

@-moz-document url-prefix("http://tieba.baidu.com/p"),
  url-prefix("https://tieba.baidu.com/p"),
url-prefix("http://tieba.baidu.com/f?c"),
url-prefix("http://tieba.baidu.com/f?z="),
url-prefix("http://tieba.baidu.com/f?kz=") {

  #thread_theme_5 {display: none !important;}
  
  #thread_theme_7 .l_thread_info {
    background: #CCC;
    display: inline-block !important;margin:0;padding:10px;border-radius: 10px 10px 0px 0px;;
    position: fixed;right:10%;bottom:-35px;z-index: 11999;
    transition: ease 0.2s }
  
  #thread_theme_7 .l_thread_info:hover {bottom:0px;}
}

@-moz-document url-prefix("http://tieba.baidu.com/f/search") {
  
  .pager.pager-search  {
    background: #CCC;
    display: inline-block !important;margin:0;padding:10px 10px 15px !important;border-radius: 10px 10px 0px 0px;;
    position: fixed;right:10%;bottom:-40px;z-index: 11999;
    transition: ease 0.2s }
  
  .pager.pager-search:hover {bottom:0px;}
  
  .s_nav_right.hasPage {margin-left:10px !important;font-size:9px!important;line-height:12px !important;height:12px !important;color:#555 !important;
  position:absolute;left:0;bottom:1px;}
  
  .cur {background:#fff !important;}
}
