css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('https://www\.playdom\.com/games/avengers/.+') {
    #main-header, #play_info, #footer {
        display: none !important;
    }
    #play_header {
        top:-90px !important;
    }
    #game_content {
        margin-top:-135px !important;
    }
    #play_content {
        margin-top:-110px !important;
    }    
    #headshot, #feed-icon, #play_feed_post_avatar {
        display: none !important;
    }    
    .banner{
        display: none !important;
    }
    #play_feed_list {
        overflow: scroll !important;
        height:650px !important;
    }    
    #play_priority_feed {
        margin-top:0px !important;
    }
    #play_team_info, #play_feed_post_container{
        display: none !important;
    }    
    .homeFeedSource_user, .avatarSmallHeadshotContainerWrapper {
        display: none !important;
    }    

    .play_feed_item_text {
        padding-left:3px !important;
        width:160px !important;
     }
    .play_feed_item_text a {
        display: none !important;
    }    
    .play_feed_item_talk {
        display: none !important;
    }      
}
