css:
@-moz-document domain("torrentz.eu") {
body {
  background: #000 !important;
  color: #ccc !important;
}
h2 {
  color: #fff !important;
  border-bottom: 0px !important;
  background: #333 !important;
}
dl a {
  color: #ccc !important;
}
div.top {
  background: #222 !important;
  border-radius: 0px !important;
  margin-left: 0px !important;
  border: 1px solid #666 !important;
}
div.top ul *  {
  background: #222 !important;
}
div.top ul li {
  border-left: 1px solid #666 !important;
}
div.top ul li a:hover {
  border-bottom: 0px !important;
  background: #555 !important;
  border-radius: 0px !important;
}
fieldset { padding: 10px 0px 10px 0px !important; }
form.search {
  margin: 0px !important;
  border-radius: 0px !important;
  background: #000 !important;
}
#thesearchbutton {
  background: #222 !important;
  border-radius: 0px !important;
  border: 1px solid #555 !important;
  color: #eee !important;
}
#thesearchbutton:hover { background: #333 !important; }
div.results {
  margin: 0px !important;
  border-radius: 0px !important;
}
#thesearchbox {
  background: #222 !important;
  color: #ddd !important;
  border: 1px solid #555 !important;
  border-radius: 0 !important;
}
span.v { background: #222 !important; }
div.results { background: #333 !important; }
div.results h3 { background: #222 !important; border: 0px !important;}
dl { 
  background: #000 !important;
  border-bottom: 1px dashed #333 !important;
}
dl:hover {
  background: #333 !important;
}
dl dd {
  background: transparent !important;
}
dl dt a:hover {
  background: transparent !important;
}
div.download {
  margin-top: 0px !important;
  background: #222 !important;
  border-radius: 0px !important;
}
div.download div {
  color: #aaa !important;
}
p {
  background: #222 !important;
  border-top: 0 !important;
  color: #aaa !important;
}
div.trackers {
  background: #333 !important;
}
div.files ul {
  background: #000 !important;
}
}
