css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
html {
	margin-top: 18px !important;
	position: relative !important;
}

#header {
	z-index: 100 !important;
}

#header:before {
	content: "" !important;
	border-bottom: 1px solid transparent !important;
	display: block !important;
	height: 18px !important;
}

/* B */
#sr-header-area {
	background: linear-gradient(#f0f0f0, #e7e7e7) red !important;
	border: none !important;
	border-bottom: 1px solid #bbb !important;
	box-shadow: 0 0 5px 2px rgba(0,0,0,.1) !important;
	height: 36px !important;
	
	font-size: 9px !important;
	left: 0 !important;
	margin: 0 !important;
	padding: 0 !important;
	position: fixed !important;
	top: 0 !important;
	width: 100% !important;
	z-index: 10000 !important;
}

#sr-header-area, #srList {
	font-family: verdana,arial,helvetica,sans-serif !important;
}

#sr-header-area, #sr-header-area * {
	line-height: 18px !important;
	font-family: verdana,arial,helvetica,sans-serif !important;
	text-shadow: none !important;
	text-decoration: none !important
}

#sr-header-area .width-clip,
#sr-header-area .sr-list {
	display: block !important;
	height: 100% !important;
}

#sr-header-area .flat-list {
	display: block !important;
	height: 100% !important;
	overflow: hidden !important;
	width: auto !important;
}

#sr-header-area .flat-list:first-child {
	float: left !important;
	width: auto !important;
}

#sr-header-area .flat-list:first-child li:nth-child(3) ~ li {
	display: none !important
}

#sr-header-area .flat-list:nth-child(3) li {
	display: inline-block !important;
	float: left !important;
}

#sr-header-area .flat-list:last-of-type {
	display: none !important;
}

.sr-bar:before, .sr-bar:after { display: none !important }

#srLeftContainer, #RESShortcutsViewport, #RESShortcuts  {
	height: 100% !important;
	margin: 0 !important;
	max-height: none !important;
	padding: 0 !important;
}

#srLeftContainer, #sr-header-area .flat-list:first-child {
	border-right: 1px dashed #aaa !important;
	padding: 0 !important;
}

.srdrop.dropdown {
	padding: 0 !important;
	position: absolute !important;
	text-align: center !important;
	top: 50% !important;
	width: 121px !important;
}

.srdrop.dropdown .title {
	margin: 0 !important;
	padding-left: 0 !important;
}

.srdrop.drop-choices {
	max-height: 90% !important;
	margin: 0 !important;
	overflow-y: scroll !important;
	position: fixed !important;
	top: 36px !important;
	width: auto !important;
}

#srDropdown, #RESStaticShortcuts {
	display: block !important;
	float: none !important;
}

#srList {
	position: fixed !important;
	top: 36px !important;
	z-index: 100 !important;
}

#srList, .srdrop.drop-choices {
	border-color: #bbb !important;
	box-shadow: 5px 5px 5px 0 rgba(64,64,64,.1) !important;
}

#srList thead tr {
	background: #eee !important;
	border-color: #ddd !important;
}

#srList thead td:hover {
	background: #e7e7e7 !important;
}

#srList thead td:active {
	background: #e4e4e4 !important;
}

#srList tbody tr, .srdrop.drop-choices .choice {
	border-bottom: 1px solid #eee !important;
	font-size: 10px !important;
	padding: 3px 8px !important;
	text-transform: none !important;
}

.srdrop.drop-choices .choice {
	width: auto !important;
}

.srdrop.drop-choices .bottom-option {
	display: none !important;
}

#srList tbody tr:hover, .srdrop.drop-choices .choice:hover {
	background: hsl(203, 70%, 95%) !important;
}

#srDropdown {
	left: 0 !important;
	position: absolute !important;
	text-align: center !important;
	top: 50% !important;
	width: 100% !important;
}

#srDropdown a {
	display: block !important;
}

#RESShortcutsViewport {
	margin-right: 25px !important;
}

#RESShortcuts {
	display: block !important;
	overflow-y: auto !important;
	white-space: normal !important;
	width: 100% !important;
	background: none !important;
}

#RESStaticShortcuts .separator:first-child, #sr-header-area .srSep,
#RESShortcutsRight, #RESShortcutsLeft, #sr-header-area .separator/*,
#RESSubredditGroupDropdown*/, #sr-more-link {
	display: none !important;
}
	
#RESSubredditGroupDropdown {
	border: 1px solid #bbb !important;
	box-shadow: 1px 1px 2px 1px rgba(0,0,0,.1);;
	position: fixed !important;
}
	
#RESSubredditGroupDropdown > .RESShortcutsEditButtons {
	border-color: #bbb !important;
}

#RESShortcutsEditContainer {
	background: rgba(0,0,0,0.1) !important;
	border-left: 1px solid rgba(128,128,128,.1) !important;
	border: none !important;
	height: 100% !important;
	max-height: none !important;
	opacity: 0.7 !important;
	width: 25px !important;
}

#RESShortcutsEditContainer:hover {
	opacity: 1 !important;
}

#RESShortcutsAdd, #RESShortcutsTrash {
	background: none !important;
	color: #eee !important;
	font-weight: bold !important;
	height: 100% !important;
	line-height: 150% !important;
	position: static !important;
	text-align: center !important;
	text-shadow:
		-1px -1px 0 #777,	0 -1px 0 #777,	1px -1px 0 #777,
		-1px  0px 0 #777,					1px  0px 0 #777,
		-1px  1px 0 #777,	0  1px 0 #777,	1px  1px 0 #777 !important;
	width: 100% !important;
}

#RESShortcutsTrash[style$="block;"] {
	background: #d0d0d0 !important;
	display: block !important;
	left: 0 !important;
	line-height: 200% !important;
	position: absolute !important;
	top: 0 !important;
}

#RESShortcutsSort {
	display: none !important
}

/* subbar links */
#RESShortcuts .subbarlink[href*="+"] {
	color: #444 !important;
	font-weight: 600 !important;
}

/*#RESShortcuts .subbarlink[href*="+"] + .separator + .subbarlink:not([href*="+"]),*/
#RESShortcuts .subbarlink:not([href*="+"]) + .separator + .subbarlink[href*="+"] {
	margin-left: 10px !important;
	position: relative !important;
}

/*#RESShortcuts .subbarlink[href*="+"] + .separator + .subbarlink:not([href*="+"]):before,*/
#RESShortcuts .subbarlink:not([href*="+"]) + .separator + .subbarlink[href*="+"]:before {
	color: #111 !important;
	content: " - " !important;
	left: -10px !important;
	pointer-events: none !important;
	position: absolute !important;
	text-align: center !important;
	width: 10px !important;
}

#sr-header-area .subbarlink, #srDropdown a,
#sr-header-area li, #sr-header-area .dropdown {
	background: none !important;
	color: #111 !important;
	font-weight: 400 !important;
	transition: background 300ms, color 200ms !important;
	text-transform: uppercase !important;
}

#sr-header-area li a {
	color: inherit !important;
}

#sr-header-area .subbarlink,
#sr-header-area li {
	display: inline-block !important;
	padding: 0 3px !important;
	z-index: 9999 !important;
	word-spacing: -0.5ex !important;
}

#sr-header-area li {
	padding: 0 !important;
}

#sr-header-area li a {
	display: block !important;
	padding: 0 3px !important;
	text-decoration: none !important;
}

#sr-header-area .subbarlink:hover, #srDropdown a:hover,
#sr-header-area li:hover {
	background: hsla(13, 0%, 50%,.2) !important;
	border: none !important;
	color: #000 !important;
}

#sr-header-area .RESShortcutsCurrentSub,
#sr-header-area li.selected {
	color: hsl(13, 100%, 50%) !important;
	font-weight: 400 !important;
}

#sr-header-area .RESShortcutsCurrentSub:hover,
#sr-header-area li.selected:hover {
	background: hsla(13, 100%, 50%,.5) !important;
	color: #eee !important;
}

#RESShortcutsAddFormContainer, #editShortcutDialog {
	background: #f0f0f0 !important;
	position: fixed !important;
	top: 39px !important;
}

#addSubreddit, #shortcut-save {
	background: linear-gradient(#C0C0C0, #BBBBBB) !important;
	border-color: #999 !important;
	padding: 3px 10px !important;
	text-shadow: 0 0 1px #777 !important;
}

#addSubreddit:hover, #shortcut-save:hover {
	background: linear-gradient(#C6C6C6, #C1C1C1) !important;
	border-color: #9F9F9F !important;
}

#RESAccountSwitcherIconOverlay, #RESMainGearOverlay, .RESDropdownList,
.drop-choices {
	margin-top: -18px !important;
}

#header > #header-bottom-right {
	margin-top: 0 !important
}
}
