@-moz-document domain("bungie.net"){

body,
.main,
.container,
.post,
.forumPost,
.header_post,
#topics li.cf {
    background-color: #101010 !important;
    color: #d0d0d0 !important;
}

.container_body,
.header_body {
    background-image: -moz-linear-gradient(left, #101010 8%, #2d2d2d 60%) !important;
    background-image: -webkit-linear-gradient(left, #101010 8%, #2d2d2d 60%) !important;
    background-image: linear-gradient(left, #101010 8%, #2d2d2d 60%) !important;
}

.column,
.side,
.left {
    background-image: -moz-linear-gradient(left, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
    background-image: -webkit-linear-gradient(left, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
    background-image: linear-gradient(left, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
}

#nav_groups a,
#nav_tags .link,
#nav_tags h4,
.post_body,
.forumPost article h1 a,
#searchedTag {
    color: #d0d0d0 !important;
}

#topics li.cf:hover,
blockquote,
div.gated,
.container_textbox input,
.container_textfield textarea,
.container_textbox,
.container_textfield,
.container_sort,
#text_search {
    background-color: #1f1f1f !important;
    color: #d0d0d0 !important;
}

div.main {
    border-left: 1px solid #2d2d2d !important;
}

.topics li {
    border-top: 1px solid #1f1f1f !important;
}

.container_sort .sort li.on {
    background-color: #0096DB !important;
}

.container_sort .sort {
    background: none !important;
    border-left: 2px solid #2d2d2d;
}

.post_title {
    color: #fcfcfc !important;
    text-shadow: 0 1px 0 #999 !important;
}

blockquote,
div.gated:hover {
    border-color: #2d2d2d #2d2d2d #2d2d2d #0096DB !important;
}

.topics .title {
    color: #d0d0d0 !important;
}

.extraInfo time,
.extraInfo .replyCount,
.extraInfo .rating {
    text-shadow: none !important;
}

.authorInfo a {
    color: #3579a8 !important;
}

}
