css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document  url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')  {

body {

    background: url('http://www.citymanga.com/files/images/one_piece/595/00.jpg') center center #000000 no-repeat fixed !important;

  }

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: url('http://www.citymanga.com/files/images/one_piece/595/00.jpg');

padding: 20px;

-webkit-border-radius: 15px;

-moz-border-radius: 15px;

border-radius: 15px;

}


{ color: #000 !important;}

#left_column #posts .notification .avatar {

          
                        

			opacity:0.85 !important;

}






/*BIG BACKGROUND begin - background for the entire screen*/

body {
background: url('http://www.citymanga.com/files/images/one_piece/595/00.jpg') center #fff  fixed !important;
 background-repeat: no-repeat;
 background-size: 100% 100%;
background-color: #Fff !important;
color:#000000!important;
 font-family: 'PermanentMarkerRegular';

}

h1,h2,h3,h4,h5,h6{color:#000 !important}

a, a:visited{color:#000 !important}



#posts LI[class*=" post"] {
background: #Fff!important;
border: 2px #CF0 !important;
opacity: 1!important;



}


.post.photo_post .photo, .post.photo_post .video_thumbnail {

 
border: 2px #000 !important;

}




.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}


#left_column #posts .post .post_avatar 

	{-moz-border-radius: 38px !important;  

	-webkit-border-radius: 60px !important;  

	width: 64px !important; height: 64px !important;  

	margin: 0px !important;} 

#left_column #posts .post .sub_avatar_inner 

	{-moz-border-radius: 38px !important;  

	-webkit-border-radius: 60px !important;  

	width: 32px !important; height: 32px !important;  

	margin: 0px !important;} 

}
