}

/* end of friends, groups, notifications, messages */

/* end of menu stuff */

/* forum post text size */
.forumPost article .post_body, .forumPost article .post_urlLinkOrImage {
  font-size: /*[[forumfontsize]]*/ !important;
}

.forumPost blockquote {
  font-size: /*[[forumfontsize]]*/ !important;
}

.forumPost .spoiler {
  font-size: /*[[forumfontsize]]*/ !important;
}
/* end of forum post text size */

