css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ru-store.4game.com") {

div.type {
    background: -moz-linear-gradient(center top , #999, #555) repeat scroll 0 0 #999 !important;
    background: linear-gradient(to left top, #fff,  #999) !important;
    background: linear-gradient(to right top, rgb(244,244,244),  #fff) !important;
    box-shadow: 0px 1px 1px rgb(255,255,255) inset !important;border-color: rgb(255,255,255) !important;
    box-shadow: 0px 1px 1px rgba(255,255,255) inset !important;border-color: #000 !important;
}

div.type a {background-color:rgba(0,0,0,0.05) !important; font-size: 9px !important; color: rgb(0,0,0) !important; font-style:oblique !important;font-weight:bolder}
div.hoverit {box-shadow: 0px 2px 2px #000 inset !important;border-color: #000 !important}
ins.mask  {box-shadow: 0px 1px 1px #000 inset !important;border-color: #000 !important}
span.value u {font-size: 9px !important; color: rgb(255,255,255) !important; font-style:oblique !important;font-weight:bolder !important}

span.value {
    
    /*background: -moz-linear-gradient(center top , #333, #000) repeat scroll 0 0 #999 !important;
    background: linear-gradient(to left bottom, #333,  #000) !important;*/
    background: linear-gradient(to right top, #333 ,rgba(222,222,222,0.1)) !important;
    /* box-shadow: 0px 0px 0px rgb(255,255,255) inset !important;border-color: rgb(0,0,0) !important;*/ }
    
    

a.bonuse, span.option
 {background-color:rgba(0,0,0,0.05) !important; font-size: 9px !important; color: rgba(212,255,255,0.9) !important; font-style:oblique !important;font-weight:bolder}

#header.main-page .decoration {background-image: none !important;}

#content .category {display:none !important;}


#content {background:#000 !important;}


#content .items li div.icon a.title {color: rgb(255, 255, 255) !important; }
#content .items li div.description {color: rgb(255, 255, 255) !important; }

strong,#navigation  b {color: rgb(255, 255, 255) !important; }
#outer #content .items div.prise p.value span.rub {color: rgb(255, 255, 255) !important;}

#outer-content {background:#000 !important;}
#outer {background:#000 !important;}

#footer {background:#000 !important;}
#services  {background:#333 !important;}

#content h2 {
    color: rgb(255, 255, 255) !important; /* Цвет текста */
    background: #333!important; /* Цвет фона */
    padding: 5px!important; /* Поля вокруг текста */
    margin-bottom: 6px!important; /* Отступ слева */}
    
span.option {
    color: rgb(255, 255, 255) !important; /* Цвет текста */
    background: #333!important; /* Цвет фона */
    padding: 1px!important; /* Поля вокруг текста */
    /*margin-bottom: 5px!important; /* Отступ слева */}
    
div.hoverit     {
border-radius:  2px  !important;
box-shadow:
    0 0px 2px rgba(255, 255, 255, 0.4) inset,
   
    2px 2px 2px rgba(0, 0, 0, 0.3) !important;  }
    
    .items div.prise p.value {
    color: rgb(255, 255, 255)  !important; font-size: 105% !important;
    font-weight: 900 !important;
    text-shadow: 1px 1px 2px rgb(0,0,0), 0 0 4em #111 !important;
    opacity: 1 !important;
    }
    div.prise p.value b {
    color: rgb(255, 255, 255)  !important; font-size: 115% !important;
    font-weight: 900 !important;
    text-shadow: 1px 1px 2px rgb(0,0,0), 0 0 4em #111 !important;
    opacity: 1 !important;
    }
    
    #outer .items_mode h3 {
    color: rgb(255, 255, 255)  !important; font-size: 135% !important;
    font-weight: 900 !important;
    text-shadow: 1px 1px 2px rgb(0,0,0), 0 0 4em #111 !important;
    opacity: 1 !important;
    }
    
    #outer .items_mode table th {
    color: rgb(255, 255, 255)  !important; font-size: 135% !important;
    font-weight: 900 !important;
    text-shadow: 1px 1px 2px rgb(0,0,0), 0 0 4em #111 !important;
    opacity: 1 !important;
    }
    
    #outer .items_mode table td {
    color: rgb(255, 255, 255)  !important; font-size: 145% !important;
    font-weight: 900 !important;
    }
    
    #outer .items_mode h2 {
    background-color: #000 !important;
    }
    
    div.decoration {
    background: none !important;
    background-color: #000 !important;
    }
    
}
