@-moz-document domain("bungie.net"){

body,
.main,
.container,
.post,
.forumPost,
.header_post,
#topics li.cf
.Nav_Top .panel,
.Nav_Top .section,
.Nav_Top .panel .link {
    background-color: #101010 !important;
    color: #d0d0d0 !important;
}

/* Thread headers */
.container_body,
.header_body {
    background-image: -moz-linear-gradient(left, #101010 8%, #1f1f1f 60%) !important;
    background-image: -webkit-linear-gradient(left, #101010 8%, #1f1f1f 60%) !important;
    background-image: linear-gradient(left, #101010 8%, #1f1f1f 60%) !important;
}

/* Sidebar */
.column,
.side,
.left {
    background-image: -moz-linear-gradient(left, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
    background-image: -webkit-linear-gradient(left, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
    background-image: linear-gradient(left, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
}

/* Headers */
h5 {
    background-image: -moz-linear-gradient(bottom, rgba(255,255,255,0.0.5) 18%, transparent 60%) !important;
    background-image: -webkit-linear-gradient(bottom, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
    background-image: linear-gradient(bottom, rgba(255,255,255,0.05) 18%, transparent 60%) !important;
    background-color: #1f1f1f !important;
    border-bottom: 0px !important;
}

/* Text, headers, some links */
#nav_groups a,
#nav_tags .link,
#nav_tags h4,
#nav_mycommunity a,
.left li a,
.post_body,
.forumPost article h1 a,
#searchedTag,
.header,
.container_section a,
.content,
.xbox .online {
    color: #d0d0d0 !important;
}

.xbox {
    color: #666;
}

/* Links */
.notifications .content a {
    color: #5787B5 !important;
}

#topics li.cf:hover,
blockquote,
div.gated,
.container_textbox input,
.container_textfield textarea,
.container_textbox,
.container_textfield,
.container_sort,
#text_search,
.dualColList {
    background-color: #1f1f1f !important;
    color: #d0d0d0 !important;
}

div.main {
    border-left: 1px solid #2d2d2d !important;
}

.topics li {
    border-top: 1px solid #1f1f1f !important;
}

.container_sort .sort li.on {
    background-color: #0096DB !important;
}

.Nav_Top .panel .link {
    border-color: #2d2d2d !important;
}

.Nav_Top .panel .link {
    box-shadow: 0 0 0 1px rgba(16, 16, 16, 0.98) inset !important;
}

/* Thread sorting bar */
.container_sort {
    box-shadow: 0px 1px 0 #0096DB inset !important;
    border-bottom: 1px solid #2d2d2d !important;
}

.container_sort .sort {
    background: none !important;
    border-right: 1px solid rgba(255,255,255,0.03);
    border-left: 1px solid rgba(0,0,0,0.1);
}

/* Thread posts */
.forumPost .post_info,
.forumPost .post {
    border-top: 1px dotted rgba(255,255,255,0.03) !important;
    border-bottom: 0px !important;
}

.post_title {
    color: #fcfcfc !important;
    text-shadow: 0 0 2px white !important;
}

/* Threads */
#topics li.cf {
    border-top: 1px solid rgba(255,255,255,0.03);
    border-bottom: 1px solid rgba(0,0,0,0.1);
}

blockquote,
div.gated:hover,
.spoiler {
    border-color: #2d2d2d #2d2d2d #2d2d2d #0096DB !important;
}

.topics .title {
    color: #d0d0d0 !important;
}

.extraInfo time,
.extraInfo .replyCount,
.extraInfo .rating {
    text-shadow: none !important;
}

.authorInfo a {
    color: #3579a8 !important;
}

.spinner {
    background-color: transparent !important;
}

/* Social network share button */
.addthis_button img {
    display: none;
}

.addthis_button:hover img {
    display: inline;
    max-width: 0px;
    opacity: 0;
}

.addthis_button:after {
    content: "  share";
    font-size: 11px;
    line-height: 20px;
    padding-left: 3px;
    float: right;
}

/* Permalink */
.btn_permalink {
    opacity: 0.5;
}

.btn_permalink:hover {
    opacity: 0.9;
}

/* News pages */
.newsFeed li {
    background-color: transparent !important;
}

#NewsFeed h1 a {
    color: #d0d0d0 !important;
}

/* Spoilers */
.forumPost .spoiler {
    background-color: #1f1f1f !important;
    color: transparent !important;
    border-width: 1px 1px 1px 3px !important;
}

.forumPost .spoiler.opened {
    color: #d0d0d0 !important;
    border-color: #2d2d2d #2d2d2d #2d2d2d #2d2d2d !important;
}

.forumPost .spoiler:after {
    color: #d0d0d0 !important;
    background-color: #1f1f1f !important;
}

/* User info popup */
.extraInfo_user,
.extraInfo_user .btns {
    background-color: #101010 !important;
    box-shadow: 0 0 20px 1px rgba(255, 255, 255, 0.05), 0 0 2px 0 rgba(255, 255, 255, 0.1) inset !important;
    border-color: #2d2d2d !important;
}

.extraInfo_user .btns {
    box-shadow: 0 0 8px rgba(255,255,255,0.1) !important;
}

.extraInfo_user .btns a {
    color: #DDD !important;
    text-shadow: 0 0 3px black !important;
}

.extraInfo_user .content p {
    text-shadow: 0 0 1px black !important;
}

.extraInfo_user .content h2 a {
    color: white !important;
}

}
