css:
@-moz-document domain("thepiratebay.org"), domain("thepiratebay.se"), domain("thepiratebay.gl"), domain("thepiratebay.sx"), domain("thepiratebay.ac"), domain("thepiratebay.pe") {
#browseContainer,
input[type="text"],
body {
  font-size: 13px !important;
  color: #555 !important;
  background: #fafafa !important;
  padding: 0;
  margin: 0;
}

a[title="Download this torrent"] {
  font: bold 16px verdana !important;
}

#content {
  margin: 0 auto !important;
}

#detailsouterframe,
#categoriesTable {
  width: 100% !important;
}

h2 {
  background: #666!important;
  font-size: 12px !important;
  padding: 5px 15px !important;
  overflow: hidden!important;
}

h2,
#searchResult tr th,
#searchResult tr th a,
#detailsouterframe #detailsframe #title {
  color: white !important;
}

h2,
#q .inputbox {
  padding: 5px !important;
}

h2,
table#searchResult a,
table#searchResult td,
table#searchResult th,
abbr {
  border: none !important;
}

h2 span,
#browseContainer dd,
#browseContainer dt {
  font-size: 13px !important;
}

h2 a {
  color: white!important;
}

h2 a:hover {
  text-decoration: underline!important;
}

a,
select#category {
  color: #666 !important;
}

td[width="150"],
td[class="smalltext"],
#details .col2 br,
#details .col1 br {
  display: none;
}

input[type="text"] {
  -moz-border-radius: 3px !important;
  height: 20px !important;
  font-size: 13px;
}

table#searchResult {
  margin-bottom: 20px!important;
}

#searchResult tr td:first-child,
#searchResult th:first-child {
  padding-left: 15px !important;
}

#searchResult tr td:last-child,
#searchResult th:last-child {
  padding-right: 15px !important;
}

table#searchResult tbody tr,
#details .nfo {
  background: #fafafa!important;
}

table#searchResult tbody tr:hover {
  -moz-background-clip: border;
  -moz-background-inline-policy: continuous;
  -moz-background-origin: padding;
  background: #eaeaea none repeat scroll 0 0!important;
}

#SearchResults,
.detDesc {
  font-size: 12px;
}

table#searchResult td,
table#searchResult th,
#foot,
.detName .detLink,
.vertTh a,
#details div.download a,
p.byline,
#comments .comment {
  font-size: 13px!important;
}

#searchResult tr th,
#searchResult tr td,
#details dt,
#details dd {
  border-bottom: 1px solid #ddd!important;
}

#searchResult tr th,
#searchResult tr td {
  padding: 6px 5px !important;
  line-height: 20px!important;
}

#searchResult tr th {
  background: #888 !important;
}

tbody,
#content,
#main-content {
  position: static !important;
}

#content {
  margin-top: 0 !important;
}

#q input.submitbutton {
  margin-top: 12px !important;
  margin-left: 170px !important;
}

input[type="submit"] {
  padding: 3px 5px !important;
  position: relative;
  top: -3px;
  left: -20px;
}

#foot {
  margin-top: 50px !important;
  line-height: 22px!important;
  margin-bottom: 97px !important;
}

#q .inputbox {
  vertical-align: top !important;
  margin-left: 0 !important;
}

abbr {
  display: block !important;
  text-align: right !important;
}

a[title="Order by Uploaded"] + a[title="Order by Size"] {
  display: inline!important;
}

a[title="Order by Uploaded"] + a[title="Order by Size"],
th a[title="Order by Size"],
#details dt {
  text-align: right!important;
}

th a[title="Order by Size"] {
  display: block!important;
}

.vertTh center,
#details dd {
  text-align: left!important;
}

.detDesc {
  color: #888 !important;
}

.searchBox {
  
}

select#category {
  font-size: 13px !important;
  margin-left: 10px !important;
  top: -31px!important;
  left: -109px;
  height: 26px!important;
}

select#category,
#details .torpicture {
  position: relative!important;
}

#detailsouterframe #detailsframe #title {
  font-size: 20px !important;
  background: #888 !important;
  padding: 10px 15px !important;
  letter-spacing: 0 !important;
}

#details {
  background: #f5f5f5 !important;
  padding: 0 15px 15px 15px!important;
}

#details div.download,
#details div#rssAdd1 {
  background: #ececec;
  border: 1px solid #dadada!important;
  padding: 10px;
  -moz-border-radius: 6px!important;
}

#details div#rssAdd1 {
  position: absolute;
  top: -1px !important;
  left: 350px;
  width: 260px;
  text-align: center;
}

#details div#rssAdd:hover,
#details div.download:hover {
  background: #ddd;
}

#details div.download a {
  background: 0 3px!important;
}

#details .col1 {
  margin-top: 25px!important;
}

#details .col2,
#details .col1 {
  line-height: 23px !important;
  width: 60%!important;
}

#details .col2 {
  margin-bottom: 30px!important;
}

#details .col1,
#details .col2 {
  clear: both!important;
}

#details dt {
  width: 170px!important;
}

#details .torpicture {
  top: -20px;
  float: right!important;
}

#details .torpicture,
#details dl,
#details .nfo,
p.byline,
#comments .comment {
  margin: 0!important;
}

#details .torpicture img {
  width: 200px;
}

#details .nfo {
  width: 100%!important;
  margin-top: 20px !important;
}

#details .nfo,
#comments .comment,
#comments .comment br {
  padding: 0!important;
}

#details .nfo,
div.browse-coms strong,
div.browse-coms a,
#search1Result tbody tr:last-child td a,
div#main-content + div[align="center"] a {
  border: 1px solid #ddd !important;
}

#details .nfo pre {
  font-family: Consolas, "Courier New" !important;
  padding: 15px !important;
  line-height: 15px !important;
}

#commentsheader h4 {
  background: #ddd !important;
  color: #666 !important;
}

#commentsheader h4,
#comments div {
  padding: 10px 15px!important;
}

#comments div {
  background: white!important;
  border: 1px solid #eee!important;
  margin: 10px 0 !important;
}

#comments .comment a {
  color: #2fa7bf!important;
  border-bottom: 1px dashed #2fa7bf!important;
}

#comments .comment a:hover {
  color: #237d8f!important;
  border-bottom: 1px dashed #237d8f!important;
}

p.byline {
  margin-bottom: 5px!important;
}

#comments .comment {
  border: none!important;
  color: #999!important;
  font-style: italic!important;
  line-height: 19px!important;
}

#comments .comment br {
  display: block;
  margin: 3px!important;
  height: 0!important;
}

#sear1chResult tbody tr:last-child td a:hover,
div.browse-coms strong,
div.browse-coms a:hover,
div#main-content + div[align="center"] a:hover {
  background: #eee;
}

#searchRe1sult tbody tr:last-child td {
  border-bottom: none!important;
  padding-top: 20px!important;
}

div.browse-coms strong,
div.browse-coms a,
#search1Result tbody tr:last-child td a,
div#main-content + div[align="center"] a {
  display: inline-block!important;
  padding: 2px!important;
  width: 12px!important;
  font-size: 8px;
  height: 18px;
  text-decoration: none!important;
}

table#searchResult a[title *= '720p'] {
  color: hsl(0, 45%, 50%) !important;
}

table#searchResult a[title *= '1080p'],
table#searchResult a[title *= 'Stitch'],
table#searchResult a[title *= 'Crochet']
{
  color: hsl(0, 0%, 75%) !important;
}

table#searchResult a:visited {
  color: hsl(0, 0%, 90%) !important;
}

/* restore color for other cells */
table#searchResult td.vertTh a:visited {
  color: #666 !important;
}

td:nth-child(5) {
  text-align: left;
}

#header {
  background-color: #CCC;
  position: fixed;
  bottom: 0;
  padding-top: 1em;
  z-index: 1;
  width: 100%;
}

#header form {
  width: 1200px;
  margin: 0 auto;
}

#q .inputbox {
  width: 347px;
}

#detailsframe {
  margin: 0;
}

#main-content {
  padding: 0;
  border: 1px solid gray;
  width: 1200px;
  margin: 0 auto !important;
}



iframe,
h2,
.nohover,
.download div,
.download a[title='Anonymous Download'],
#main-content > iframe,
#CommentDiv ~ br,
div[style="position:relative;"] > div[style="clear:both;"]
{
  display: none !important;
}

#details > div.download,
#details > div.nfo
{
  display: block !important;
}

#social ~ div[style="position:relative;"]
{
  display: block !important;
  position: fixed !important;
  top: 0;
  left: 0;
}
}
