css:
@-moz-document url-prefix("http://fr.openclassrooms.com/forum/sujet/") {
blockquote {
   background: lightgray !important;
}
section#mainContent div.comment div.message {
   max-width: none !important;
}
.row {
    margin: 0 !important;
    padding: 0 !important;
}
.row .author, .actions {
    display: block !important;
}
.avatar > a {
    display: inline-block !important;
    width: auto !important;
}
.avatar img {
    padding: 0 15px 15px 15px !important;
}
section#mainSection .skills .skill { 
    width: auto !important;
}
.content {
    margin: 0 !important;
}
section#mainContent #mainSection ul.actions a,
a.date {
    display: block !important;
    opacity: 1 !important;
    color: black !important;
}
section#mainContent .author, 
section#mainContent div.comment a.date {
   border-bottom: 1px solid #e2e2e2 !important;
   font-size: small !important;
   font-family: TrebuchetMS, 'Trebuchet MS', Verdana, Tahoma, sans-serif !important;
   padding: 0 3px 3px 15px !important;
}
section#mainContent div.comment a.date {
   text-transform: lowercase !important;
}
section#mainContent div.comment a.date:before {
   content: "# Posté " !important;
   text-transform: capitalize !important;
}
section.comments {
   -moz-box-sizing: border-box !important;
   box-sizing: border-box !important;
   display: table !important;
   border-collapse: collapse !important;
   word-wrap: break-word !important;  
}
.row {
    border-radius: 0 !important;
   -moz-box-sizing: border-box !important;
    box-sizing: border-box !important;
    display: table-row !important;    
}

section#mainContent section.comments {
    width: 100% !important;
}
section#mainContent section.comments div.row div.userInfos,
#mainSection section.comments div.span11,
section#mainContent section.comments #contentPubThread,
section#mainContent section.comments div.row div.forumAd {
  -moz-box-sizing: border-box !important;
  box-sizing: border-box !important;
  display: table-cell !important;
  margin: 0 !important;
  padding: 0 !important;
  width: auto !important;
  float: none !important;
  border: 1px solid #e2e2e2 !important;
}
  
.content .message {
   -moz-box-sizing: border-box !important;
   box-sizing: border-box !important;
   padding: 0 10px !important; 
}
.span12 {
   display: none !important;
}
div.span1 {
   width: auto !important;
}
.content > br {
   display: none !important;
}
div.content > span.label {
   display: block !important;
   float:left !important;
   margin: 0 5px !important;
}
.noVote {
   visibility: visible !important;
}
.commentVote, .comments .addthis_buttons {
   height: 23px !important;
   padding: 5px !important;
   position: relative !important;
   display: inline-block !important;
   margin: 5px !important;
}

.comments .row {
   background: white !important;
}
.comments .date, .comments .author {
   background-color: #EEE5D0 !important;
}
.row.topVotes {
   background-color: #E5F0CB !important;
}

section#mainContent h1 {
   padding-bottom: 0 !important;
}
section#mainContent h1 + h2 {
   padding: 0 !important;
   margin: -10px 0 0 0 !important;
}
#mainContent #mainSection div.syntaxhighlighter {
   width: 60em !important;
   overflow: auto !important;
}
section#mainContent div.comment .signature {
   white-space: initial !important; 
   max-height: initial !important;
}
.paginationContent {
   padding: 0 !important;
   margin: 0 !important;
   height: auto !important;
    }
}

@-moz-document url("http://fr.openclassrooms.com/forum/"), url-prefix("http://fr.openclassrooms.com/forum/categorie/") {
.row {
  font-family: Verdana, Arial, sans-serif, Helvetica !important;
  font-size: small !important;
  padding: 0 !important;
}
section#mainContent #mainSection h1 a {
   font-size: x-large !important;
   font-weight: 100 !important;   
}
section#mainContent #mainSection h1 {
 padding: 0 0 8px 0 !important;
}
section#mainContent ul.list {
   margin: 0 !important;
}
section#mainContent ul.list li {   
   margin-bottom: 0 !important;
}
#mainContent #mainSection .list {
   display: table !important;   
}
.paginationContent {
   margin: 5px 0 !important;
}

section#mainContent ul.list li span.cellsLink a, 
section#mainContent ul.list li div.cellsLink a {
   padding: 0 !important;
}
section#mainContent ul.list li {
   display: table-row-group !important;
}
section#mainContent ul.list li div.row {
   display: table-row !important;
}
.span1, .span2, .span3, .span5 {
   display: table-cell !important;
   white-space: nowrap !important;   
   line-height: initial !important;      
}



.span5 {
   width: auto !important;
   position: relative !important;
}
.span1, .span2, .span3, .span5 {
  border-left: 1px solid black !important;
  border-top: 1px solid black !important;      
}
.span3 {
  border-right: 1px solid black !important;
}
.list li:last-child .span1,
.list li:last-child .span2, 
.list li:last-child .span3,
.list li:last-child .span5 {  
  border-bottom: 1px solid black !important; 
}
#mainContent #mainSection .row > .span5 h3 {
   font-size: small !important;
   font-family: verdana !important;
   font-weight: normal !important;
}
section#mainContent #mainSection ul.list li.lock span.lockSecure {
   background-position: -86px -706px;
   top: 3px !important;
   left: 10px !important;
   width: 25px !important;
   height: 27px !important;
   position: absolute !important;    
   display: block !important;
   float: left !important;
}
#mainContent #mainSection .span5 a {
   display: table !important;
   width: 100% !important;
}
#mainContent #mainSection .span5 h3,
#mainContent #mainSection .span5 span
{
   display: table-cell !important;
}

#mainContent #mainSection .row > .span5 a span {
   font-size: small !important;
   font-weight: normal !important;   
   
   float: right !important;
   padding-right: 5px !important;
   margin-top: 0 !important;
}
#mainContent #mainSection .row .cellsCentred {
  padding: 0 5px !important;
}
#mainContent #mainSection .row a.lastComment,
#mainContent #mainSection .row a.lastComment strong {
   font-size: x-small !important;
   padding: 0 !important;
   text-align: center;
   line-height: initial !important;
}

section#mainContent ul.list li span.cellsLink *, section#mainContent ul.list li div.cellsLink * {
   font-size: small !important; 
}
section#mainContent ul.list li span.cellsLink * h3 + span, section#mainContent ul.list li div.cellsLink * h3 + span {
   margin-top: 0 !important;
}
section#mainContent #mainSection ul.list li div span.postit, 
section#mainContent ul.list li div span.resolved {
   top: auto !important;
   position: absolute !important;
   bottom: 0 !important;
}
section#mainContent #mainSection ul.list li div span.postit {
   background-position: -46px -703px;
}
#mainContent #mainSection .notRead .span5 a h3 {
  font-weight: bold !important;
}
#mainContent #mainSection .lock .span5 a h3 {
  padding-left: 30px !important;
}
section#mainContent ul.list li:hover {
  background-color: inherit !important;
}
section#mainContent ul.list li.notRead div.cellsLink *,
section#mainContent ul.list li:hover .lastComment,
section#mainContent #mainSection ul.list li .lastComment,
section#mainContent ul.list li:hover .lastComment strong,
section#mainContent ul.list li .lastComment strong,
section#mainContent ul.list li:hover div.cellsCentred,
section#mainContent ul.list li div.cellsCentred,
section#mainContent ul.list li:hover div h3 + span,
section#mainContent ul.list li div h3 + span,
section#mainContent ul.list li:hover div h3 + span strong, 
section#mainContent ul.list li div h3 + span strong {
  color: black !important;
  opacity: 1 !important;  
}


section#mainContent h2 {
  padding-bottom: 10px;
}
section#mainContent ul.list li.notRead div.cellsLink * h3,
section#mainContent ul.list li.read span.cellsLink * h3, 
section#mainContent ul.list li.lock span.cellsLink * h3, 
section#mainContent ul.list li.read div.cellsLink * h3, 
section#mainContent ul.list li.lock div.cellsLink * h3 {
   width: auto !important;
   vertical-align: middle !important;
}
section#mainContent .groupCategoryForum * {
   font-size: small !important;
   white-space: initial !important;
}
section#mainContent .categoryForum .imgCategoryForum img {
  width: 30px !important;
  margin: 5px 5px 0 0 !important;
}
section#mainContent .categoryForum .imgCategoryForum {
    height: 30px !important;
    padding: 0 !important;
}
section#mainContent .categoryForum .rightContentCategoryForum {
    min-height: initial !important;
}
section#mainContent .categoryForum .name+.lastMessage,
section#mainContent .categoryForum .name+.lastMessage .lastMessage
{
  width: auto !important;
  height: auto !important;
  line-height: initial !important;
  bottom: 0 !important;
}
section#mainContent .categoryForum {
  height: auto !important;
  width: auto !important;
  margin: 5px !important;
  padding: 0 !important;
}
section#mainContent .categoryForum .name .rightContentCategoryForum {
  width: auto !important;
}
section#mainContent .groupCategoryForum {
  display: flex !important;  
  flex-flow: row wrap !important;  
  margin-top: 5px !important;
}
@supports not (flex-wrap: wrap) {
  section#mainContent .groupCategoryForum  { display: block !important; }
}
}

@-moz-document url-prefix("http://fr.openclassrooms.com/") {
#oc-header .oc-logo {
   -webkit-background-size: 342px 221px !important;
    -moz-background-size: 342px 221px !important;
    background-size: 342px 221px !important;
    background-position: 0 -168px !important;
    width: 90px !important;
    height: 53px !important;
}
.centered {
   width: auto !important;
   padding: 0 10px !important;
}
#oc-header nav .spacer {
   height: auto !important;
}  
#oc-header .menu-nav .vertical-divider {
   height: 20px !important;
}
#oc-header .menu-nav, #oc-header .input-group.smaller input, div.input-group.smaller .oc-btn  {
   min-height: initial !important;
}
#oc-header .header-inner {
   padding: 0 !important; 
   min-height: initial !important;
}
#oc-header header {
   padding-top: 0 !important;   
   margin-bottom: 0 !important;
}
#oc-header nav .to-right.menu-connection {
   padding-top: 0 !important;
}
#oc-header footer {
  display: none !important;
}

vertical-divider.to-right, .spacer.to-right, .menu-nav-widget.to-right, .menu-nav-items.to-right {
   float: none !important;
}
body.oldsdz #headerBreadcrumb, 
section#mainContent h1+h2,
section#mainContent .categoryTitle,
section#mainContent #mainSection h1,
section#mainContent #mainSection .categoryForum > *,
header#mainHeader section#headerBreadcrumb ul,
header#mainHeader section#headerBreadcrumb li,
body.oldsdz header#mainHeader section#headerBottom,
body.oldsdz header#mainHeader section#headerBottom a,
body.oldsdz header#mainHeader section#headerBottom ul,
body.oldsdz header#mainHeader section#headerBottom li,
body.oldsdz #headerBreadcrumb a
{
   height: auto !important;
   margin-top: 0 !important;
   margin-bottom: 0 !important;
   padding-top: 0 !important;
   padding-bottom: 0 !important;
   line-height: initial !important;
   min-height: initial !important;
}

.containerSdz { 
    width: auto !important; 
    padding: 0 40px 0 20px !important; 
}
.subscription {
    padding: 0 9px;
}
section#mainContent section#mainSection {
    width: auto !important;
}

#headerMiddle, #beta, .uservoice, header#mainHeader section #mainMenu div.contentDown .menu li.sep:last-child {
   display: none !important;
}
#headerBreadcrumb + .adBan {
   padding: 0 !important;
   border-bottom: 1px solid black !important;
}
body {
   position: absolute !important;
   min-width: 100% !important;
   min-height: 100% !important;
   padding-bottom: 114px !important; 
   -moz-box-sizing: border-box !important;
   box-sizing: border-box !important;
}
#oc-footer {
   position: absolute !important;  
   min-width: 100% !important; 
}
#fixedNav {
   width: 100% !important;
}
#fixedNav a {
   right: 5px;
}
.pavement, .adBan {
   display: none !important;
}
header#mainHeader section#headerBottom {
   margin-top: 0 !important;
}
ul.level-2 {
  display: flex !important;  
  flex-flow: row wrap !important;  
}
ul.level-2 li {
  width: auto !important;
  white-space: initial !important;  
  max-width: 300px;
}
.toc-big .level-2>li,
.toc-big .level-2>li:nth-child(3n) {
   margin: 5px !important;
   height: auto !important;
}
.toc-big .course-title-1 span {
  margin: 5px !important;
  line-height: initial !important;
}
.toc-big .level-3 li span,
.toc-big .course-title-2 span{  
  display: inline;          
}
.toc-big .course-title-2 span:after {
  content:'\a';
  white-space: pre;
}
}
