css:
.ad_wrapper_fixed p, #pushdown_banner, .ad_wrapper_fixed, #ad300, #ad728, #ban_bottom, #vert_slug_leaderboard, .adBox2, .footOverlay {
	width: 0px;
	height: 0px;
	overflow: hidden;
	z-index: -1;
	border: none;
	background: none;
}

td.slug img {
	width: 1px;
	height: 1px;
	padding-top: 90px;
}
#ad-table td.slug {
	background-color: transparent;
}
#pushdown_banner {
	z-index: -1;
	height: 1px !important;
}

.adbox, .adbox2, .ad_slug, .ad_wrapper, .ad_wrapper_fixed, .brand-mamabar, .ppp_interior_ad, #column-ad, #layout-ad-top { display: none !important; }

#ad-table td.slug { background-color: transparent !important; }

td.slug img { visibility: hidden !important; }
.brand-mamabar {
	height: 0px;
	width: 0px;
	overflow: hidden;
}
.brand-mamabar {
	height: 0px !important;
}
