css:
@-moz-document url-prefix('http://eksisozluk.com'),
 url-prefix('https://eksisozluk.com'),
 url-prefix('http://www.eksisozluk.com'),
 url-prefix('https://wwww.eksisozluk.com') {

/*ana arkaplan rengi, satırarası yüksekliği ve toplam genişlik*/
body
 {
background-image: url(http://i45.tinypic.com/f4g2mr.jpg);
background-color:#cccccc;
background-repeat: no-repeat;
color: #212121;
width:100%;
line-height:15px; 
background-size: 100%;
background-attachment:fixed;
}

a { 
color: #00295e;
}

/*oylama grubu*/
/*like*/
#entry-list footer .rate-options li a.like { 
background-color: #ffffff;
color: #006b00;
}
/*dislike*/
#entry-list footer .rate-options li a.dislike { 
background-color: #ffffff;
color: #6b0000;
}

/*nick*/
address { 
background-color: #ffffff;
}

/*mesaj atma grubu*/
#entry-list footer div.options .other > a, #entry-list footer div.options > a { 
background-color: #ffffff;
color:#666666;
}

/*tarih*/
time { 
color: #444444;
font-size: 0.8 em;
}



#title {
	background-attachment: scroll;
	background-image: url(http://a1301.hizliresim.com/15/g/hxz1y.gif);
	background-repeat: no-repeat;
	*background-color: #fff;
	background-position: right center;
}





*logogizle*/
#logo a{display: none}

#logo a { 
background-image: url("http://b1301.hizliresim.com/15/g/hxz8w.gif");
*background-image: url("http://b1301.hizliresim.com/15/f/hxfpk.gif");
background-position: 1px 0px;
background-repeat: no-repeat;
background-size: 190px 40px;
display: block;
height: 40px;
line-height: 40px;
margin-bottom: 0px;
margin-left: 0px;
margin-right: 0px;
margin-top: 0px;
padding-bottom: 0px;
padding-left: 0px;
padding-right: 0px;
padding-top: 0px;
text-indent: -119988px;
}


/*entry girme kutusu*/
textarea { 
color: #000000;
background-color: rgba(255, 255, 255, 0.400);
}

/*top*/
html > body > header #top-bar { 
height: 65px;
background-image: url("http://i46.tinypic.com/m8jjid.gif");
*background-image: url("http://a1301.hizliresim.com/15/9/hsvr2.gif");
}



/*bugün gündem badi vs. bandı sonra incele sil*/
#top-navigation > ul > li > a, #sub-navigation > ul > li > a { 
padding-left: 10px;
}

/*kutu yüksekliği*/
* { 
box-sizing: inherit;
}

/*arama kutusu yüksekliği*/
#search-textbox { 
height: 21px;
}

/*videolar reklamını kaldır*/
#aside{display: none}


/*sol frame genişliği*/
#index-section { 
width:240px;
margin-left:5px;
}

/*ana frame uzaklığı*/
#content-section { 
margin-left: 63px;
margin-right: 70px;
}

/*bugün düğmesi küçültücü*/
h2 { 
font-size: 12px;
margin-bottom: 0px;
}

/*yenile düğmesi küçültücü*/
#feed-refresh-link { 
font-size: 9px;
border-bottom-width: 0px;
border-right-width: 0px;
border-top-width: 0px;
border-left-width: 0px;
margin-bottom: 0px;
text-align: right;
}

/*içerik kısmı genişliği*/
 #topic{
 top:0px;
 width: 120%
}



/*bugün gündem badi vs. bandı*/
#sub-navigation { 
background-color: #566484;
font-size: 0.8em;
}

/*logo ve arama kutusu bandı*/
body > header { 
background-color: #cccccc;
}


/*ara düğmesi renklendirici*/
#a3-toggle { 
margin-top: 0px;
background-color: #FFC600;
background-image: -o-linear-gradient(top, #FFCC1A, #E6B200);
background-repeat: repeat-x;
border-bottom-color: #B38B00;
border-bottom-style: solid;
border-bottom-width: 1px;
border-left-color: #B38B00;
border-left-style: solid;
border-left-width: 1px;
border-right-color: #B38B00;
border-right-style: solid;
border-right-width: 1px;
border-top-color: #B38B00;
border-top-style: solid;
border-top-width: 1px;
box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.102);
color: #000000;
}

/*ben mesaj olay yazı rengi*/
#top-navigation > ul > li > a { 
margin-top: 8px;
background-color: #FFC600;
background-image: -o-linear-gradient(top, #FFCC1A, #E6B200);
1background-repeat: repeat-x;
border-bottom-color: #B38B00;
border-bottom-left-radius: 3px;
border-bottom-right-radius: 3px;
border-bottom-style: solid;
border-bottom-width: 1px;
border-left-color: #B38B00;
border-left-style: solid;
border-left-width: 1px;
border-right-color: #B38B00;
border-right-style: solid;
border-right-width: 1px;
border-top-color: #B38B00;
border-top-left-radius: 3px;
border-top-right-radius: 3px;
border-top-style: solid;
border-top-width: 1px;
box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.102);
color: #000000;
font-family: "Verdana", sans-serif;
}

/*ben mesaj olay yazı şaftı*/
#top-navigation > ul { 
line-height: 25px;
}

/*mesajınız var yeşili*/
#top-navigation > ul .new-update, #sub-navigation > ul .new-update  { 
background-image: -o-linear-gradient(top, #ff00bb, #ff00bb);
border-bottom-color: #B38B00;
border-bottom-left-radius: 3px;
border-bottom-right-radius: 3px;
border-top-left-radius: 3px;
border-top-right-radius: 3px;
border-top-style: solid;
box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.102);
background-color: rgba(255, 0, 187, 1.0);
}

/*getir düğmesi boyutlandırıcı*/
#top-bar input[type="submit"].primary { 
height: 21px;
}
}
