css:
@-moz-document domain("forums.somethingawful.com") {
#container {
        max-width: 1526px !important; 
        margin: 0 auto !important;
        background-color:white;
        border-radius: 5px;
        box-shadow: 0px 0px 50px 5px black;
}
body {
        background-image:url(http://i.somethingawful.com/core/header-bg-bluenoise.png) !important;
        background-attachment:fixed !important;
        background-position:center !important;
        background-repeat:yes !important;
}
}

@-moz-document url-prefix("http://forums.somethingawful.com/forumdisplay.php?forumid=26"), url-prefix("http://forums.somethingawful.com/forumdisplay.php?forumid=154") {
body {
        background-image:url(http://i.somethingawful.com/u/docevil/fyadbg.jpg) !important;;
}
}

@-moz-document url-prefix("http://forums.somethingawful.com/f/search") {
body {
        background-image:url(http://images5.fanpop.com/image/photos/29400000/White-writing-29491444-516-350.jpg) !important;;
}
}
