body {

background:url("http://f.cl.ly/items/281a0Q0c1O3N3V3H3g0f/Untitled.jpg") no-repeat fixed !important;
  background-color: rgba(226,170,230, 1.0) !important;
  background-position: 0% 100% !important;
  background-size: 1024px 768px  !important;
}



#logo {
  width: 0px !important;
  height: 0px !important;

  width: 196px !important;
  height: 44px !important;
}


