[class="commentsfirst"] {color: #999 !important}
[id="commentForm_FormField_content"] {background-color: rgba(20,25,25,1)  !important; border-color: #333 !important; -moz-border-top-colors: #333 !important; -moz-border-bottom-colors: #333 !important; -moz-border-left-colors: #333 !important; -moz-border-right-colors: #333 !important}
[type="submit"] {
    color: #999 !important;
    background-color: rgba(33,44,33,1) !important;
    /*text-shadow: 0px 0px 4px rgba(0,0,0,1) !important;*/
    box-shadow: inset 1px 1px 0px rgba(80,80,80,1) !important
    }
[class="category"] {color: #999 !important; background-color:transparent !important}
[class="rank"] {opacity: 0.5 !important; color: rgba(40,20,20,1) !important}

[class="users"] {min-height: 200px !important}
[class="users"]>li>div[class="ctn"]>span[class="title"] {font-size: 12px !important}
[class="users"]>li>div[class="ctn"]>[class="username"] {font-size: 22px !important; position:absolute; left:135px; top:30px}

/*____ ZDJĘCIE - OVERLAY____*/
[id="overlay"] [class="username"] {font-size: 24px !important}
[id="overlay"] [class="category"] {font-size: 14px !important}
[id="photoLayer"] {opacity: 1 !important; background-color: rgba(20,20,20,1)  !important}
[id="overlay"] {background: none !important; background-color: rgba(1,1,1,.95)  !important}
div#commentformcont {background-image: none !important; box-shadow: inset 0px 1px 0px rgba(40,40,40,1) !important}
[id="overlay"] [class="description"] label {position: relative; left: -15px !important}
[id="overlay"] [class="users"] label {position: relative; left: -25px !important}

/*____ WYSZUKIWARKA ____*/
[class="filterForm"], [class="portfolio_filter"] {width: 500px !important}
[class="filterForm"] [class="selectspan"], [class="select_text"], [ style="opacity: 0;"], [class="ui-autocomplete-input"] {color: #999 !important; background-color: rgba(20,25,25,1) !important; border-color: #333 !important}
[class="filterForm"] [class="cancel"]  {color: #111 !important; background-color: rgba(125,25,25,1) !important}
[class="content portfolio"] [type="submit"]  {color: #111 !important; background-color: rgba(65,120,50,1) !important}
[class="portfolio_select_items"], [class="category_select_items"] {background-color: rgba(20,25,25,.95) !important}
[name="filter[province]"] [value] {opacity: 0.5 !important}
[class="spancheckbox  "], [class^="spancheckbox"] {opacity: 0.5 !important}
[class="selected parentlabel"] label {color: #888 !important;}
[class="parentlabel"] div {color: #888 !important}
[class="parentlabel"] span {color: #888 !important;}
[class^="filter_select selected"] label {color: #888 !important}
[class="ui-menu-item"] {background-color: rgba(20,25,25,.95) !important}

/*____ RANKING ____*/
[class="content ranking"] [class="active"] {background-color: rgba(21,23,22,1)  !important; padding-left: 10px !important; padding-right: 5px !important;  margin-left: 5px !important; box-shadow: inset 0px 1px 1px rgba(70,70,70,1), 0px 0px 4px rgba(1,1,1,1) !important; background-image: none !important}
[class="content ranking"]>li:hover {background-color: rgba(21,23,22,1)  !important; box-shadow: inset 0px 1px 1px rgba(60,60,60,1) !important; background-image: none !important}

/*____ FORUM ____*/
[class="content forum"] {background-color: #181918 !important; padding-left: 10px !important; padding-right: 10px !important}
[class="content forum"] p {font-size: 16px !important} 
[class="head"] {background-color: #151716  !important; box-shadow: inset 0px 1px 1px rgba(60,60,60,1) !important}
[class="subforum_name"] {font-size: 30px  !important}
[class="body"] tr {border-color: #333 !important}
td {height: 35px !important}
[class="center"]  {width: 130px; height: 10px !important}
[class="count"]  {width: 70px; height: 10px !important; border-color: #333 !important; -moz-border-top-colors: #333 !important; -moz-border-bottom-colors: #333 !important; -moz-border-left-colors: #333 !important; -moz-border-right-colors: #333 !important}
[class="post_count"], [class="posts_count"]  {border-color: #333 !important; -moz-border-top-colors: #333 !important; -moz-border-bottom-colors: #333 !important; -moz-border-left-colors: #333 !important; -moz-border-right-colors: #333 !important}
[class="stats"] {height: 65px !important}
[class="quote"] {background-color: rgba(27,32,32,.4) !important; margin-left: 10px !important; margin-right: 5px !important;  border-color: #333 !important; -moz-border-top-colors: #333 !important; -moz-border-bottom-colors: #333 !important; -moz-border-left-colors: #333 !important; -moz-border-right-colors: #333 !important}
td, [class="subforum"] {border-color: #333 !important; -moz-border-top-colors: #333 !important; -moz-border-bottom-colors: #333 !important; -moz-border-left-colors: #333 !important; -moz-border-right-colors: #333 !important}
[style="width: 81px"] {border-color: #333 !important; -moz-border-top-colors: #333 !important; -moz-border-bottom-colors: #333 !important; -moz-border-left-colors: #333 !important; -moz-border-right-colors: #333 !important}
[class="text"] {background-color: transparent !important}
[class="time"] {font-size: 12px !important}
[class="date"] {font-size: 12px !important}
[class="reply post loginTrigger"] {
    color: #999 !important;
    background-color: rgba(33,44,33,1) !important;
    text-shadow: 0px 0px 4px rgba(0,0,0,1) !important;
    box-shadow: inset 1px 1px 0px rgba(80,80,80,1) !important;
    position:relative; right: 5px
    }
a[class="reply post loginTrigger"] {color: #999 !important}
[class="content forum"] [class="active"] {background-color:#151716  !important; border-radius: 3px !important; padding-left: 7px !important; padding-right: 7px !important; padding-bottom: 3px !important; box-shadow: 0px 1px 1px rgba(70,70,70,1), inset 0px 0px 4px rgba(1,1,1,1) !important}

[class="FormField FormFieldTextarea"] {color: #999 !important; background-color: rgba(20,25,25,1) !important; border-color: #333 !important}
[id="postformpopup"], [class="boxcontent newpost"] {width: 800px !important; height: 600px !important}
[class="boxcontent newpost"] [class="container"] {width: 800px !important; height: 550px !important}
[class="container"] h1 {font-size: 30px !important}
[id="Forum_PostForm"], [id="Forum_PostForm_FormBox"] {width: 800px !important; height: 570px !important}
div[class="FormRow FormRowTextarea FormRow_pst_content"]  {width: 800px !important; height: 370px !important}
[class="FormRow FormRowTextarea FormRow_pst_content"] {width: 750px !important; height: 340px !important}
[id="Forum_PostForm_FormField__content"] {width: 600px !important; height: 350px !important}



