css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("proboards56.com") {

body { background:url("http://image.bayimg.com/pabpoaabd.jpg") fixed #dde no-repeat!important; }

#pagedropmenu + table[width="92%"] > tbody > tr:first-child > td:first-child { color:#ccc; }

a:link, a:visited  { text-decoration: none !important; }
a:link             { color:#fff !important; }
a:visited          { color:#eee !important; }
a:hover            { text-decoration:underline !important; color:#ff8 !important; }

body *[bgcolor="#0099ff"], *[bgcolor="#00ffff"] { background:#88f !important; }
*[bgcolor="#0033ff"] { background:#88c !important; }

.windowbg2, .titlebg, .popuptitlebg, .quote, .windowbg2 .quote table.quote *, .code, .welcomebg { background-color:#88c !important; background-image:none !important; }
.windowbg, .catbg, .menubg, .newstitlebg, .windowbg2 .code, .windowbg2 .quote, .windowbg .quote table.quote *, #fscroller { background-color:#88f !important; background-image:none !important; }
.quote table.quote table.quote { border:solid black 2px !important; }

}
