css:
@-moz-document url("http://www.allmystery.de/"), 
url("https://www.allmystery.de/"), 
url-prefix("http://www.allmystery.de/themen"), 
url-prefix("https://www.allmystery.de/themen"),
url-prefix("http://www.allmystery.de/ena/"), 
url-prefix("https://www.allmystery.de/ena/"),
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/artikel/"),
url-prefix("http://www.allmystery.de/stats/"),
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/chat/"), 
url-prefix("https://www.allmystery.de/chat/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/wiki/"),
url-prefix("http://www.allmystery.de/pn"), 
url-prefix("https://www.allmystery.de/pn"), 
url-prefix("http://www.allmystery.de/fcg"), 
url-prefix("https://www.allmystery.de/fcg"), 
url-prefix("http://www.allmystery.de/am"), 
url-prefix("https://www.allmystery.de/am"), url-prefix("https://www.allmystery.de/videos/")  {

body.page, body.popup, .titelg {
    background: url(http://static.allmystery.de/upics/f606e5_erZFE.jpg) !important;
    background-attachment:fixed !important;
    }

a.attitel {
   font-size: 0 !important;
   }

a.attitel:after {
   content: "My Mystery" !important;
   font-size: 18px !important;
   }

.header {
    background-color: transparent !important;
    background-image: none !important;
    }

.tabnav a {
    background-color: transparent !important;
    color: white !important;
    }

.tabnav a:hover, a.tabnav_selected, a.tabnav_js_selected {
    background-color: #424242 !important;
    border-radius: 10px 10px !important; 
    }

.sufu {
    border: 1px solid transparent !important;
    border-radius: 10px 10px !important;
    }

.quick_item_selected {
    background: #424242 !important;
    }

.mcell_inhalt, .mcell_inhalt2, .mcell_inhalt3 {
    border-bottom-left-radius: 10px !important;
    border-bottom-right-radius: 10px !important;
    border-radius: 0 0 10px 10px !important;
    background: rgba(102,102,102,0.5)!important;
    box-shadow: 2px 2px 15px #000000 !important;
    box-shadow: 2px 2px 15px #000000 !important;
    }
	
.mcell, .mcell2, .mcell3 {
    border-top-left-radius: 10px !important;
    border-top-right-radius: 10px !important;
    border-radius: 10px 10px 0 0 !important;
    opacity: 0.8 !important;
    }

table {
   border-spacing: 5px !important;
   border-color: transparent !important;
   }

.start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last, .post, .post:nth-child(even), .paginatorLast, .paginator, .fd, .fh, .videos .videoitem, .videos .videoitem:nth-of-type(even) {
    background-color: transparent !important;
    }

.finfo {
    border-right: solid 0px transparent !important;
    }

.fbeitrag {
    border-left: solid 0px transparent !important;
    }

img.smallCategoryImage {
   border-radius: 10px 10px !important;
   }

.message, .message_info {
   border-color: transparent !important;
   border-width: 1px !important;
   border-style: solid !important;
   background-color: transparent !important;
   }

#intro {
   background: transparent !important;
   border: 4px solid #424242 !important;
   }

.titelg {
   border-bottom: 1px solid transparent !important;
   }

a img {
   border-color: transparent !important;
   border-radius: 10px 10px !important;
   }

.blogs-prevpost.even {
    background: transparent !important;
    border: 1px solid transparent !important;
    }

.blogs-prevpost {
    background: transparent !important;
    border: 1px solid transparent !important;
    }

.voteup {
    background: transparent !important;
    border: 1px solid transparent !important;
    }

.Voting .ui-widget-content .ui-state-default, .Voting-bar-filling {
    background: grey !important;
    }

.Voting-voted .Voting-bar-filling  {
    background: #ffffff !important;
    }

div.post.warn {
    background: none !important;
    background-color: rgba(152,0,0,1.0) !important;
    box-shadow:inset 90px 0px 120px #3A3A3A !important;
    border: 2px dotted  #ff0000 !important;
    }


}
