css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ::::: Align Nav Bar with Windows Explorer
         Remove Search Box Margin at Right ::::: */

#main-window #navigator-toolbox #nav-bar
{
  margin-right: -7px !important;
  margin-top: -2px !important;
  padding: 3px 0 4px 0 !important; /*more padding below url bar */
}

/* ::::: Resize URL Bar, Remove right border ::::: */

#main-window #navigator-toolbox
{
	-moz-appearance: none !important;
	border: 0 none !important;
	padding: 0 !important;
}

#main-window #navigator-toolbox toolbar
{
	-moz-appearance: toolbox !important;
	border: 0 none !important;
}

#main-window #navigator-toolbox #toolbar-menubar,
#main-window #navigator-toolbox #PersonalToolbar
{
	-moz-opacity: 0.99;
}

#main-window #navigator-toolbox #toolbar-menubar,
#main-window #navigator-toolbox #toolbar-menubar menubar,
#main-window #navigator-toolbox #nav-bar
{
	-moz-appearance: none !important;
}

#main-window #navigator-toolbox #toolbar-menubar
{
	background-color: rgba(255, 255, 255, 0.60);
	-moz-border-radius: 2px;
}


#identity-box > hbox
{
	margin-left: 4px !important;
	border-left: 4px solid transparent !important;
	-moz-border-radius-topleft: 0 !important;
	-moz-border-radius-bottomleft: 0 !important;
	-moz-background-clip: padding !important;
}

/* ::::: Fix url bar and search bar look in vista basic mode ::::: */

#main-window #searchbar .searchbar-textbox,
#main-window #urlbar
{
	-moz-background-clip: padding;
	background-color: rgba(255, 255, 255, 0.75) !important;
	-moz-opacity: 0.99;
	border: 3px solid !important;
	-moz-border-radius: 3px;
	-moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) rgba(255, 255, 255, 0.95) !important;
	-moz-border-right-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) rgba(255, 255, 255, 0.95)!important;
	-moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) rgba(255, 255, 255, 0.95)!important;
	-moz-border-left-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) rgba(255, 255, 255, 0.95)!important;
	-moz-appearance: none !important;
  height: 24px !important;
}
#main-window #searchbar .searchbar-textbox{
  margin-right: 7px !important
}

#main-window #urlbar
{
  border-right: none !important;
  -moz-border-radius-topright: 0px !important;
  -moz-border-radius-bottomright: 0px !important;
}

#main-window #urlbar[level="high"][focused="true"] > .autocomplete-textbox-container,
#main-window #urlbar[level="high"][focused="true"] > .autocomplete-history-dropmarker
{
	background-color: rgba(245, 246, 190, 0.99) !important;
}

#main-window #searchbar .searchbar-textbox:hover:not([focused="true"]),
#main-window #urlbar:hover:not([focused="true"])
{
	background-color: rgba(255, 255, 255, 0.90) !important;
}

#main-window #urlbar[level="high"]:hover:not([focused="true"]) > .autocomplete-textbox-container,
#main-window #urlbar[level="high"]:hover:not([focused="true"]) > .autocomplete-history-dropmarker
{
	background-color: rgba(245, 246, 190, 0.65) !important;
}

/* ::::: Redesign Stop Button ::::: */

#stop-button,
#stop-button:not([disabled="true"]):hover,
#stop-button[disabled="true"],
#stop-button:not([disabled="true"]):hover:active {

  width: auto !important;
  padding: 0px !important;
  margin: 0px !important;
  margin-right: 0px !important;
  -moz-appearance: none !important;
  border: none !important;

}

#stop-button > .toolbarbutton-icon {

  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABcAAAAUCAYAAABmvqYOAAAAB3RJTUUH2AUVCBgpUyRNsgAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAsSURBVHjaY2CgIWD8////fhqZ7chES5ePGj5q+Kjho4aPGj48DAfVRDQzHAACpwcfsQXmCgAAAABJRU5ErkJggg==") no-repeat center center !important;
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin:0px !important;
  padding: 0px !important;
  border-top: 2px solid !important;
  border-bottom: 2px solid !important;
  border-right: 2px solid !important;
  -moz-border-radius-topright: 3px !important;
  -moz-border-radius-bottomright: 3px !important;
  -moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) !important;
  -moz-border-right-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) !important;
  -moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) !important;
}

#stop-button:not([disabled="true"]):hover > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


#stop-button:not([disabled="true"]):hover:active > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


/* ::::: Redesign Reload Button ::::: */

#reload-button,
#reload-button:not([disabled="true"]):hover,
#reload-button[disabled="true"],
#reload-button:not([disabled="true"]):hover:active {

  width: auto !important;
  padding: 0px !important;
  margin: 0px !important;
  margin-left: -4px !important;
  -moz-appearance: none !important;
  border: none !important;

}

#reload-button > .toolbarbutton-icon {

  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAUCAYAAACXtf2DAAAAB3RJTUUH2AUVCBc7JwUgNQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAsSURBVHjaY2CgMWD8////flpawERrH4xaMGrBqAWjFoxaMGoBCIBqNJpaAACHAQbePI7ZsAAAAABJRU5ErkJggg==") no-repeat center center !important;	
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin:0px !important;
  padding: 0px !important;
  border-top: 2px solid !important;
  border-bottom: 2px solid !important;
  -moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) !important;
  -moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) !important;
}

#reload-button:not([disabled="true"]):hover > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


#reload-button:not([disabled="true"]):hover:active > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


/* ::::: Highlight Stop and Reload Button on urlbar hover ::::: */

#urlbar-container:hover + #reload-button > .toolbarbutton-icon {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAUCAYAAACXtf2DAAAAB3RJTUUH2AUVCBgVfEsxNQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAnSURBVHjaY2CgMWD8////U1pawERrH4xaMGrBqAWjFoxaMGoBXQAAe7cEBr3gVkUAAAAASUVORK5CYII=") no-repeat center center !important;
}

#urlbar-container:hover + #reload-button + #stop-button > .toolbarbutton-icon {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABcAAAAUCAYAAABmvqYOAAAAB3RJTUUH2AUVCBYmXRh9rQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAArSURBVHjaY2CgIWD8////U5oYzMgozURLl48aPmr4qOGjho8aPjwMpykAABMDBCTXayFoAAAAAElFTkSuQmCC") no-repeat center center !important;
}


/* ::::: Redesign Urlbar Dropmarker ::::: */

#urlbar .dropmarker-icon {
  display:none !important
}

#urlbar > dropmarker {
  width: 19px !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}

#urlbar > dropmarker {
  background: url("data:image/png;base64,") no-repeat center center !important;
}

#urlbar[focused="true"] > dropmarker {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAASCAYAAABWzo5XAAAAB3RJTUUH2AUVCBk2xzdxBgAAAAlwSFlzAAALEAAACxABrSO9dQAAAARnQU1BAACxjwv8YQUAAABjSURBVHjaY2AYBaMAAe48etHw8fO3/7jwvSevGrDpY0QXACnm50/DadHnz7MZeLk5MfQxoQu8/fC5EaQYlyGv339uJNp7IOd//vr9PwNTDByD+Li8RbRhZBuCbhhFhowC6gMAKm1fuIv++6EAAAAASUVORK5CYII=") no-repeat center center !important;
}

#urlbar[level="high"] > dropmarker {
  background: url("data:image/png;base64,") no-repeat center center !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: rgba(245, 246, 190, 0.65) !important;
}

#urlbar[level="high"][focused="true"] > dropmarker {
  background: url("data:image/png;base64,") no-repeat center center !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: rgba(245, 246, 190, 0.99) !important;
}

#urlbar > dropmarker:hover,
#urlbar[level="high"] > dropmarker:hover {
  background: url("data:image/png;base64,") no-repeat center center !important;
  -moz-image-region: auto !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: #000 !important;
}

#urlbar > dropmarker:hover:active,
#urlbar[level="high"] > dropmarker:hover:active {
  background: url("data:image/png;base64,") no-repeat center center !important;
  -moz-image-region: auto !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: #000 !important;
}

/* ::::: Redesign Back and Forward Button and Dropmarker ::::: */

#back-button ,
#nav-bar[saved-iconsize]  #back-button,
#back-button:hover ,
#nav-bar[saved-iconsize] #back-button:hover,
#back-button:active,
#nav-bar[saved-iconsize] #back-button:active,
#back-button[disabled="true"],
#nav-bar[saved-iconsize] #back-button[disabled="true"],
#forward-button,
#nav-bar[saved-iconsize] #forward-button,
#forward-button:hover,
#nav-bar[saved-iconsize] #forward-button:hover,
#forward-button:active,
#nav-bar[saved-iconsize] #forward-button:active,
#forward-button[disabled="true"],
#nav-bar[saved-iconsize] #forward-button[disabled="true"] {
  list-style-image: url("data:image/png;base64,") !important;
}


#back-button ,
#nav-bar[saved-iconsize]  #back-button {
  -moz-image-region: rect(27px 28px 55px 0px) !important;
  background: url("data:image/png;base64,") no-repeat !important;
  background-position: center right !important;
}

#back-button:hover ,
#nav-bar[saved-iconsize] #back-button:hover{
  -moz-image-region: rect(54px 28px 82px 0px) !important;
}

#back-button:active,
#nav-bar[saved-iconsize] #back-button:active{
  -moz-image-region: rect(81px 28px 109px 0px) !important;
}

#back-button[disabled="true"],
#nav-bar[saved-iconsize] #back-button[disabled="true"] {
  -moz-image-region: rect(0px 28px 28px 0px) !important ;
}

#back-button > .toolbarbutton-menubutton-stack,
#nav-bar[saved-iconsize] #back-button > .toolbarbutton-menubutton-stack{
  background: url("data:image/png;base64,") no-repeat !important;
  background-position: center left !important;
}


#forward-button,
#nav-bar[saved-iconsize] #forward-button{
  -moz-image-region: rect(27px 53px 55px 27px) !important;
  background: url("data:image/png;base64,")   no-repeat !important;
  background-position:  left center !important;
}

#forward-button:hover,
#nav-bar[saved-iconsize] #forward-button:hover {
  -moz-image-region: rect(54px 53px 82px 27px) !important;
}

#forward-button:active,
#nav-bar[saved-iconsize] #forward-button:active{
  -moz-image-region: rect(81px 53px 109px 27px) !important;
}

#forward-button[disabled="true"],
#nav-bar[saved-iconsize] #forward-button[disabled="true"]{
  -moz-image-region: rect(0px 53px 28px 27px) !important ;
}
#forward-button > .toolbarbutton-menubutton-stack ,
#nav-bar[saved-iconsize] #forward-button  > .toolbarbutton-menubutton-stack{
  background: url("data:image/png;base64,")   no-repeat !important;
  background-position:  left center !important;
}


#back-forward-dropmarker {
  -moz-appearance: none  !important;
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0px 48px 24px 24px) !important;
  border:0 !important ;
  padding:0 !important  ;
  margin-left:-3px !important;
  margin-top: 1px !important;
}

#back-forward-dropmarker[disabled="true"],
#back-forward-dropmarker[disabled="true"]:hover,
#back-forward-dropmarker[disabled="true"]:active,
#back-forward-dropmarker[disabled="true"]:hover:active {
  -moz-image-region: rect(0px 24px 24px 0px) !important;
}

#back-forward-dropmarker:hover {
  -moz-image-region: rect(0px 72px 24px 48px) !important;
}

#back-forward-dropmarker:hover:active {
  -moz-image-region: rect(0px 96px 24px 72px) !important;
}



/* ::::: Redesign all Throbbers ::::: */

#navigator-throbber {
list-style-image: url("data:image/png;base64,") !important;
}

.addonThrobber,
#navigator-throbber[busy="true"],
#sidebar-throbber[loading="true"],
richlistitem[loading="true"] .updateBadge,
toolbar[mode="text"] #navigator-throbber[busy="true"],
.alltabs-item[busy] > .menu-iconic-left > .menu-iconic-icon,
.tabbrowser-tab[busy] > .tab-image-middle > .tab-icon > .tab-icon-image {
  list-style-image: url("data:image/gif;base64,") !important;
  background:url("data:image/png;base64,") no-repeat center center !important;
  -moz-image-region: auto !important;
}

#navigator-throbber[busy="true"] {
  padding-left: 1px !important;
  margin-left: -1px !important;
}


#sidebar-throbber[loading="true"] {
  -moz-margin-end: 4px;
}



.textbox-icon-box {
  margin-top: -1px !important;
  margin-bottom: -1px !important;
  margin-left: -1px !important;
}


.searchbar-textbox {height: 24px !important;}

.searchbar-engine-button {
  margin-top: -1px !important;
  margin-bottom: -1px !important;
  margin-left: -1px !important;
}

.search-go-button {
  padding-top: 1px !important;
  padding-bottom: 1px !important;
}
