css:
/* AGENT_SHEET */
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("xda-developers.com"){

/* Roboto fonts */
@font-face{font-family:'Roboto';font-style:normal;font-weight:400;src:local('Roboto Regular'),local('Roboto-Regular'),url(http://themes.googleusercontent.com/static/fonts/roboto/v10/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff')}
@font-face{font-family:'Roboto';font-style:normal;font-weight:500;src:local('Roboto Medium'),local('Roboto-Medium'),url(http://themes.googleusercontent.com/static/fonts/roboto/v10/RxZJdnzeo3R5zSexge8UUT8E0i7KZn-EPnyo3HZu7kw.woff) format('woff')}
@font-face {font-family:'Roboto';font-style:normal;font-weight:700;src:local('Roboto Bold'),local('Roboto-Bold'),url(http://themes.googleusercontent.com/static/fonts/roboto/v10/d-6IYplOFocCacKzxwXSOD8E0i7KZn-EPnyo3HZu7kw.woff) format('woff')}
@font-face{font-family:'Roboto';font-style:italic;font-weight:400;src:local('Roboto Italic'),local('Roboto-Italic'),url(http://themes.googleusercontent.com/static/fonts/roboto/v10/1pO9eUAp8pSF8VnRTP3xnvesZW2xOQ-xsNqO47m55DA.woff) format('woff')}
@font-face{font-family:'Roboto';font-style:italic;font-weight:700;src:local('Roboto Bold Italic'),local('Roboto-BoldItalic'),url(http://themes.googleusercontent.com/static/fonts/roboto/v10/t6Nd4cfPRhZP44Q5QAjcC4bN6UDyHWBl620a-IRfuBk.woff) format('woff')}

/* Global changes */
body{background-color:#111!important;color:#fff!important}
.page .tborder{background-color:#222!important}
a{color:#33b5e5!important;text-shadow:none!important}
*{border-color:#292929!important;outline:none!important}
#xda_global_wrapper{min-width:0!important}
ul{color:#fff!important}

/* TODO: replace more text colors that are too dark/find better replacement colors */
font[color="black"]{color:#fff!important}
font[color="blue"]{color:#07a!important}
font[color="red"]{color:#c00!important}
font[color="orange"]{color:#f80!important}
font[color="lime"]{color:#9c0!important}

body,p,div,textarea{font-family:'Roboto'!important}
::-moz-selection{background:#09c!important;color:#fff!important}
::-webkit-selection{background:#09c!important;color:#fff!important}
::selection{background:#09c!important;color:#fff!important}

/* Holofied corners */
.forumSearch_button,.jumpToTop,#forumSearch_form .forumSearch_advanced,.postContent blockquote,.pagenavControls td,input.bginput,#thanksTable .alt2,.searchContainer,.searchResult .alt,#sidepanel_less-threadSearch #sidepanel_less-searchInput,#username_box,.pagenavControls .alt2,.pagenavControls .alt1:active,.pagenavControls .alt1,.pagenavControls .goto_nav,#sidepanel_less-threadSearch,#sidepanel-threadSearch #sidepanel-searchInput,#threadPoll,.postControls .postbitFunction,#postLegend,.postControls .selectedButton,.postControls .postbitFunction:hover,.postControls .postbitMultiquote:hover,.tabControl_editDevice_Button,.forumbox .forumbox-header,.forumbox .content,.social_group_message,#user_avatar.alt2,#profilepic img,.btn-silver,.btn-yellow,#vB-widget #vb_login_submit,#vB-widget #vb_login_username,#vB-widget #vb_login_password,.quickReplyTable .panelsurround,.controlbar,.quickReplyTable .tcat,#pageContainer .button,input[type=button],input[type=submit],.xdabutton,#navtab_findDevice #s,#xda_header_login #navbar_username,#xda_header_login #navbar_password{border-radius:2px!important}
#quicklinks_menu,#navtab_findDevice #results{border-radius:0 0 2px 2px!important}
.quickReplyTable .editorControlsTable,#rssbox h3,#content_container #link_bar,.sidebox h3{border-radius:2px 2px 0 0!important}
#navtab_searchForums #gsc-input,.tborder td:first-child,.postButtonLeft,#threadslist .threadsTable td:first-child,.moreUserInfoButton .buttonLeft,#posts .postHeader .postTitle{border-radius:2px 0 0 2px!important}
.tborder td:last-child,.postButtonRight,#threadslist .threadsTable td:last-child,#posts .postHeader .submitAsTip,.moreUserInfoButton .buttonRight{border-radius:0 2px 2px 0!important}
#thanksTable .alt1{border-radius:0 0 2px 2px!important}
#thanksTable .collapseCell{border-radius:0 2px 0 0!important}
.tborder td:only-child{border-radius:2px 2px 0 2px!important}
.ToolTip{border-radius:0 2px 2px 2px!important}

/* Some transitions to tabs like elements */
.tabcontrols a,#forumhome_tabs a,#xda_navbar a{transition:all .25s ease-in-out 0s!important}

/* input boxes */
input[type='checkbox']{-moz-appearance:none!important;-webkit-appearance:none!important;border:none!important;background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAYAAABy6+R8AAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH3QYaFBclhzi8dwAAAD5JREFUKM/t0rERgDAMwEDZx12q7Efljm21EFVYIaahQf13CvUCbvYbqGcDoFYCQbPkRT/6BK0uOoCpVue9B0J0DTeJSi8hAAAAAElFTkSuQmCC) no-repeat center center!important;width:13px!important;height:13px!important;color:transparent!important}
input[type='radio']{-moz-appearance:none!important;-webkit-appearance:none!important;border:none!important;background:url(data:image/png;base64,) no-repeat center center!important;width:13px!important;height:13px!important;color:transparent!important}
input[type='checkbox']:checked{-moz-appearance:none!important;-webkit-appearance:none!important;background:url(data:image/png;base64,) no-repeat center center!important}
input[type='radio']:checked{-moz-appearance:none!important;-webkit-appearance:none!important;background:url(data:image/png;base64,) no-repeat center center!important}
input[type='radio']:active{background:linear-gradient(#09c,#09c)!important;box-shadow:0 0 2px 3px rgba(0,153,204,0.5)!important}
input[type='checkbox']:active{background:linear-gradient(#09c,#09c)!important;box-shadow:0 0 2px 3px rgba(0,153,204,0.5)!important}
select,textarea{-moz-appearance:none!important;-webkit-appearance:none!important;background-color:#333!important;color:#fff!important;border:1px solid #292929!important}
/* adapted from: http://codepen.io/killercup/details/CBeAq */
input{margin:0!important;border:0!important;-webkit-appearance:none!important;-moz-appearance:none!important;appearance:none!important;border-radius:0!important;outline:none!important;box-shadow:none!important}
input{color:#fff!important;border:none!important;-webkit-box-sizing:border-box!important;-moz-box-sizing:border-box!important;box-sizing:border-box!important;padding:8px 8px 6px!important;background:bottom left -webkit-gradient(linear,50% 0%,50% 100%,color-stop(0%,#888),color-stop(100%,#888)) no-repeat,bottom center -webkit-gradient(linear,50% 0%,50% 100%,color-stop(0%,#888),color-stop(100%,#888)) repeat-x,bottom right -webkit-gradient(linear,50% 0%,50% 100%,color-stop(0%,#888),color-stop(100%,#888)) no-repeat!important;background:bottom left -webkit-linear-gradient(#888,#888) no-repeat,bottom center -webkit-linear-gradient(#888,#888) repeat-x,bottom right -webkit-linear-gradient(#888,#888) no-repeat!important;background:bottom left -moz-linear-gradient(#888,#888) no-repeat,bottom center -moz-linear-gradient(#888,#888) repeat-x,bottom right -moz-linear-gradient(#888,#888) no-repeat!important;background:bottom left linear-gradient(#888,#888) no-repeat,bottom center linear-gradient(#888,#888) repeat-x,bottom right linear-gradient(#888,#888) no-repeat!important;-webkit-background-size:1px 6px,1px 1px,1px 6px!important;-moz-background-size:1px 6px,1px 1px,1px 6px!important;background-size:1px 6px,1px 1px,1px 6px!important}
input:focus{background:bottom left -webkit-gradient(linear,50% 0%,50% 100%,color-stop(0%,#09c),color-stop(100%,#09c)) no-repeat,bottom center -webkit-gradient(linear,50% 0%,50% 100%,color-stop(0%,#09c),color-stop(100%,#09c)) repeat-x,bottom right -webkit-gradient(linear,50% 0%,50% 100%,color-stop(0%,#09c),color-stop(100%,#09c)) no-repeat!important;background:bottom left -webkit-linear-gradient(#09c,#09c) no-repeat,bottom center -webkit-linear-gradient(#09c,#09c) repeat-x,bottom right -webkit-linear-gradient(#09c,#09c) no-repeat!important;background:bottom left -moz-linear-gradient(#09c,#09c) no-repeat,bottom center -moz-linear-gradient(#09c,#0099cc) repeat-x,bottom right -moz-linear-gradient(#09c,#09c) no-repeat!important;background:bottom left linear-gradient(#09c,#09c) no-repeat,bottom center linear-gradient(#09c,#09c) repeat-x,bottom right linear-gradient(#09c,#09c) no-repeat!important;-webkit-background-size:1px 6px,1px 1px,1px 6px!important;-moz-background-size:1px 6px,1px 1px,1px 6px!important;background-size:1px 6px,1px 1px,1px 6px!important}

/* Theme select element */
select > button{-moz-appearance:none!important;-webkit-appearance:none!important;border:none!important;visibility:hidden!important}
/* adapted from: https://github.com/zmyaro/holo-web */
select:not([multiple]){border:none!important;margin:2px!important;background:-webkit-linear-gradient(135deg,#888 8px,transparent 9px)!important;background:-moz-linear-gradient(135deg,#888 8px,transparent 9px)!important;background:linear-gradient(315deg,#888 8px,transparent 9px)!important;border-bottom:1px solid #888!important;color:#fff!important}
select:not([multiple]):focus{background:-webkit-linear-gradient(135deg,#09c 8px,transparent 9px)!important;background:-moz-linear-gradient(135deg,#09c 8px,transparent 9px)!important;background:linear-gradient(315deg,#09c 8px,transparent 9px)!important;border-bottom-color:#09c!important}
select:not([multiple]):active{background:-webkit-linear-gradient(135deg,#888 8px,#09c 9px)!important;background:-moz-linear-gradient(135deg,#888 8px,#09c 9px)!important;background:linear-gradient(315deg,#888 8px,#09c 9px)!important;border-bottom:1px solid #888!important}
optgroup,option{background:#333!important}
option:checked{background:#09c!important;color:#fff!important}

/* input buttons */
.btn-grey,.xdabutton,button,input[type="button"],input[type="submit"],input.button{-moz-appearance:none!important;-webkit-appearance:none!important;box-shadow:none!important;background:rgba(255,255,255,0.2)!important;text-shadow:none!important;color:#fff!important;border:none!important}
.btn-grey,.xdabutton,input[type="button"],input[type="submit"],input.button{box-shadow:0 1px #888 inset!important}
.btn-grey:active,.xdabutton:active,input[type="button"]:active,input[type="submit"]:active,input.button:active{background:#33b5e5!important;box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.btn-grey:hover,.xdabutton:hover,input[type="button"]:hover,input[type="submit"]:hover,input.button:hover{box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.bginput[name="attachmenturl[]"],.bginput[name="attachment[]"]{margin-top:3px!important;border:none!important}
input::-moz-focus-inner,button::-moz-focus-inner{border:none!important}

/* Forum top nav bar */
#xda_navbar{background-color:#444!important;color:#09c!important;box-shadow:0 1px 3px #09c!important} /* nav bar bg */
#xda_navbar td > a{color:#09c!important;text-shadow:none!important;border:none!important} /* nav buttons */
#xda_navbar td > a:hover{background-color:transparent!important;box-shadow:0 -5px 0 -1px #09c inset!important}
#xda_navbar td{border:none!important}

/* Forum posts table */
.threadTitle[style=""]{color:#09c!important}
.tborder thead td{background:#333!important;color:#09c!important} /* Forum head (title) */
.threadsTable .announcementContainer{background-color:#292929!important} /* Announcement */
.tborder .forumstats{background-color:#292929!important} /* 2nd row */
.pageNumberList{color:#09c!important}

/* Right pane */
#stickyNav.fixed-bottom,#stickyNav.fixed{z-index:auto!important} /* Fix:Prevent the sidepane from appearing over images and dropdown menus */
#page_rightPane{background-color:#292929!important;border-color:#444!important} /* bg */
#rssbox .articleBox{background-color:#222!important} /* portal posts boxes */
#rssbox .articleBox:hover{background-color:#333!important}
#rssbox h3{background:#333!important}/* portal posts header */
#vB-widget #vB-segControl .vB-sC.sC-active{box-shadow:0 -5px 0 -1px #09c inset!important} /* subscriptions */
#vB-widget #vB-segControl .vB-sC{background:#333!important;border:none!important}
#vB-widget #vB-segControl .vB-sC:hover{box-shadow:0 -5px 0 -1px #09c inset!important}
#vB-widget{background-color:#222!important}

/* Buttons (e.g. "Mark Forum Read") */
.btn-silver{background:rgba(255,255,255,0.2)!important;box-shadow:0 1px #888 inset!important;border:none!important;color:#fff!important}
.btn-silver:hover{box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.btn-silver:active{background:#33b5e5!important;box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}

/* Page buttons ("1", "2", "3", etc) */
.pagenavControls .alt1{background:rgba(255,255,255,0.2)!important;box-shadow:0 1px #888 inset!important;border:none!important}
.pagenavControls .alt1:hover{box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.pagenavControls .alt1:active{background:#33b5e5!important;box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.pagenavControls .alt1 a:active{color:#fff!important}
.pagenavControls .alt1 a{color:#fff!important}
.pagenavControls .goto_nav{background:rgba(255,255,255,0.2)!important;box-shadow:0 1px #888 inset!important;border:none!important}
.pagenavControls .goto_nav:hover{box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.pagenavControls .goto_nav:active{background:#33b5e5!important;box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.pagenavControls .alt2{background:#33b5e5!important;border:none!important}

/* New Thread button */
.btn-yellow{background:rgba(255,255,255,0.2)!important;color:#fff!important;box-shadow:0 1px #888 inset!important;text-shadow:none!important;border:none!important}
.btn-yellow:hover{box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.btn-yellow:active{background:#33b5e5!important;box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}

/* Thread Header */
#posts .postHeader .postTitle,.submitAsTip{background:#333!important}
h1[style="font-size: 1.6em;color: #353535;text-shadow: 1px 1px #DDD;margin: 0px;"]{color:#fff!important;text-shadow:none!important}

/* Post */
.tborder .posterInfo,.tborder .alt1.postContent{background-color:#222!important}
.postsig{border-color:#444!important}
.postDate{color:#888!important}

/* Project (ROM threads' top bar) */
.upper_nav_container{background-color:#333!important}
.threadcontrols2 a.active{box-shadow:0 -5px 0 -1px #09c inset!important}
.threadcontrols2 a:hover{box-shadow:0 -5px 0 -1px #09c inset!important}
#tabControl_loading:hover{box-shadow:none!important}
#project_menu_threadcontrols a{background-color:transparent!important}

/* Project > Screenshot */
.sidepane_panel .sidepane_panel_title{background-color:#333;color:#fff!important}
.sidepane_panel,.sidepane_panel .sidepane_panel_footer{background:#222!important;color:#fff!important}

/* Project > Downloads */
#devdb_list .forumbox-header{color:#fff!important}
.project_section table th{background:#292929!important;color:#fff!important}

/* Post Footer */
.tborder .posterInfoFoot{background-color:#333!important;border-width:1px!important;border-right:none!important}
.tborder .postContentFoot{background-color:#333!important;border-width:1px!important}

/* Post header */
#posts .posterInfoHead,#posts .tborder .postContentHead{background-color:#333!important}
hr[style="color:#fff; background-color:#fff"]{border-color:#444!important}

/* Post quotes */
.postContent blockquote{background-color:#333!important}
.alt2[style='border:1px solid #B4B4B4;border-radius: 3px;background-color: #E9E9E9;padding: 10px;']{background-color:#333!important}

/* [code] */
.postContent div .smallfont{color:#888!important}
.prettyprint.prettyprinted{border:1px solid #292929!important}
.prettyprint.prettyprinted .pln{color:#fff!important}
.prettyprint.prettyprinted .kwd{color:#33b5e4!important}
.prettyprint.prettyprinted .typ{color:#a6c!important}
.prettyprint.prettyprinted .pun{color:#690!important}
.prettyprint.prettyprinted .str{color:#9c0!important}
.prettyprint.prettyprinted .com{color:#f44!important}

/* Post polls */
#threadPoll{background:#292929!important}
#threadPoll .tcat{background-color:transparent!important}

/* Most Thanked sidebar */
#topthanked li{background-color:#222!important}
#topthanked li:hover{background:#292929!important}
.tooltip-content{background-color:#292929!important;box-shadow:0 0 1px #999 inset!important}
.topThanks-padding{background-color:#222!important}
.topthanked_nav{background-color:#222!important}
.topthanked_nav:hover{background:#292929!important}
.topthanked_selected{box-shadow:0 -5px 0 -1px #09c inset!important}
.sidebox h3{background:#333!important}
.thanked[style="color:#000"]{color:#fff!important}

/* Community apps sidebar box */
.promoted_apps_box{background-color:#222!important}

/* Profile info box */
#usercss #content_container #username_box{background:#333!important;border-top:none!important;border-left:none!important;border-right:none!important}
#usercss #content_container #link_bar{background-color:#222!important}

/* Profile tabs buttons */
#profile_tabs .tab_list{background-color:#333!important;padding-top:5px!important}
#profile_tabs{background-color:#222!important}
#profile_tabs .tab_list .tcat{box-shadow:0 -5px 0 -1px #09c inset!important;background:transparent!important}
#profile_tabs .tab_list .thead:hover{box-shadow:0 -5px 0 -1px #09c inset!important;transition:all .25s ease-in-out 0s!important}
.tcat[id*='_tab']{color:#09c!important;border-top:none!important}
#profile_tabs .tab_list .thead{background:none!important;text-shadow:none!important}

/* Profile tabs content*/
.alt2.list_no_decoration{background:none!important}

/* Breadcrumb */
#breadcrumb span:last-child{color:#09c!important}
#breadcrumb .lowertitle{color:#fff!important}

/* User info popup (when clicking on someone's name) */
.postmenu_profile{background:#333!important}

/* headers (general) */
.thead{background-color:#292929!important;color:#fff!important}
.tcat{background-color:#333!important;border:none!important;color:#09c!important}
/*.forumbox-header{background:red!important}*/

/* Quick links dropdown */
#quicklinks_menu{background:#222!important}
li[class='header']{background-color:#444!important}

/* Quick Reply */
.vBulletin_editor{background:none!important}
.controlbar{background-color:#333!important}
.quickReplyTable .editorControlsTable{background-color:#333!important}

/* Post (Advanced editing) */
.controlbar .popup_pickbutton{background-color:transparent!important}
.popup_feedback{color:#fff!important;border:none!important}
.controlbar .vbmenu_popup{background:#222!important}
.osize,.ofont,.ocolor,.osmilie{background:#222!important;color:#fff!important}
.osize:hover,.ofont:hover,.ocolor:hover,.osmilie:hover{background:#09c!important}

/* Attachments page */
#collapseobj_attachkey .tcat{background-color:#292929!important}

/* login button */
#xda_header_login input.button{box-shadow:none!important;background:transparent!important}

/* PM */
.pagenav{background:transparent!important}
#pmform table tbody tr{background-color:#333!important}

/* PM capacity bar */
td[style="background-color:red; font-size:10px"]{background-color:#c00!important}
td[style="background-color:orange; font-size:10px"]{background-color:#f80!important}
td[style="background-color:green; font-size:10px"]{background-color:#690!important}

/* popup menus */
.vbmenu_popup{background-color:#292929!important}
.pagenavControls .thead,.vbmenu_popup .vbmenu_option{background-color:#292929!important}

/* search */
.cse .gsc-control-cse,.gsc-control-cse{background-color:#222!important}
.cse .gs-webResult .gs-snippet,.gs-webResult .gs-snippet,.gs-fileFormatType,.gs-imageResult .gs-snippet{color:#fff!important}
.cse .gs-webResult .gs-visibleUrl,.gs-webResult .gs-visibleUrl,.cse .gs-webResult .gs-visibleUrl-short,.gs-webResult .gs-visibleUrl-short,.gs-imageResult .gs-visibleUrl,.gs-imageResult .gs-visibleUrl-short{color:#fff!important}
.cse .gs-webResult.gs-result a.gs-title:link,.gs-webResult.gs-result a.gs-title:link,.cse .gs-webResult.gs-result a.gs-title:link b,.gs-webResult.gs-result a.gs-title:link b,.cse .gs-webResult.gs-result a.gs-title:visited,.gs-webResult.gs-result a.gs-title:visited,.cse .gs-webResult.gs-result a.gs-title:visited b,.gs-webResult.gs-result a.gs-title:visited b,.cse .gs-webResult.gs-result a.gs-title:hover,.gs-webResult.gs-result a.gs-title:hover,.cse .gs-webResult.gs-result a.gs-title:hover b,.gs-webResult.gs-result a.gs-title:hover b,.gs-imageResult a.gs-title:link,.gs-imageResult a.gs-title:link b,.gs-imageResult a.gs-title:visited,.gs-imageResult a.gs-title:visited b,.gs-imageResult a.gs-title:hover,.gs-imageResult a.gs-title:hover b,.cse .gsc-cursor-page,.gsc-cursor-page,.cse .gsc-trailing-more-results:link,.gsc-trailing-more-results:link,.cse .gs-spelling a,.gs-spelling a{color:#33b5e5!important}
.cse .gsc-results .gsc-cursor-page.gsc-cursor-current-page,.gsc-results .gsc-cursor-page.gsc-cursor-current-page{background:none!important}

/* Service unavailable / 404 */
body > #content{background-color:#333!important}
body > center > #content{background-color:#333!important}
#content > form{color:#fff!important}

/* Forums list top filtering bar */
#forumhome_tabs li{border:none!important}
.forumhome_tab_Selected,#forumhome_tabs a:hover{box-shadow:0 -5px #09c inset!important}
#forumhome_filters{background-color:#333!important}
[id*="tabControl"].active,.tabcontrols a:hover{box-shadow:0 -5px #09c inset!important}
.tabcontrols a{border:none!important}
#smartDevice .threadMenu{background-color:#333!important}

/* Forum ROMs/Kernels/Tools/Utilities tabs */
.projectheader td{background:#333!important}
.filter_device li{background:#333!important;color:#fff!important}
.project_dateModified,.project_follow,.project_review,.project_author,.project_device,.project_title{background:#222!important;color:#fff!important}
span[style="font-family: Arial,Helvetica,sans-serif;color:#000;"]{color:#fff!important}
.lastrow td{background:#222!important}
.project_tags li{background:#333!important}

/* DevDB */
/*#devdb_list .forumbox-header{background:#292929!important}*/
#bx-pager li,.slider-container img{background-color:#222!important}
.card{background-color:#222!important}
.itemHeader{background-color:#222!important}
.lb-outerContainer{background-color:#333!important} /* image loading popup */
.forumbox-header{background:#292929!important} /* tab's header */
.project_section{background-color:transparent!important} /* tab's content */
#devdb_announcement{background:#444!important;color:#fff!important}
/* DevDB Project creation page */
#smartproject__edit .radioGroup label{color:#fff!important}
.btn-green,.btn-red{background:rgba(255,255,255,0.2)!important;box-shadow:0 1px #888 inset!important;color:#fff!important}
.btn-green:hover{box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.btn-red:hover{box-shadow:0 0 2px 3px rgba(204,0,0,0.5)!important}
.btn-red:active{background:#f44!important;box-shadow:0 0 2px 3px rgba(204,0,0,0.5)!important}
.btn-green:active{background:#33b5e5!important;box-shadow:0 0 2px 3px rgba(51,181,229,0.5)!important}
.chzn-single b{visibility:hidden!important}
.chzn-single{box-shadow:none!important;padding-bottom:5px!important;margin:2px!important;width:143px!important;border:none!important;background:url(data:image/png;base64,) no-repeat center!important;color:#fff!important}
