@namespace url(http://www.w3.org/1999/xhtml);

/* --------------------------------------------	*
 * Style:   All Dark Eyes Wide Open     	*
 * for use with Black Google and Youtube        *
 * Author:  iiwdpn				*
 * --------------------------------------------	*
 * Jan 14th, 2013:				*
 * version 3		*
 * --------------------------------------------	*/

@-moz-document regexp("(?!https?://(www\\.google\\.com|www\\.youtube\\.com|mail\\.google\\.com|.*\\.swf)).*$") {

    /* Global */
    
	*			{ color: #999999 !important; border-color: #333333 !important; }
	a,
	a *			{ color: #666600 !important; text-decoration: none !important; }

	a:hover,
	a:hover *		{ color: #55AA00 !important; }

	a:visited,
	a:visited *		{ color: #884400 !important; }

	a:visited:hover,
	a:visited:hover *	{ color: #55AA00 !important; }
    
    /* No background images. try to exclude icons, other misc items. */ 
    *:not(:empty):not([onclick*="open"]):not(SPAN):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([id*="lbImage"])
    
    { background-image: none !important }

    /* Basic Bodies */
    HTML, BODY { background: none #151515 !important }

    /* Make descendents of the body element transparent.  Formerly "DIV + SPAN" rules. */
    BODY * {background-color: transparent !important}

    /* Give id's BG hopefully */
    DIV[id] {background-color: inherit !important}

    /* Try to contrast containers */
    html:root > BODY > * > * > * > *:not(INPUT):not([onclick]) > DIV:not(:empty):not([id])
    
    {background: none #151515 !important}
    
}
