css:
@-moz-document domain(meetup.com) {





.C_contextNav
{
background-color: #036 !important;
}

#C_nav {
width:750px !important;
}

.C_navBox 
{
}

.C_navBox ul
{
padding-left: 0 !important;
margin-left: 0 !important;
margin-top: -2px !important;
color: White !important;
float: left !important;
width: 630px !important;
height: 40px !important;
-moz-border-radius:5px;
}

.C_navBox ul li { 
display: inline !important;
}

.C_navBox ul li a
{
padding: 0.2em 2.5em !important;
margin-left: 0.5em !important;
border:0px !important;
background-color: #036 !important;
color: White !important;
text-decoration: none !important;
float: left !important;
-moz-border-radius:5px !important;
}

.C_navBox ul li.select a
{
background: #258 !important;
}

.C_navBox ul li a:hover
{
background-color: #369 !important;
color: #fff !important;
}


#C_org {
margin-top: -90px;
margin-left: 620px;
}

#C_nav iframe {
display: none;
}

#C_tabs {
font-size:80% !important;
float:left !important;
position: absolute !important;
top:-8px !important;
width:200px;
}


.serendipity{
visibility: hidden;
}

#C_nav {
margin-top: -116px;
margin-left: 20px;
height: 0px;
}

.organizer
{
position: absolute;
border: 1px grey !important;
-moz-border-radius:5px;
background-color: white !important;
margin-left: 10px !important;
margin-top: 30px !important;
padding: 4px;
width:100px;
}

.assistants
{
position: absolute !important;
background-color: white !important;
width: 760px !important;
margin-left: -643px !important;
margin-top: 166px !important;
border:1px solid #D7D7D7 !important;
border-bottom:0 !important;
-moz-border-radius:5px;
}


#C_pageBody {
width:776px !important;
padding: 0 !important;
margin: 0 !important;
}

#C_context {
padding: 0 !important;
margin: 100px 0 !important;
width: 776px !important;
}

#C_document {
margin-left: 0 !important;
padding-left: 8px !important;
}

.D_box{
width:760px !important;
}

.D_boxbody {
margin:0 !important;
padding:0 !important;
}


#C_googleAds{
visibility: hidden !important;
}

p.meta {
font-size:0.8em !important;
}


.D_actions {
font-size:0.8em !important;
}

.D_tabular thead th {
font-size:0.75em !important;
padding:0.25em 0.55em 0.15em !important;
}

.D_checkColumn{
padding:2px !important;
}

.readState {
padding:2px !important;
}

.D_subject{
padding:4px !important;
max-width:310px !important;
}


.D_subject + td{
font-size:0.8em !important;
max-width:130px !important;
}


.posts{
max-width:50px !important;
}

.views {
max-width:65px !important;
}

.views + td{
padding-left:10px !important;
font-size:0.8em !important;
}

.views + td .D_nobr{
font-size:1em !important;
}

#C_footer {
width:776px !important;
}


.spans2 .D_colbody .D_box{
width:211px !important;
}

.spans5 .D_colbody .D_box{
width:550px !important;
}

.divby7 .last .D_colbody {
margin-left:0 !important;
}

.divby2 .last .D_colbody .D_box{
width:550px !important;
}

.first .D_colbody{
width:550px !important;
max-width:550px !important;
}

.divby2 .D_col {
width:100% !important;
}

.D_boxsection {
padding:1px !important;

}

#C_contextHead {
padding:1em 1em 2.4em !important;
}

/***************************/
/* alternate thread colors */
/***************************/

.thread{
background: #F4F4F4;
}

.thread.tOdd{
background: #FFF;
}




}
