@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tab-stack {
	min-height: 30px !important;
	
	}
	
/* commom properties for all tabs */
.tabbrowser-tab {
	-moz-appearance: none !important;
	padding: 0 !important;
	border: 0 solid Transparent !important;
	background: 0 !important;
	-moz-border-image: none !important;
    text-shadow: 0px 1px 0px rgba(255,255,255,0.4) !important;
    border-radius: 0px !important;
    
    }

.tab-content {
	border: solid Transparent !important;
	padding: 4px 16px 4px 10px !important;
	margin-left: 0px !important;
	margin-bottom: 2px !important;
	border-width:0px 1px 1px 1px !important;
	margin-left: -5px !important;
	}
	
.tabbrowser-tab:first-child .tab-content{
    padding-left:16px !important;
    }
.tabbrowser-tab[pinned] .tab-content {
	padding: 4px 10px 4px 8px !important;
	}
	
/*inactive tab*/
.tabbrowser-tab:not(:first-child) > .tab-stack > .tab-content:not([selected="true"]) {
	background-image: url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: right !important;

	}
/*inactive first child*/
.tabbrowser-tab:first-child > .tab-stack > .tab-content:not([selected="true"]) {
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAeCAYAAAAPSW++AAAACXBIWXMAABImAAASJgEXrYlSAAAAB3RJTUUH3QEOCCoM1wUqaQAAAHdJREFUKM+dkkEKwDAIBHfV/395e2gEWw20yUXQOBODwNGRRACUZJmLFe19ORMOgCuOHTNK0rOjJJ4FkgFAJH2SW+2obK8OK0grj7kLi+2TIwBYQ6WjobaOlfDvckkpj2lyb5P/l28/8ciRKMOwBFvHWLAdqi3cBevNIBLm8quHAAAAAElFTkSuQmCC") !important;
   	background-repeat: no-repeat !important;
   	background-position: right !important;
    margin-left: 2px !important;
	}
/*active tab*/
.tabbrowser-tab:not(:first-child) > .tab-stack > .tab-content[selected="true"] {
	background: #fff !important;
	background-image:-moz-linear-gradient(rgba(222,222,222,1), rgba(230,230,230,1)) !important; 
	background-repeat: no-repeat !important;
	background-position: 0px !important;
	border-radius: 0px 0px 4px 4px !important;
	border-color: #6f6f6f !important;

	box-shadow: inset 1px 0px 0px 0px #dedede,inset -1px 0px 0px 0px #dedede;
	}
/*active first child */
.tabbrowser-tab:first-child > .tab-stack > .tab-content[selected="true"] {
    background: #fff !important;
	background-image:-moz-linear-gradient(rgba(222,222,222,1), rgba(230,230,230,1)) !important; 
	background-repeat: no-repeat !important;
	background-position: 0px !important;
	border-radius: 0px 0px 4px 4px !important;
	border-color: #6f6f6f !important;
	box-shadow: inset 1px 0px 0px 0px #dedede,inset -1px 0px 0px 0px #dedede;
    margin-left: 2px !important;
    }
	
	
