/*toolbar */
toolbar {
	background-image:-moz-linear-gradient(rgba(222,222,222,1), rgba(192,192,192,1)) !important; 
	background-color: #d9d9d9 !important;
	/*border-bottom: 1px solid #c8c8c8 !important;*/
	}
menubar, toolbox {
	background-image:-moz-linear-gradient(rgba(217,217,217,1), rgba(217,217,217,1)) !important; 
	background-color: #d9d9d9 !important; 
	}


/* url and search bar */
#urlbar,
#search-container .searchbar-textbox {
    -moz-appearance: none !important;
    background-color: #fff !important;
    background-image: url('data:image/png; base64, iVBORw0KGgoAAAANSUhEUgAAAAYAAAAYCAIAAABWchv0AAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH3QEOCTYNR7ctlQAAACtJREFUGNNjvHXrFgMqYGFhYSFPiJGRkZZCTExMdLZxUDgCi42fPn1CEwIAe+sGKJWtgLYAAAAASUVORK5CYII=') !important ;
    border: 1px solid #979797 !important;
    border-radius: 3px !important;
    padding: 2px !important;
    box-shadow: inset 1px 0px 0px 0px #eeeeee, 0px 1px 0px 0px #d7d7d7 !important;
    
    }
#urlbar[focused="true"],
#search-container .search-textbox[focused="true"],
#search-container .textbox-input-box[focused="true"] {
    border: 1px solid #689cd3 !important;
    box-shadow: inset 0px -1px 0px 0px #d0e2f4;
}

/*bookmarks bar */
#PersonalToolbar {
    -moz-appearance: none !important;
    height:26px !important;
    padding: 0px !important;
    background-image: url('data:image/png; base64, iVBORw0KGgoAAAANSUhEUgAAAAUAAAAcCAIAAAAm1OLhAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH3QEOEQQQ2/Tn9QAAAERJREFUGNNjPHHiBAMSYNmyZQsyn/HatWso8k+ePEHmMzGgApb////jkydVPbXNG2zq0eVZeHl5UfgbNmxA4VtbWyPzATiIJMNh+1K3AAAAAElFTkSuQmCC') !important;
    }
#PersonalToolbar .bookmark-item,
#PersonalToolbar toolbarbutton {
    -moz-appearance: none;
    padding: 0px !important;
    font-size: 0.9em !important;
    }
.bookmark-item .toolbarbutton-icon {
    
    }
#PersonalToolbar .bookmark-item:hover,
#PersonalToolbar toolbarbutton:hover {
    -moz-appearance:none !important;
    margin: 0 6px !important;
    cursor: pointer !important;
    text-decoration: underline !important;
    }
.bookmark-item > .toolbarbutton-text,
 .bookmark-item > .toolbarbutton-text:hover{

    -moz-margin-start: 3px !important;
    -moz-margin-end: 3px !important;
    font-size: 1.0em !important;
}

/* appmenu */
#appmenu-toolbar-button .toolbarbutton-icon{
    list-style-image : url('data:image/png; base64, ') !important;
    -moz-margin-end: 0px !important;
    }
#appmenu-toolbar-button > .toolbarbutton-text, 
#appmenu-toolbar-button > .toolbarbutton-menu-dropmarker {
    display:none!important;
    }

