css:
@-moz-document url("http://www.xiami.com/"), url-prefix("http://www.xiami.com/?type=") {
body {
background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.298039), rgba(255, 255, 255, 0.298039)), url(http://img13.poco.cn/mypoco/myphoto/20121210/22/632086652012121022063907.png) !important;
background-size: cover !important;
background-attachment: fixed;
background-position: 50% 50%;
background-repeat: no-repeat no-repeat;
}
#n_header {
background-image:url(http://img170.poco.cn/mypoco/myphoto/20121216/19/6320866520121216195140038.png)
}
.n_header_inner a.logo {
-webkit-box-shadow: 0px 0px 10px #999 inset;
border-radius: 2px;
}
#search_box {
background: rgba(244, 244, 244, 0.44);
border-radius: 2px;
-webkit-box-shadow: 0px 0px 10px #999 inset;
}
a.popup_trigger {
border: 0px solid #ededed;
}
ul.xUser_tabs a{
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.498039) 0px 0px 5px;
background: rgba(244, 244, 244, 0.298039);
}
.yasha,.yasha #container, .w302, #n_header_channel .nhsys_menu, #auto_complete td, #auto_complete tr.alt td{
background-color:transparent;
}
.yasha #nav_mini, .tline, .yasha #container, .mod_music_inner, .ys_slide, .mod_focus,  #n_header_channel ul li{
border-color: transparent;
}
.yasha #content, #footer, #n_header_channel ul li.nh_selected, #n_header_channel .nhsys_menu_inner, #auto_complete {
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.498039) 0px 0px 5px;
background: rgba(244, 244, 244, 0.298039);
border-left: 0px solid #e2e2e2;
}
.mod_music_inner:hover, .ys_slide:hover, #footer, #auto_complete td:hover, #auto_complete tr.alt td:hover, #auto_complete, .side_nav{
background: rgba(246, 246, 246, 0.64);
}
#ys_wel{
opacity: 0.7;
}
#ys_sidebar{
background: rgba(255, 255, 255, 0.61);
}
#n_header_logined a {
color: rgb(78, 108, 168);
}
}

@-moz-document url-prefix("http://www.xiami.com/?type=") {
.widget .info .num, .widget .info .name, .widget .list2_h, .list2 li.items .s_n a.a_n:link {
color: rgb(66, 66, 66);
}
.list2 li.items .s_c {
color: rgba(211, 35, 35, 0.72);
}
}
